INSERT INTO `Action` VALUES ('Accept_Invitation','2016-01-15 13:47:28',494,'Accept Invitation',1,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- Required: Current_Item -->\n\n<!-- Check if a code was set -->\n[If Current_Action.Variables.Invitation_Code]\n\n	<!-- Search for a matching reset code -->\n	[Invitation_Code from database where Name is Current_Action.Variables.Invitation_Code]\n		<!-- Disambiguate -->\n		[Set Current_Invitation_Code to this /]\n		\n		<!-- Check if the reset code was already used -->\n		[If not Current_Invitation_Code.Used]\n		\n		<!-- Track visit --> \n		[If Globals.Allow_Tracking]\n			[Current_Invitation_Code.User]\n				[Set Current_Invitation_User to This/]\n				[External_Script]\n					// Don\'t die on adblock\n				\n					if (mixpanel)\n					{\n\n						// Register identity (should be first time... for this site.)\n						mixpanel.alias(\"[Current_Invitation_User.Email_Address no_wrap/]:[PHP]return $_SERVER\\[\'HTTP_HOST\'\\];[/PHP]\");\n					\n						// Register additional information\n						var Mixpanel_Super_Properties = {};\n		\n						[If Current_Invitation_User.First_Name]\n							Mixpanel_Super_Properties\\[\'$first_name\'\\] = \"[Format as \"Javascript String\"][Current_Invitation_User.First_Name no_wrap/][/Format]\";\n						[/If]\n		\n						[If Current_Invitation_User.Last_Name]\n							Mixpanel_Super_Properties\\[\'$last_name\'\\] = \"[Format as \"Javascript String\"][Current_Invitation_User.Last_Name no_wrap/][/Format]\";\n						[/If]														\n						Mixpanel_Super_Properties\\[\'$email\'\\] = \"[Format as \"Javascript String\"][Current_Invitation_User.Email_Address no_wrap/][/Format]\";\n						Mixpanel_Super_Properties\\[\'Email\'\\] = \"[Format as \"Javascript String\"][Current_Invitation_User.Email_Address no_wrap/][/Format]\";\n					\n						// Track state\n						mixpanel.track(\n								\'Invite Viewed\', \n								{	\n									\'Account\': \'[PHP]return $_SERVER\\[\'HTTP_HOST\'\\];[/PHP]\',\n									\'Email_Address\': \'[Current_Invitation_User.Email_Address no_wrap/]\',\n									[If \"[PHP]return strtolower($_SERVER\\[\'HTTP_HOST\'\\]);[/PHP]\" = \"community.better.space\"]\n										[Set Current_Action.Markets_Traversed to false/]\n										\'Markets\': \n											\\[\n												[Current_Invitation_User.What_kind_of_communities_do_you_organize_or_work no_wrap][If Markets_Traversed], \n													[/If]\'[Name no_wrap/]\'[Set Current_Action.Markets_Traversed to true/][/Current_Invitation_User.What_kind_of_communities_do_you_organize_or_work]\n											\\]\n									[/If]\n								}\n							);\n					}\n				[/External_Script]\n			[/Current_Invitation_Code.User]\n		[/If]\n		\n			<!-- Pass code on to action -->\n			[Input \"Hidden\" with Name = \"Code\", Value = Current_Action.Variables.Invitation_Code /]\n\n			<!-- Execute link -->\n			<div style=\"background-color: #ffffee; padding: 25px; text-align: center; margin-bottom: 25px;\">\n				<p style=\"margin-bottom:15px;line-height:1.5;\">\n					Hello, [Current_Invitation_Code.User.First_Name/]! <br/>\n					This is your profile in the [Site.Name/] Better network.  It\'s invisible right now!<br/> \n					Accept the invitation to allow [Site.Name/] members to reach out to you.<br/>\n					To help people find you, join the volunteer teams you like.\n				</p>\n				[Link to Execute with Class = \"Button\" /]\n			</div>\n		[/If]\n	[/Invitation_Code]\n[/If]','<!-- Requires: Form.Code -->\n\n<!-- Check for code -->\n[If Form.Code]\n	[Then]\n		<!-- Search for a matching reset code -->\n		[Invitation_Code from database where Name is Form.Code]\n			[Then]\n				<!-- Disambiguate -->\n				[Set Current_Invitation_Code to this /]\n\n				<!-- Check if the reset code was already used -->\n				[If Current_Invitation_Code.Used]\n					[Then]\n						Code was used.\n					[/Then]\n					[Else]\n						[Current_Invitation_Code.User]\n							[Then]\n								<!-- Disambiguate -->\n								[Set Current_User to this /]\n\n								<!-- Track accept --> \n								[If Globals.Allow_Tracking]\n									[External_Script]\n										// Don\'t die on adblock\n						\n										if (mixpanel)\n										{\n									\n											// Track state\n											mixpanel.track(\n													\'Invite Accepted\', \n													{	\n														\'Account\': \'[PHP]return $_SERVER\\[\'HTTP_HOST\'\\];[/PHP]\',\n														\'Email_Address\': \'[Current_User.Email_Address no_wrap/]\',\n														[If \"[PHP]return strtolower($_SERVER\\[\'HTTP_HOST\'\\]);[/PHP]\" = \"community.better.space\"]\n															[Set Current_Action.Markets_Traversed to false/]\n															\'Markets\': \n																\\[\n																	[Current_User.What_kind_of_communities_do_you_organize_or_work no_wrap][If Markets_Traversed], \n																		[/If]\'[Name no_wrap/]\'[Set Current_Action.Markets_Traversed to true/][/Current_User.What_kind_of_communities_do_you_organize_or_work]\n																\\]\n														[/If]\n													}\n												);\n											\n											mixpanel.track(\n													\"Network Joined\", \n													{	\n														\"Via\": \"Invite\",\n														\"User Name\": \"[Format as \"Javascript String\"][Current_User.Name no_wrap/][/Format]\",\n														\"Account\": \"[PHP]return $_SERVER\\[\"HTTP_HOST\"\\];[/PHP]\"\n													}\n												);\n										}\n									[/External_Script]\n								[/If]\n															\n								<!-- Mark reset code as used -->\n								[Set Current_Invitation_Code.Used to true /]\n								[Save Current_Invitation_Code /]\n								\n								<!-- Mark user as activated -->\n								[Set Current_User.Activated to true /]\n								\n								<!-- Save user with new password -->\n								[Save Current_User /]\n								\n								<!-- Log in user -->\n								[Set Current_Session.User to User /]\n								[Save Current_Session /]\n								\n								<!-- Go to user -->\n								[Go to Current_User by Alias /]\n\n								<!-- Ask for a team Join -->\n								[Go to \"/User/[Current_User.ID no_wrap /]/Add_Value_Clean:Property_Alias=Team\" into \"Window\"/]\n\n							[/Then]\n							[Else]\n								Could not load user for invitation code.\n							[/Else]\n						[/Current_Invitation_Code.User]\n					[/Else]\n				[/If]\n			[/Then]\n			[Else]\n				Invalid code.\n			[/Else]\n		[/Reset_Password_Code]\n	[/Then]\n	[Else]\n		Code required.\n	[/Else]\n[/If]',NULL,NULL,'Published'),('Add_Assistant','2016-01-15 13:47:28',495,'Add Assistant',2,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	<!-- Admin only -->					\n	<div class=\"Window Add_Assistant\">\n		<div class=\"Close_Link\">\n			<a href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n		</div>\n		\n		<div class=\"Create_New\">\n			Create new:\n		</div>\n		\n		<div class=\"Create_Links\">\n			[Type \"Page\"]\n				[Link to \"/type/page/create_item\" into \"Parent\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/file.svg\"> Page[/Link]\n			[/Type]\n			[Action \"Create List\"]\n				[Link to this into \"Parent\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/list.svg\"> List[/Link]\n			[/Action]\n			<br class=\"Clear\" />\n		</div>\n		\n		<div class=\"Import_Link\">\n			[Action \"Import Google Sheet\"]\n				[Link to this into \"Parent\"]Import Data...[/Link]\n			[/Action]\n		</div>\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Added\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','<!-- Does nothing -->',NULL,NULL,'Published'),('Add_Input_Module','2016-01-15 13:47:28',496,'Add Input Module',3,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- Requires: Current_Item -->\n<!-- Optional: Type_Alias -->\n<!-- TODO Currently only supports adding property inputs -->\n\n<!-- Admin only -->\n[Admin]\n	<!-- Pass Current_Item on to action -->\n	[Input \"Hidden\" with Name = \"Current Item\", Value = Current_Item /]\n	\n	<!-- Get target type -->\n	[Type from database where Alias is Current_Action.Variables.Type_Alias]\n		<!-- Disambiguate -->\n		[Set Current_Type to This /]\n		\n		<span class=\"Add_Input_Module_Window\">\n			<!-- Close Button -->\n			<a class=\"Close_Button\" href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window();return false;\">\n				<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\">\n			</a>\n			\n			<span class=\"Header\">\n				Add a new question to the form\n			</span>			\n\n			<div>\n				[Current_Type.Action \"Add Question\"/]\n			</div>\n			\n			<!-- Property List -->\n			<!-- TODO: Hacky, could be done better without a hidden input, etc -->\n			<div id=\"[Current_Action.Namespace no_wrap /]_Property_Item_Input_Finder\">\n				[Input \"Hidden\" with Name = \"Property Item\", Value_Type = \"Property\", Execute_On_Change = true /]\n			</div>\n	\n			<span id=\"[Current_Action.Namespace no_wrap /]_Select_Property_Header\" class=\"Header\" style=\"display:none;\">\n				Or select one\n			</span>\n			\n			[Set Current_Action.Variables.Show_Select_Property_Header to False/]\n			[New [Current_Type.Alias no_wrap /]]\n				<!-- TODO Reverse -->\n				<!-- Only show properties that are not advanced -->\n				[This.Forward_Properties where not Advanced and not Core]\n					<!-- Disambiguate -->\n					[Set Current_Property to this /]\n			\n					<!-- Check if there is a matching property module that is not visible -->\n					[Current_Item.Content_Module restrict_type Input_Module where Property_Item is Current_Property.ID]\n						[Else]\n							[Set Current_Action.Variables.Show_Select_Property_Header to True/]\n							<!-- TODO: Check if still works -->\n							<!-- Link to select property -->\n							<a href=\"#\" onclick=\"Jelly.jQuery(\'#[Current_Action.Namespace no_wrap /]_Property_Item_Input_Finder \\[name=Property_Item\\]\').val(\'[Current_Property.ID no_wrap /]\').change();return false;\" class=\"Small_Button\" style=\"margin-bottom:3px;\">[Current_Property.Name /]</a>\n						[/Else]\n					[/Current_Type.Content_Module]\n				[/Property_Lookup_Item.Forward_Properties]\n\n				[If Current_Action.Variables.Show_Select_Property_Header]\n					[External_Script]\n						document.getElementById(\"[Current_Action.Namespace no_wrap /]_Select_Property_Header\").style.display = \"block\";\n					[/External_Script]\n				[/If]\n			[/New]\n			\n		</span>\n	[/Type]\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Created\",\n			\"Code\": function (Parameters)\n			{\n				Jelly.jQuery(\'#[Current_Action.Namespace no_wrap /]_Property_Item_Input_Finder \\[name=Property_Item\\]\').val(Parameters\\[\'Item\'\\]).change();\n			}\n		});\n		\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Added\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','<!-- Requires: Form.Current_Action -->\n\n<!-- Admin only -->\n[Admin]\n	<!-- Disambiguate -->\n	[Set Current_Item to Form.Current_Item /]\n	[Set Current_Property to Form.Property_Item /]\n	\n	<!-- Create input module for selected property -->\n	[New Input_Module with Property_Item = Current_Property]\n		<!-- Disambiguate -->\n		[Set New_Input_Module to This /]\n		\n		<!-- Save the new input module -->\n		[Save New_Input_Module /]\n		\n		<!-- Add input module to item -->\n		[Add New_Input_Module to Current_Item.Content_Module /]\n	[/New]\n	\n	[External_Script]\n		// Close window\n		Jelly.Interface.Close_Top_Window();\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published'),('Add_Item_From_URL','2016-01-15 13:47:28',497,'Add Item From URL',4,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- TODO: Admin only? -->\n\n<!-- Requires: Type_Alias -->\n<!-- Optional: Execute_On_Change, Result_Element_ID -->\n\n<!-- Pass type to action -->\n[Input \"Hidden\" with Name = \"Type Alias\", Value = Current_Action.Variables.Type_Alias /]\n\n<!-- URL -->\n<span class=\"Input_Row_A\">\n	<span class=\"Input_Row_A_Label\">\n		URL:\n	</span>\n	<span class=\"Input_Row_A_Input\" id=\"[Current_Action.Namespace no_wrap /]_URL_Input_Finder\">\n		[Input \"Text Field\" with Name = \"URL\", Placeholder = \"http://example.com\", Blur_On_Execute = 1[If Current_Action.Variables.Execute_On_Change][/If] /]\n	</span>\n	[If not Current_Action.Variables.Execute_On_Change]\n		<span class=\"Input_Row_A_Link\">\n			<!-- Execute -->\n			[Link to Execute]Add[/Link] [Action as Loading/] [Action as Result /]\n		</span>\n	[/If]\n	<span class=\"Input_Row_A_Disclaimer\">\n		[If Current_Action.Variables.Type_Alias = \"Video\"]Currently Limited to YouTube, Vimeo, and Vine[/If]\n		[If Current_Action.Variables.Type_Alias = \"Sound\"]Currently Limited to Soundcloud[/If]\n		[If Current_Action.Variables.Type_Alias = \"Social_Media\"]Better, Twitter, Facebook, LinkedIn, Pinterest, Google+, Tumblr, Instagram[/If]\n	</span>\n</span>\n\n<!-- TODO Clear input -->','<!-- TODO: Admin only? -->\n\n<!-- Inputs: Type_Alias, URL -->\n\n[Set Form.URL to \"[PHP]\n	$URL = &New_String(\'[Format as \"PHP Single Quoted String\"][Form.URL no_wrap /][/Format]\');\n	\n	// Revalidate the link and don\'t set if not a link\n	if (strpos($URL, \'http\') === 0 || (filter_var((\'http://\' . $URL), FILTER_VALIDATE_URL) !== false && strpos($URL, \'.\') !== false))\n	{\n		// For websites, prepend http prefix if needed\n		if (strpos($URL, \'http\') === 0)\n		{\n		}\n		else if (filter_var((\'http://\' . $URL), FILTER_VALIDATE_URL) !== false && strpos($URL, \'.\') !== false)\n		{\n			// If adding the http prefix creates a valid URL, add the prefix to the value\n			$URL = &New_String(\'http://\' . $URL);\n		}\n	}\n	else\n		$URL = &New_String(\'\');\n	\n	return $URL;\n	[/PHP]\" /]\n[If Form.URL]\n	[Then]\n		<!-- Create item -->\n		[New [Form.Type_Alias no_wrap /] with Path = Form.URL]\n			<!-- Disambiguate -->\n			[Set Current_New_Item to This /]\n			\n			<!-- Look up remote ID -->\n			\n			<!-- Save item -->\n			[Save Current_New_Item no_refresh /]\n		\n			<!-- Trigger Added Event -->\n			[External_Script]\n				Jelly.Handlers.Call_Handler_For_Target(\n					{\n						\"Event\": \"Added\",\n						\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\"),\n						\"Item\": \"[Current_New_Item.ID no_wrap /]\",\n						\"Item_Type\": \"[Current_New_Item.Type as Alias No_Wrap /]\",\n						\"Badge\": \"[Format as \"Javascript String\"][Current_New_Item as Badge with No_Drag = 1 /][/Format]\"\n					});\n					\n				Jelly.Interface.Close_Top_Window();\n			[/External_Script]\n		[/New]\n	[/Then]\n	[Else]\n		Enter a valid URL.\n	[/Else]\n[/If]',NULL,NULL,'Published'),('Add_Template','2016-01-15 13:47:28',498,'Add Template',5,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	[Input \"Hidden\" with Name = \"Type\", Value = Current_Action.Variables.Template_Type/]\n	[Input \"Hidden\" with Name = \"Name\", Value = Current_Action.Variables.Name/]\n	[Input \"Hidden\" with Name = \"Alias\", Value = Current_Action.Variables.Alias/]\n	[Input \"Hidden\" with Name = \"Content\", Value = Current_Action.Variables.Content/]\n	[Link to Execute]Create Template \"[Current_Action.Variables.Name no_wrap /]\"[/Link]\n[/Admin]','[New Template]\n	[Set Template.Type = Form.Values.Type/]\n	[Set Template.Name = Form.Name/]\n	[Set Template.Alias = Form.Alias/]\n	[Set Template.Content = Form.Content_Original/]\n	[Save Template as \"Unsaved\"/]\n	[Go to Edit Template/]\n[/New Template]',NULL,NULL,'Published'),('Add_Type','2016-01-15 13:47:28',499,'Add Type',6,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	[Input \"Text Field\" with Name = \"New Name\" /]\n	[Input \"Text Field\" with Name = \"New Parent Type\", Value = \"Item\" /]\n	[Link to Execute /]\n[/Admin]','[Admin]\n	[New Type with Name = Form.New_Name, Parent_Type = \"[Form.New_Parent_Type as Alias no_wrap /]\"]\n		[Save Type /]\n	[/Type]\n[/Admin]',NULL,NULL,'Published'),('Add_Video','2016-01-15 13:47:28',500,'Add Video',7,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	<!-- Admin only -->\n	\n	<!-- URL -->\n	URL: [Input \"Text Field\" with Name = \"URL\", Blur_On_Execute = 1 /]\n	\n	<!-- Execute -->\n	[Link to Execute /]\n[/Admin]','[Admin]\n	<!-- Create video -->\n	[New Video with Path = Form.URL]\n		<!-- Disambiguate -->\n		[Set Current_New_Item to This /]\n		\n		<!-- Look up remote ID -->\n		\n		<!-- Save video -->\n		[Save Current_New_Item /]\n	\n		<!-- Trigger Added Event -->\n		[External_Script]\n			Jelly.Handlers.Call_Handler_For_Target(\n				{\n					\"Event\": \"Added\",\n					\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\"),\n					\"Item\": \"[Current_New_Item.ID no_wrap /]\",\n					\"Item_Type\": \"[Current_New_Item.Type as Alias No_Wrap /]\",\n					\"Badge\": \"[Format as \"Javascript String\"][Current_New_Item as Badge with No_Drag = 1 /][/Format]\"\n				});\n		[/External_Script]\n	[/New]\n[/Admin]',NULL,NULL,'Published'),('Add','2016-01-15 13:47:28',501,'Add',8,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- TODO - seems outdated --> \n[Link to Execute][Add_Label /][/Link]','<!-- Add Action Code -->\n<!-- TODO -  should we work on new variable naming throughout this code? --> \n[Set Current_Action to This /]\n\n[If Form.Target Exists]\n	[Then]\n		<!-- If form handling created an item, use it as this action\'s target -->\n		[Set Current_Action.Target to Form.Target /]\n	[/Then]\n	[Else]\n		<!-- Otherwise, create new item --> \n		[New [Form.Add_Type.Alias no_wrap /]]\n			[Set Current_Action.Target to This /]\n		[/New [Form.Add_Type.Alias no_wrap /]]\n	[/Else]\n[/If]\n\n<!-- Set item\'s status to unsaved -->\n[Set Current_Action.Target.Status to \"Unsaved\" /]\n	\n<!-- Call Item\'s Edit Action to Save the New Item -->\n[Current_Action.Target.Action \"Edit\" as Execute with Suppress_Edited_Handler = 1[If Current_Action.Variables.No_Refresh], No_Refresh = true[/If] /]\n		\n[If Current_Action.Target.Status.Alias = \"Unsaved\"]\n	[External_Script]\n		// Call \"Added_Unsaved\" handler for items that remain \"Unsaved\" after Adding them. -->\n		//  TODO - cleanup Item_ID, Item\n\n		Jelly.Handlers.Call_Handler_For_Target\n		(\n			{\n				\"Event\": \"Added_Unsaved\",\n				\"Target\": document.getElementById(\"[Current_Action.Namespace no_wrap /]\"),\n				\"Item\": \"[Current_Action.Target as Key No_Wrap /]\",\n				\"Item_ID\": \"[Current_Action.Target.ID No_Wrap /]\",\n				\"Item_Type\": \"[Current_Action.Target.Type.Alias No_Wrap /]\",\n			}\n		);\n	[/External_Script]\n[/If]',NULL,NULL,'Published'),('Cancel','2016-01-15 13:47:28',502,'Cancel',9,NULL,0,'2016-01-15 13:47:29','Interaction','Admin',NULL,'[Admin]\n	[If Form.Item.Status.Alias = \"Unsaved\"]\n		[Remove Form.Item /]\n	[/If]\n[/Admin]',NULL,NULL,'Published'),('Change_User','2016-01-15 13:47:28',503,'Change User',10,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- Action \"Change User\" Content -->\n\n<!-- Admin only -->\n[Member]\n	<span id=\"Change_User_Menu\">\n		[Ignore]\n			[Link to Execute]\n				<span class=\"Change_User_Menu_Item\">\n					<span class=\"Icon\">\n						<img class=\"iconic\" data-src=\"/jelly/images/privacy/Members.svg\">\n					</span>\n					<span class=\"Label\">\n						Preview as Member\n					</span>\n				</span>\n			[/Link]\n		\n			<span class=\"Change_User_Menu_Item\">\n				<span class=\"Icon\">\n					<img class=\"iconic\" data-src=\"/jelly/images/privacy/Public.svg\">\n				</span>\n				<span class=\"Label\">\n					Preview as Public\n				</span>\n			</span>\n\n			<!-- \n				<span class=\"Change_User_Menu_Item\">\n					<span class=\"Icon\">\n						<img class=\"iconic\" data-src=\"/jelly/images/privacy/Public.svg\">\n					</span>\n					<span class=\"Label\">\n						View as Public \n					</span>\n				</span>\n			--> \n		[/Ignore]\n\n		<span class=\"Change_User_Menu_Item\">\n			<span class=\"Icon\">\n				<img data-state=\"logout\" class=\"iconic\" data-src=\"/jelly/images/flaticon/arrows15.svg\">\n			</span>\n			<span class=\"Label\">\n				Logout\n			</span>\n		</span>	\n	</span>\n[/Member]','[Preview_Mode \"Public\" from Database]\n	[Set Current_Session.Preview_Mode to This/]	\n	[Save Current_Session/]\n[/Preview_Mode]\n\n[External_Script]\n	document.location.reload();\n[/External_Script]',NULL,NULL,'Published'),('Clean_Add_Assistant','2016-01-15 13:47:28',504,'Clean Add Assistant',11,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- Action \"Clean Add Assistant\" Content -->\n\n<!-- Admin only -->\n[Admin]\n	<span class=\"Add_Item_Menu\">\n		[Action \"Create List\" from database]\n			[Link to this into \"Window\" with Class = \"Add_List_Link\"]	\n				<span class=\"Content\">\n					<span class=\"Icon\">\n						<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/list.svg\">\n					</span>\n					<span class=\"Label\">\n						List\n					</span>\n				</span>\n			[/Link]\n		[/Action]\n		\n		[Link to \"/type/page/create_item\" into \"Window\" with Class = \"Add_Page_Link\"]\n			<span class=\"Content\">\n				<span class=\"Icon\">\n					<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/file.svg\">\n				</span>\n				<span class=\"Label\">\n					Page\n				</span>\n			</span>\n		[/Link]\n		\n		[Action \"Import Google Sheet\"]\n			[Link to this into \"Window\" with Class = \"Import_Spreadsheet_Link\"]Import Spreadsheet...[/Link]\n		[/Action]\n		[Action \"Import Users From Google Sheet\"]\n			[Link to this into \"Window\" with Class = \"Import_Spreadsheet_Link\"]Import Users...[/Link]\n		[/Action]\n	</span>\n[/Admin]','<!-- Does nothing -->',NULL,NULL,'Published'),('Connect_Stripe','2016-01-15 13:47:28',505,'Connect Stripe',12,NULL,0,'2016-01-15 13:47:29','Interaction','Admin',NULL,'<span class=\"Page_Item\">\n	[If Path_Variables.Code exists and Path_Variables.Code is not \"\" and Path_Variables.Error twas not set]\n		[1 Site from Database]	\n			[Set Current_Action.Variables.Stripe_Authorization_Error to false/]\n			[Set Current_Action.Variables.Stripe_Authorization_Error to [PHP]\n						$Stripe_Authorization_URL = \"https://connect.stripe.com/oauth/token\";			\n						$Configuration = &Get_Environment_Variables();\n						$Stripe_Mode = $Configuration\\[\'API_Keys\'\\]\\[\'Stripe\'\\]\\[\'Mode\'\\];\n						$Stripe_Secret_Key = $Configuration\\[\'API_Keys\'\\]\\[\'Stripe\'\\]\\[$Stripe_Mode\\]\\[\'Secret_Key\'\\];\n						$Stripe_Authorization_Code = \"[Path_Variables.Code/]\";\n\n						$Stripe_Data = array(\n							\'client_secret\' => $Stripe_Secret_Key,\n							\'code\' => $Stripe_Authorization_Code,\n							\'grant_type\' => \'authorization_code\'\n						);\n		\n						$Stripe_Response = Get_Remote_Content($Stripe_Authorization_URL, null, null, $Stripe_Data);\n						$Stripe_Response_Body = $Stripe_Response\\[\'Body\'\\];\n						$Stripe_Values = json_decode($Stripe_Response_Body, true);\n		\n						if (\n							$Stripe_Values\\[\'error\'\\] || \n							!$Stripe_Values\\[\'access_token\'\\] || \n							!$Stripe_Values\\[\'refresh_token\'\\] || \n							!$Stripe_Values\\[\'stripe_publishable_key\'\\] || \n							!$Stripe_Values\\[\'stripe_user_id\'\\]\n						)\n						{\n							return \'true\';\n						}\n						else \n						{	\n							// Get site item from memory\n							$Site_Command_String = &$_;$_ = \'1 Site from Memory as Reference\'; unset($_);\n							$Processed_Site_Command = &Process_Command_String($Database, $Site_Command_String, $Memory_Stack_Reference);\n							$Site_Item = &$Processed_Site_Command\\[\'Chunks\'\\]\\[0\\]\\[\'Item\'\\];\n			\n							// Save site variables.\n							Set_Value($Site_Item, \'Stripe_Access_Token\', $Stripe_Values\\[\'access_token\'\\]);\n							Set_Value($Site_Item, \'Stripe_Refresh_Token\', $Stripe_Values\\[\'refresh_token\'\\]);\n							Set_Value($Site_Item, \'Stripe_Publishable_Key\', $Stripe_Values\\[\'stripe_publishable_key\'\\]);\n							Set_Value($Site_Item, \'Stripe_User_ID\', $Stripe_Values\\[\'stripe_user_id\'\\]);\n			\n							// Saves w/o Refresh (refresh is handled in language processing)\n							Save_Item($Site_Item);\n			\n							return \'false\';\n						}\n					[/PHP]\n				/]\n\n			[If not Current_Action.Variables.Stripe_Authorization_Error]\n				[Go to \"/\"/]\n				[Go to \"/settings\" into \"Window\"/]\n			[/If]		\n			\n		[/1 Site from Database]\n	[/If]\n\n	[If Path_Variables.Code twas not set or Path_Variables.Code is \"\" or Path_Variables.Error twas set or Current_Action.Variables.Stripe_Authorization_Error]\n			Something went wrong.<br/>\n			<br/>\n			[If Path_Variables.Error = \"access_denied\"]\n				[Then]\n					Did you press cancel?  Try again, and press \"Connect\" in the next screen. \n				[/Then]\n				[Else]\n					 Please try again, or reach out at support@better.space.\n				[/Else]\n			[/If]\n			<br/>\n			<br/>\n			[Link to \"https://connect.stripe.com/oauth/authorize?response_type=code&client_id=[PHP]\n					$Configuration_File_Path = &New_String(\'jelly_data/Jelly_Configuration.php\');\n					$Configuration = include($Configuration_File_Path);\n					$Stripe_Mode = $Configuration\\[\'API_Keys\'\\]\\[\'Stripe\'\\]\\[\'Mode\'\\];\n					return $Configuration\\[\'API_Keys\'\\]\\[\'Stripe\'\\]\\[$Stripe_Mode\\]\\[\'Client_ID\'\\];\n				[/PHP]&scope=read_write&state=[PHP]\n					$Host = $_SERVER\\[\'HTTP_HOST\'\\];\n					if (strtolower($Host) == \'better.dev\')\n						return $Host;\n					else \n						return explode(\'.\', $Host)\\[0\\];\n				[/PHP]\" with Class = \"Small_Button\"]Connect to Stripe...[/Link]\n	[/If]\n\n</span>',NULL,NULL,'Published'),('Copy_Email_Addresses_For_All_Active_Users','2016-01-15 13:47:28',506,'Copy Email Addresses For All Active Users',13,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	<div class=\"Window Send_Email_Window\">\n		<a href=\"#\" class=\"Close_Button\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\" style=\"float:right;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n\n		<span class=\"Header\">\n			Copy Email Addresses for All Active Users\n		</span>\n	\n		<span class=\"Section\" style=\"padding:20px;\">\n			<span class=\"Label\" style=\"display:block; margin-bottom:15px;\">\n				Press ⌘-C or Control-C and copy these into your email client.\n			</span>\n			<span class=\"Label\" style=\"display:block; margin-bottom:15px;font-weight:normal;\">\n				(Only admins have access to do this)\n			</span>\n			<span class=\"Input\">\n				[Set Current_Action.Traversed to False/]\n				<textarea id=\"Quick_Email_List\" readonly style=\"width:100%; font-size:14px; padding:14px; background-color:#f9f9f9; border-radius:2px; color:#333333;min-height:175px;resize:vertical;\">[User from Database where Activated and Email_Address exists and Email_Address is not \"\" and ID is not Current_Session.User.ID no_wrap][If Current_Action.Traversed], [/If][User.First_Name no_wrap/] [User.Last_Name no_wrap/] &lt;[User.Email_Address no_wrap/]&gt;[Set Current_Action.Traversed to True/][/Current_Action.Target.Members]</textarea>\n			</span>\n		</span>\n		\n		<span class=\"Execute\">\n			<a href=\"Done\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\">Done</a>\n		</span>\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published'),('Copy_Email_Addresses_For_Other','2016-01-15 13:47:28',507,'Copy Email Addresses For Other',14,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	<div class=\"Window Send_Email_Window\">\n		<a href=\"#\" class=\"Close_Button\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\" style=\"float:right;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n\n		<span class=\"Header\">\n			Copy Email Addresses for General Contributors\n		</span>\n	\n		<span class=\"Section\" style=\"padding:20px;\">\n			<span class=\"Label\" style=\"display:block; margin-bottom:15px;\">\n				Press ⌘-C or Control-C and copy these into your email client.\n			</span>\n			<span class=\"Label\" style=\"display:block; margin-bottom:15px;font-weight:normal;\">\n				(Only admins have access to do this)\n			</span>\n			<span class=\"Input\">\n				[Set Current_Action.Traversed to False/]\n				<textarea id=\"Quick_Email_List\" readonly style=\"width:100%; font-size:14px; padding:14px; background-color:#f9f9f9; border-radius:2px; color:#333333;min-height:175px;resize:vertical;\">[User from Database where Activated and Email_Address exists and Email_Address is not \"\" and ID is not Current_Session.User.ID no_wrap][Set Current_Member to this /][1 Team no_wrap][Else][If Current_Action.Traversed], [/If][Current_Member.First_Name no_wrap/] [Current_Member.Last_Name no_wrap/] &lt;[Current_Member.Email_Address no_wrap/]&gt;[Set Current_Action.Traversed to True/][/Else][/1 Team][/Current_Action.Target.Members]</textarea>\n			</span>\n		</span>\n		\n		<span class=\"Execute\">\n			<a href=\"Done\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\">Done</a>\n		</span>\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published'),('Create_Event','2016-01-15 13:47:28',508,'Create Event',15,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	[Link to Execute]Event[/Link]\n[/Admin]','[Admin]\n	[New Event]\n		[Set This.Name to \"New Event\" /]\n		[Save this /]\n		[Go to this by Alias /]\n	[/Event]\n	[External_Script]\n		Jelly.Interface.Close_Top_Window();\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published'),('Create_List','2016-01-15 13:47:28',509,'Create List',16,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- Admin Only -->\n[Admin]\n	[If Parent_Type_Alias]\n		[Type [Parent_Type_Alias no_wrap/] from Database]\n			[Set Parent_Type to This/]\n			[Input \"Hidden\" with Name = \"Parent Type\", Value = Parent_Type/]\n		[/Type]\n	[/If]\n	<div class=\"Window Create_Other_List\">\n		<a href=\"#\" class=\"Close_Button\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n		\n		<span class=\"Header\">\n			Name your list\n		</span>\n		\n		<span class=\"Section\">\n			<span class=\"Input\">\n				[Input \"Text Field\" with Name = \"List Name\" /]\n			</span>\n		</span>\n		\n		<span class=\"Execute\">\n			[Link to Execute]Create[/Link]\n		</span>\n	</div>\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action.Namespace no_wrap /]\"),\n			\"Event\": \"Added\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Created\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n				\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','<!-- Admin only -->\n[Admin]\n	[If Form.List_Name]\n		[Then]\n			<!-- Create type -->\n			[New Type with Name = Form.List_Name, Starred = True, Package = \"Local\"]\n				<!-- Disambiguate -->\n				[Set New_Type to Type/]\n				\n				<!-- Star type -->\n				[Set New_Type.Starred to true /]\n				\n				<!-- Save new type -->\n				[Save New_Type/]\n				\n				[External_Script]\n				\n					/* TODO - Doesn\'t work */\n					Jelly.Handlers.Call_Handler_For_Target(\n						{\n							\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\"),\n							\"Event\": \"Added\",\n							\"Item\": \"[New_Type.ID no_wrap /]\",\n							\"Item_Alias\": \"[New_Type.Alias no_wrap/]\",\n							\"Item_Type\": \"Type\"\n						}\n					);\n														\n					// Hide inspector\n					Jelly.Interface.Hide_Inspector();\n				[/External_Script]\n				\n				<!-- Add to navigation root -->\n				[Folder \"Root\" from database]\n					[Set Current_Folder to this /]\n					[Add New_Type to Folder.Child_Item /]\n				[/Folder]\n				\n				[If Form.Parent_Type]\n					[Then]\n						<!-- Create property --> \n						[New [Form.Parent_Type.Alias no_wrap/]]\n							[Set Form.Item to This/]\n							[Set Form.Value_Type to New_Type.Alias/]\n							[Action \"Create Property Clean\" as \"Execute\"/]\n						[/New]\n					[/Then]\n					[Else]\n						<!-- Manage new type -->\n						[If Form.Parent_Type]\n							[Go to New_Type /]\n						[/If]\n					[/Else]\n				[/If]								\n			[/New Type]\n		[/Then]\n		[Else]\n			Enter a name for your list.\n		[/Else]\n	[/If]\n[/Admin]',NULL,NULL,'Published'),('Create_Page','2016-01-15 13:47:28',510,'Create Page',17,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	[Link to Execute]Page[/Link]\n[/Admin]','[Admin]\n	[New Page]\n		[Set New_Item to this /]\n		\n		[Set This.Name to \"New Page\" /]\n		[Save this /]\n		\n		[New Text_Module]\n			[Set This.Body to \"Enter some text\" /]\n			[Save This /]\n			[Add this to New_Item.Content_Module /]\n		[/Text_Module]\n		\n		[Go to this by Alias /]\n	[/Event]\n	\n	[External_Script]\n		Jelly.Interface.Close_Top_Window();\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published'),('Donate','2016-01-15 13:47:28',511,'Donate',18,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Link to Execute with Class = \"Button\"]Donate[/Link]\n<span style=\"display:none;\">[Action as \"Result\"/]</span>','<!-- Create Shopping Cart -->\n[New Shopping_Cart]\n	<!-- Disambiguate -->\n	[Set Current_Shopping_Cart to This /]\n\n	<!-- Save Shopping Cart -->\n	[Save Current_Shopping_Cart /]\n	\n	[Set Current_Shopping_Cart.Donation to 25 /]\n				\n	<!-- Recalculate total -->\n	[Current_Shopping_Cart.Action \"Recalculate\" as Execute /]\n\n	<!-- Show checkout window -->\n	[Go to \"/[Current_Shopping_Cart.Type.Alias no_wrap /]/[Current_Shopping_Cart.ID no_wrap /]/simple_donation_checkout\" into \"Window\" /]\n[/New Shopping_Cart]',NULL,NULL,'Published'),('Dump_All_Databases','2016-01-15 13:47:28',512,'Dump All Databases',19,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	<div style=\"margin-bottom: 5px;\">\n		Run: [Link to Execute /]\n	</div>\n	<div style=\"margin-bottom: 5px;\">\n		(This will save a backup of all databases accessible from the current account using MySQL\'s own \"mysqldump\" function to \"/jelly_data/Backups/MySQL\". The resulting file will contain your entire database, and can be used to restore the site, excluding any files stored on disk. All files (pictures, videos, etc) do not have their data stored in the database and so must be backed up separately.)\n	</div>\n	<div style=\"color: red;\">\n		[Action:Result /]\n	</div>\n[/Admin]','[Admin]\n	[PHP]\n		// Prepare directory\n		$MySQL_Backup_Directory = \"jelly_data/Database/MySQL/[New Date nowrap][Year No_Wrap /]-[Month No_Wrap /]-[Day No_Wrap /] [Hour_24 No_Wrap /]h[Minute No_Wrap /][/Date]\";\n		Make_Directories_If_Nonexistent($MySQL_Backup_Directory);\n		\n		// Get path to mysqldump\n		$MySQL_Path_Query = \"SELECT @@basedir\";\n		$MySQL_Path_Result = mysqli_query($MySQL_Path_Query);\n		$MySQL_Path_Row = mysqli_fetch_assoc($MySQL_Path_Result);\n		$mysqldump_Path = $MySQL_Path_Row\\[\"@@basedir\"\\] . \"/bin/mysqldump\";\n		\n		$Database_List_Query = \"SHOW DATABASES;\";\n		$Database_List_Result = mysqli_query($Database_List_Query);\n		while ($Database_List_Row = mysqli_fetch_assoc($Database_List_Result))\n		{\n			$Database_Name = $Database_List_Row\\[\"Database\"\\];\n			\n			$MySQL_Backup_File_Name = \"Database Backup (\" . $Database_Name . \").sql\";\n			$MySQL_Backup_Path = $MySQL_Backup_Directory . \"/\" . $MySQL_Backup_File_Name;\n			\n			$Shell_Command = \"$mysqldump_Path --force --opt --user=\\\"\" . $GLOBALS\\[\"Database\"\\]\\[\"Username\"\\] . \"\\\" --password=\\\"\" . $GLOBALS\\[\"Database\"\\]\\[\"Password\"\\] . \"\\\" --databases \\\"\" . $Database_Name . \"\\\" --result-file=\\\"$MySQL_Backup_Path\\\"\";\n			\n			// Dump database\n			$Result = exec($Shell_Command);\n			\n			// Check if file was written properly\n			if (!file_exists($MySQL_Backup_Path))\n				echo \"An error seems to have occurred during the backup, since the backup file does not exist. The result was \\\"$Result\\\". Attempted to execute the following command: $Shell_Command<br/><br/>\";\n			$MySQL_Backup_File_Size = filesize($MySQL_Backup_Path);\n			if ($MySQL_Backup_File_Size == 0)\n				echo \"An error seems to have occurred during the backup, since the backup file size is 0 bytes. Attempted to execute the following command: $Shell_Command<br/><br/>\";\n			\n			echo \"Backed up \\\"$Database_Name\\\" to $MySQL_Backup_Path ($MySQL_Backup_File_Size bytes)<br/><br/>\";\n		}\n	[/PHP]\n[/Admin]',NULL,NULL,'Published'),('Dump_Database','2016-01-15 13:47:28',513,'Dump Database',20,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	<div style=\"margin-bottom: 5px;\">\n		Run: [Link to Execute /]\n	</div>\n	<div style=\"margin-bottom: 5px;\">\n		(This will save a backup of your current database using MySQL\'s own \"mysqldump\" function to \"/jelly_data/Backups/MySQL\". The resulting file will contain your entire database, and can be used to restore the site, excluding any files stored on disk. All files (pictures, videos, etc) do not have their data stored in the database and so must be backed up separately.)\n	</div>\n	<div style=\"color: red;\">\n		[Action:Result /]\n	</div>\n[/Admin]','[Admin]\n	[PHP]\n		// Prepare directory\n		$MySQL_Backup_Directory = \"jelly_data/Backups/Database/[New Date nowrap][Year No_Wrap /]-[Month No_Wrap /]-[Day No_Wrap /] [Hour_24 No_Wrap /]h[Minute No_Wrap /][/Date]\";\n		$MySQL_Backup_File_Name = \"[Site nowrap][Name No_Wrap /][/Site] Database Backup (\" . $GLOBALS\\[\"Database\"\\]\\[\"Database_Name\"\\] . \").sql\";\n		$MySQL_Backup_Path = $MySQL_Backup_Directory . \"/\" . $MySQL_Backup_File_Name;\n		Make_Directories_If_Nonexistent($MySQL_Backup_Directory);\n		\n		// Get path to mysqldump\n		$MySQL_Path_Query = \"SELECT @@basedir\";\n		$MySQL_Path_Result = mysqli_query($MySQL_Path_Query);\n		$MySQL_Path_Row = mysqli_fetch_assoc($MySQL_Path_Result);\n		$mysqldump_Path = $MySQL_Path_Row\\[\"@@basedir\"\\] . \"/bin/mysqldump\";\n		\n		$Shell_Command = \"$mysqldump_Path --force --opt --user=\\\"\" . $GLOBALS\\[\"Database\"\\]\\[\"Username\"\\] . \"\\\" --password=\\\"\" . $GLOBALS\\[\"Database\"\\]\\[\"Password\"\\] . \"\\\" --databases \\\"\" . $GLOBALS\\[\"Database\"\\]\\[\"Database_Name\"\\] . \"\\\" --result-file=\\\"$MySQL_Backup_Path\\\"\";\n		\n		// Dump database\n		$Result = exec($Shell_Command);\n		\n		// Check if file was written properly\n		if (!file_exists($MySQL_Backup_Path))\n			return \"An error seems to have occurred during the backup, since the backup file does not exist. The result was \\\"$Result\\\". Attempted to execute the following command: $Shell_Command\";\n		$MySQL_Backup_File_Size = filesize($MySQL_Backup_Path);\n		if ($MySQL_Backup_File_Size == 0)\n			return \"An error seems to have occurred during the backup, since the backup file size is 0 bytes. Attempted to execute the following command: $Shell_Command\";\n		\n		return \"Backed up to $MySQL_Backup_Path ($MySQL_Backup_File_Size bytes)\";\n	[/PHP]\n[/Admin]',NULL,NULL,'Published'),('Email_All_Active_Users','2016-01-15 13:47:28',514,'Email All Active Users',21,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Set Current_Action.Variables.Visible = False/]\n[Admin][Set Current_Action.Variables.Visible = True/][/Admin]\n\n[If Current_Action.Variables.Visible]\n	<div class=\"Window Send_Email_Window\">\n		<a href=\"#\" class=\"Close_Button\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\" style=\"float:right;\"><img class=\"iconic\" dat