ext = @file_get_contents($CSV_Request_URL.\'&\'.mt_rand());\n			\n			// TODO: Check if data looks like CSV\n			\n			// Copy the response headers\n			$CSV_Request_Response_Headers = $http_response_header;\n			\n			// Check if no headers were returned\n			if (count($CSV_Request_Response_Headers) == 0)\n				return \'Could not fetch the document. No headers returned.\';\n			\n			// Get response code and file name from headers\n			foreach ($CSV_Request_Response_Headers as $CSV_Request_Response_Header)\n			{\n				// Check if this is a response-code header\n				if( preg_match(\"#HTTP/\\[0-9\\.\\]+\\s+(\\[0-9\\]+)#\", $CSV_Request_Response_Header, $Matched_Values))\n					$Response_Code = intval($Matched_Values\\[1\\]);\n				\n				// Check if this is a content-disposition header\n				if (strtolower(substr($CSV_Request_Response_Header, 0, strlen(\"Content-Disposition:\"))) == strtolower(\"Content-Disposition:\"))\n				{\n					// Contain to quoted string\n					// TODO if filename*= doesn\'t exist, use filename=\n					// TODO seems as though the UTF-8 version is unterminated\n					$CSV_Response_File_Name = substr($CSV_Request_Response_Header, strpos($CSV_Request_Response_Header, \'filename*=UTF-8\\\'\\\'\') + strlen(\'filename*=UTF-8\\\'\\\'\'));\n					\n					// Decode the file name\n					// TODO doesn\'t seem as though it\'s actually UTF-8 encoded, just URL-encoded\n					$CSV_Response_File_Name = urldecode($CSV_Response_File_Name);\n				}\n			}\n			\n			// Check response code\n			if ($Response_Code != 200)\n				return \'Could not fetch the document. Returned error: \' . $Response_Code;\n			\n			// If Google Docs, check if document has been published\n			if (strpos($URL, \'docs.google.com/spreadsheets\') !== false)\n			{\n				// TODO: this check might change; better way to validate data?\n				if (strpos($CSV_Request_Response_Text, \'Sign in to continue to Sheets\') !== false)\n					return \'Could not fetch the document. Please make sure it is published.\';\n			}\n			\n			// Generate type name from file name\n			{\n				// Use file name as type name\n				$Type_Base_Name = &New_String(\'User\');\n			}\n			$Type_Name = &$Type_Base_Name;\n			$Type_Alias = &New_String(\'User\');\n			\n			\n						\n			// TODO plural types seem to get properties from existing types with same single name\n			\n			// TODO improve value mapping (use heuristics to check for repeated values that would indicate a dropdown list, booleans, dates, numbers, etc)\n			\n			// Get type\n			$Cached_Type = &Get_Cached_Type($Database, $Type_Alias);\n			\n			// Fetch URL again to get header\n			$Handle = fopen($CSV_Request_URL.\'&\'.mt_rand(), \"r\");\n			if (!$Handle)\n				return \"Error: could not open the connection.\";\n			\n			// Fetch the header data\n			$Header_Data = fgetcsv($Handle, null, \",\");\n			if ($Header_Data === FALSE)\n				die(\"Error reading CSV header.\");\n			\n			// Read the remaining data\n			$Lines = &New_Array();\n			while (($Line_Data = fgetcsv($Handle, null, \",\")) !== FALSE)\n				$Lines\\[\\] = $Line_Data;\n			\n			// Create Properties\n			$Header_Property = array();\n			$Found_Name_Column = false;\n			$Found_First_Name_Column = false;\n			$Found_Last_Name_Column = false;\n			$Found_Email_Column = false;\n			foreach ($Header_Data as $Header_Property_Index => &$Header_Property_Name)\n			{\n				// Create the property unless an existing property can be used\n				$Create_Property = true;\n				\n				// Check if a property with that name already exists...\n				\n				// Use the header as the starting property name\n				$Property_Base_Name = &$Header_Property_Name;\n				\n				// Turn number property names into strings\n				// TODO support number property names\n				if (is_numeric($Property_Base_Name))\n					$Property_Base_Name = &New_String(\'Property \' . $Property_Base_Name);\n				\n				// Check if property name is blank\n				if (!$Property_Base_Name)\n					$Property_Base_Name = \'New Property\';\n				\n				// Check if this is a Name column\n				if (strtolower($Property_Base_Name) == \'name\')\n					$Found_Name_Column = true;\n				if (strtolower($Property_Base_Name) == \'first name\')\n					$Found_First_Name_Column = true;\n				if (strtolower($Property_Base_Name) == \'last name\')\n					$Found_Last_Name_Column = true;\n				\n				// Check if this is an Email column\n				if (strtolower($Property_Base_Name) == \'email\' || strtolower($Property_Base_Name) == \'email address\')\n				{\n					$Found_Email_Column = true;\n					\n					// Map email columns to Email Address\n					$Property_Base_Name = \'Email Address\';\n				}\n				\n				// Search for existing properties in all parent types\n				$Property_Name = &$Property_Base_Name;\n				$Property_Index = 1;\n				while (true)\n				{\n					$Recursive_Type = &$Cached_Type;\n					$Found_Property = false;\n					while ($Recursive_Type)\n					{\n						// TODO sanitize?\n						\n						// Get type alias\n						$Recursive_Type_Alias = &$Recursive_Type\\[\'Alias\'\\];\n						\n						// Check if property exists for type\n						$Check_Property_Command_String = &New_String(\'Property from database where Type is \"\' . Jelly_Format($Recursive_Type_Alias, \'jelly attribute\') . \'\" and Name is \"\' . Jelly_Format($Property_Name, \'jelly attribute\') . \'\" as reference\');\n						$Check_Property_Result = Process_Command_String($Database, $Check_Property_Command_String, $Memory_Stack_Reference);\n						$Check_Property_Item = $Check_Property_Result\\[\'Chunks\'\\]\\[0\\]\\[\'Item\'\\];\n						\n						// If property exists, stop searching\n						if (!$Check_Property_Item\\[\'End_Of_Results\'\\])\n						{\n							$Found_Property = true;\n							break;\n						}\n						\n						// Move to parent type\n						$Recursive_Type = &$Recursive_Type\\[\'Cached_Parent_Type\'\\];\n					}\n					\n					// If a property already exists, check if it can be used\n					if ($Found_Property)\n					{\n						switch (strtolower($Property_Name))\n						{\n							// TODO automate this?\n							case \'id\':\n							case \'created\':\n							case \'modified\':\n							case \'alias\':\n							case \'package\':\n							case \'viewable_by\':\n							case \'created_by\':\n							case \'modified_by\':\n							case \'status\':\n							case \'order\':\n							{\n								// If the property is metadata, generate a new unique property name\n								$Property_Index++;\n								$Property_Name = &New_String($Property_Base_Name . \' \' . $Property_Index);\n								break;\n							}\n							case \'name\':\n							default:\n							{\n								// Re-use existing properties that are not metadata\n								$Property_Alias = &$Check_Property_Item\\[\'Data\'\\]\\[\'Alias\'\\];\n								$Cached_Property = &$Cached_Type\\[\'Cached_Property_Lookup\'\\]\\[strtolower($Property_Alias)\\];\n								$Header_Property\\[\\] = &$Cached_Property;\n								\n								$Create_Property = false;\n								break 2;\n							}\n						}\n					}\n					else\n					{\n						// If property does not already exist, create it\n						break;\n					}\n				}\n				\n				// Check if a property should be created\n				if ($Create_Property)\n				{\n					// Create the property\n					$Property_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, \'Property\'));\n					\n					// Set the property\'s values\n					Set_Value($Property_Item, \'Type\', $Type_Alias);\n					Set_Value($Property_Item, \'Name\', Jelly_Format($Property_Name, \'jelly text block\'));\n					Set_Simple($Property_Item, \'Viewable_By\', \'Public\');\n					\n					switch (strtolower($Property_Base_Name))\n					{	\n						// Set value type based on header base name for special cases\n						case \'address\':\n							$Value_Type = \'Location\';\n							break;\n							\n						// Determine value type statistically\n						default:\n							// Count how many times values validate for different value types\n							$Matches_By_Type = array(\'Number\' => 0, \'Date\' => 0, \'Date_Time\' => 0, \'Time\' => 0, \'Boolean\' => 0, \'URL\' => 0, \'Video\' => 0);\n							$Value_String_Matches = array();\n							$Value_Count = 0;\n							foreach ($Lines as &$Line)\n							{\n								$Line_Data_Value = &$Line\\[$Header_Property_Index\\];\n						\n								if ($Line_Data_Value)\n								{\n									$Value_Count++;\n							\n									if (!isset($Value_String_Matches\\[$Line_Data_Value\\]))\n										$Value_String_Matches\\[$Line_Data_Value\\] = 0;\n									$Value_String_Matches\\[$Line_Data_Value\\]++;\n							\n									// Check if the value is numeric\n									if (is_numeric($Line_Data_Value))\n									{\n										// TODO Add checks for meaningful leading zero (i.e. not a zero followed by a decimal)\n										$Matches_By_Type\\[\'Number\'\\]++;\n									}\n							\n									// Ignore date handling for special values\n									// TODO not sure why these are parsed as valid dates\n									if (strtolower($Line_Data_Value) != \'y\')\n									{\n										// Check if the value has date/time data\n										$Date_Time_Value = strtotime($Line_Data_Value, 0);\n										if ($Date_Time_Value !== false)\n										{\n											$Matches_By_Type\\[\'Date_Time\'\\]++;\n									\n											// Check if the time portion is midnight (indicating a date value)\n											if (date(\'H:i:s\', $Date_Time_Value) == \'00:00:00\')\n												$Matches_By_Type\\[\'Date\'\\]++;\n									\n											// Check if year is unaffected by the value (indicating a time value)\n											if (date(\'Y-m-d\', $Date_Time_Value) == \'1970-01-01\')\n												$Matches_By_Type\\[\'Time\'\\]++;\n										}\n									}\n								\n									// Check if the value is a standard URL or validates with the http prefix\n									// TODO better URL checking\n									if (strpos($Line_Data_Value, \'http\') === 0 || (filter_var((\'http://\' . $Line_Data_Value), FILTER_VALIDATE_URL) !== false && strpos($Line_Data_Value, \'.\') !== false))\n									{\n										// TODO add checks for media types\n										$Matches_By_Type\\[\'URL\'\\]++;\n									}\n							\n									// Check if the value includes youtube\n									// TODO better video checking\n									if (strpos($Line_Data_Value, \'youtube\') !== false)\n									{\n										// TODO add checks for media types\n										$Matches_By_Type\\[\'Video\'\\]++;\n									}\n							\n									// Check if the value includes youtube\n									// TODO better video checking\n									if ((strpos($Line_Data_Value, \'.jpg\') !== false) || (strpos($Line_Data_Value, \'.jpeg\') !== false) || (strpos($Line_Data_Value, \'.gif\') !== false) || (strpos($Line_Data_Value, \'.png\') !== false))\n									{\n										// TODO add checks for media types\n										$Matches_By_Type\\[\'Picture\'\\]++;\n									}\n								}\n							}\n					\n							// Choose value type based on percentage matches of the data validation\n							if ($Value_Count > 0)\n							{\n								if ($Matches_By_Type\\[\'Date\'\\] / $Value_Count >= .75)\n									$Value_Type = &New_String(\'Date\');\n								elseif ($Matches_By_Type\\[\'Time\'\\] / $Value_Count >= .75)\n									$Value_Type = &New_String(\'Time\');\n								elseif ($Matches_By_Type\\[\'Date_Time\'\\] / $Value_Count >= .75)\n									$Value_Type = &New_String(\'Date_Time\');\n								else if ($Matches_By_Type\\[\'Number\'\\] / $Value_Count >= .75)\n									$Value_Type = &New_String(\'Number\');\n								else if ($Matches_By_Type\\[\'Video\'\\] / $Value_Count >= .75)\n									$Value_Type = &New_String(\'Video\');\n								else if ($Matches_By_Type\\[\'Picture\'\\] / $Value_Count >= .75)\n									$Value_Type = &New_String(\'Picture\');\n								else if ($Matches_By_Type\\[\'URL\'\\] / $Value_Count >= .75)\n									$Value_Type = &New_String(\'Website\');\n								else\n									$Value_Type = &New_String(\'Long_Text\');\n							}\n							else\n								$Value_Type = &New_String(\'Long_Text\');\n							break;\n					}\n					\n					// Set value type\n					Set_Value($Property_Item, \'Value_Type\', $Value_Type);\n					\n					// Save the property\n					Save_Item($Property_Item);\n					\n					// Fetch the cached property for the property that was just created\n					$Property_Alias = &$Property_Item\\[\'Data\'\\]\\[\'Alias\'\\];\n					$Cached_Property = &$Cached_Type\\[\'Cached_Property_Lookup\'\\]\\[strtolower($Property_Alias)\\];\n					$Header_Property\\[\\] = &$Cached_Property;\n				}\n			}\n			\n			if (!$Found_Name_Column && !$Found_First_Name_Column && !$Found_Last_Name_Column)\n				return \"Requires Name column (or First Name and Last Name columns).\";\n			if (!$Found_Email_Column)\n				return \"Requires Email column (or Email Address).\";\n			\n			// Continue fetching data to get items\n			$Item_Index = &New_Number(0);\n			foreach ($Lines as &$Line)\n			{\n				// Check if all values are blank\n				$All_Values_Are_Blank = true;\n				foreach ($Line as $Line_Data_Index => &$Line_Data_Value)\n				{\n					if ($Line_Data_Value)\n						$All_Values_Are_Blank = false;\n				}\n				\n				// Only create item if not all values are blank\n				if (!$All_Values_Are_Blank)\n				{\n					// Increment index of item\n					$Item_Index = &New_Number($Item_Index + 1);\n					\n					// Create a new item for each line\n					$Item = &Create_Memory_Item($Database, $Cached_Type);\n					\n					// Set each value for the item\n					foreach ($Line as $Line_Data_Index => &$Line_Data_Value)\n					{\n						// Fetch the mapped property alias\n						$Cached_Property = &$Header_Property\\[$Line_Data_Index\\];\n						$Property_Alias = &$Cached_Property\\[\'Alias\'\\];\n						\n						$Cached_Property_Value_Type = $Cached_Property\\[\'Cached_Value_Type\'\\];\n						$Cached_Property_Value_Type_Alias = $Cached_Property_Value_Type\\[\'Alias\'\\];\n						switch (strtolower($Cached_Property_Value_Type_Alias))\n						{\n							case \'text\':\n							case \'long_text\':\n							{\n								// Set text values by escaping it first\n								Set_Value($Item, $Property_Alias, Jelly_Format($Line_Data_Value, \'jelly text block\'));\n								break;\n							}\n							case \'number\':\n							{\n								// Revalidate the number (in case it was one of the outliers before)\n								if (is_numeric($Line_Data_Value))\n								{\n									// Set the value directly\n									Set_Value($Item, $Property_Alias, $Line_Data_Value);\n								}\n								break;\n							}\n							case \'date\':\n							case \'time\':\n							case \'date_time\':\n							{\n								// Revalidate the date (in case it was one of the outliers before)\n								if (strtotime($Line_Data_Value) !== false)\n								{\n									// Set the value directly\n									$Converted_Line_Data_Value = &New_Number(strtotime($Line_Data_Value));\n									Set_Value($Item, $Property_Alias, $Converted_Line_Data_Value);\n								}\n								break;\n							}\n							case \'picture\':\n							case \'video\':\n							case \'website\':\n							{\n								// Check if a value has been set\n								if ($Line_Data_Value)\n								{\n									// Revalidate the link and don\'t set if not a link\n									if (strpos($Line_Data_Value, \'http\') === 0 || (filter_var((\'http://\' . $Line_Data_Value), FILTER_VALIDATE_URL) !== false && strpos($Line_Data_Value, \'.\') !== false))\n									{\n										// For websites, prepend http prefix if needed\n										if (strtolower($Cached_Property_Value_Type_Alias) == \'website\')\n										{\n											if (strpos($Line_Data_Value, \'http\') === 0)\n											{\n											}\n											else if (filter_var((\'http://\' . $Line_Data_Value), FILTER_VALIDATE_URL) !== false && strpos($Line_Data_Value, \'.\') !== false)\n											{\n												// If adding the http prefix creates a valid URL, add the prefix to the value\n												$Line_Data_Value = &New_String(\'http://\' . $Line_Data_Value);\n											}\n										}\n										\n										// Create a picture/video item and set its path\n										$URL_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, $Cached_Property_Value_Type_Alias));\n										Set_Value($URL_Item, \'Path\', New_String(Jelly_Format($Line_Data_Value, \'jelly text block\')));\n										Save_Item($URL_Item);\n										\n										// Set the value to the new item\n										Set_Value($Item, $Property_Alias, $URL_Item);\n									}\n								}\n								\n								break;\n							}\n							\n							case \'location\':									\n								// Create location item\n								$Location_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, \'Location\'));\n								\n								// Clean row value \n								$Address_Value = &Jelly_Format($Line_Data_Value, \'jelly text block\');\n								\n								// Set location values...\n								\n								// Set original address\n								Set_Value($Location_Item, \'Original_Address\', $Address_Value);\n								\n								// Get geocoded values\n								$Geocoded_Address = &Geocode($Address_Value);\n								\n								// Set geocoded values\n								if ($Geocoded_Address !== false)\n								{\n									Set_Value($Location_Item, \'Latitude\', $Geocoded_Address\\[\'lat\'\\]);\n									Set_Value($Location_Item, \'Longitude\', $Geocoded_Address\\[\'lng\'\\]);\n									Set_Value($Location_Item, \'Name\', $Geocoded_Address\\[\'Address\'\\]);\n								}\n								\n								// Save location item\n								Save_Item($Location_Item);\n								\n								// Set location item to row value item\n								Set_Value($Item, $Property_Alias, $Location_Item);\n								break;\n								\n							default:\n							{\n								traverse($Cached_Property);\n								die(\"Could not match property value type.\");\n								break;\n							}\n						}\n					}\n					\n					// If no name column is specified, generate a name for it\n					if (!$Found_Name_Column)\n					{\n						if ($Item\\[\'Data\'\\]\\[\'First_Name\'\\] && $Item\\[\'Data\'\\]\\[\'Last_Name\'\\])\n							$Generated_Item_Name = &New_String($Item\\[\'Data\'\\]\\[\'First_Name\'\\] . \' \' . $Item\\[\'Data\'\\]\\[\'Last_Name\'\\]);\n						else if ($Item\\[\'Data\'\\]\\[\'First_Name\'\\])\n							$Generated_Item_Name = &New_String($Item\\[\'Data\'\\]\\[\'First_Name\'\\]);\n						else if ($Item\\[\'Data\'\\]\\[\'Last_Name\'\\])\n							$Generated_Item_Name = &New_String($Item\\[\'Data\'\\]\\[\'Last_Name\'\\]);\n						\n						// Set the item\'s name\n						Set_Value($Item, \'Name\', Jelly_Format($Generated_Item_Name, \'jelly text block\'));\n					}\n					if (!$Found_First_Name_Column && !$Found_Last_Name_Column)\n					{\n						$Item_Name = $Item\\[\'Data\'\\]\\[\'Name\'\\];\n						if (strpos($Item_Name, \' \') !== false)\n						{\n							$Generated_Item_First_Name = &New_String(substr($Item_Name, 0, strpos($Item_Name, \' \')));\n							$Generated_Item_Last_Name = &New_String(substr($Item_Name, strpos($Item_Name, \' \') + 1));\n						}\n						else\n						{\n							$Generated_Item_First_Name = &$Item_Name;\n							$Generated_Item_Last_Name = &New_String(\'\');\n						}\n						\n						// Set the item\'s fist and last name\n						Set_Value($Item, \'First_Name\', Jelly_Format($Generated_Item_First_Name, \'jelly text block\'));\n						Set_Value($Item, \'Last_Name\', Jelly_Format($Generated_Item_Last_Name, \'jelly text block\'));\n					}\n					\n					// TODO: Generate random password\n					\n					// Mark that user has not been activated yet\n					Set_Simple($Item, \'Activated\', false);\n					\n					// Save the new item\n					Save_Item($Item);\n				}\n			}\n			\n			// Close the CSV reading handle\n			fclose($Handle);\n			\n			\n			// If succesful, pass the type alias back to the script\n			$Check_Type_Command_String = &New_String(\'Set Current_Action.Variables.New_Type_Alias to \"\' . $Type_Alias . \'\"\');\n			$Check_Type_Result = Process_Command_String($Database, $Check_Type_Command_String, $Memory_Stack_Reference);\n		[/PHP]\n		\n		<!-- Check if a type was created -->\n		[If Current_Action.Variables.New_Type_Alias]\n			<!-- Get the type from the database -->\n			[Type [Current_Action.Variables.New_Type_Alias no_wrap /] from database]\n				<!-- Disambiguate -->\n				[Set Current_Type to This /]\n				\n				<!-- Go to refresh action for the new type -->\n				[Go to \"/Type/[Current_Type.Alias no_wrap /]\" /]\n				\n				[External_Script]\n					Jelly.Interface.Close_Top_Window();\n				[/External_Script]\n			[/Type]\n		[/If]\n	[/Then]\n	[Else]\n		Enter a URL for your list.\n	[/Else]\n[/If]',NULL,NULL,'Published'),('Import_from_Account','2016-01-15 13:47:28',523,'Import from Account',30,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','This will import the data from another account into this one.<br/>\n<br/>\n[Admin]\n	[Then]\n		Name:\n		[Input \"Text Field\" with Name = \"Account\"/]<br/>\n		<br/>\n		\n		Password:\n		[Input \"Text Field\" with Name = \"Password\"/]<br/>\n		<br/>\n\n		[Link to Submit with Class=\"Button\"/]\n	[/Then]\n	[Else]\n		4 Adminz Only\n	[/Else]\n[/Admin]','[Admin]\n	[PHP]\n		// Set up export settings\n		$Export_Database_Settings = array(\n			\'Host_Name\' => \'45.79.137.50\',\n			\'Username\' => \'[PHP]return substr(\'U_[Form.Account no_wrap/]\', 0, 16);[/PHP]\',\n			\'Password\' => \'[Form.Password no_wrap/]\',\n			\'Database_Name\' => \'Better_User_DB_[Form.Account no_wrap/]\'\n		);	\n	\n		// Generate cache\n		$Export_Database = &Connect_Database($Export_Database_Settings);\n		Generate_Database_Cache($Export_Database);	\n\n		// Create xml file\n		$Imported_XML = Export_Local_Data_as_XML($Export_Database, $Export_Database_Settings);\n		\n		// Save xml file\n		$XML_Backup_Directory = $GLOBALS\\[\"Data_Directory_Path\"\\] . \'/\' . \'Export\';\n		Make_Directories_If_Nonexistent($XML_Backup_Directory);\n	\n		$XML_File_Name = \"[Format as \"File Name\"][Form.Account No_Wrap /] ([PHP]return date(\'Y-m-d H\\hi\');[/PHP]).xml[/Format]\";\n		$XML_Path = $XML_Backup_Directory . \"/\" . $XML_File_Name;\n		$XML_Handler = fopen($XML_Path, w);\n		fwrite($XML_Handler, $Imported_XML);\n		fclose($XML_Handler);\n	\n		$XML_Link_Path = \'http://\' . $_SERVER\\[\'HTTP_HOST\'\\] . \'/\' . $XML_Path;\n		echo \'Saved to <a href=\"\'. $XML_Link_Path . \'\">\' . $XML_File_Name . \'</a>\';\n		flush();ob_flush();\n	\n		// Import XML\n		$XML_Paths = array($XML_Path);\n		$Database\\[\'Ready\'\\] = &New_Boolean(false);\n		Load_XML_Files($Database, $XML_Paths);\n		$Database\\[\'Ready\'\\] = &New_Boolean(true);\n	[/PHP]\n	Imported!\n[/Admin]',NULL,NULL,'Published'),('Import','2016-01-15 13:47:28',524,'Import',31,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<label>Enter a URL</label>\n[Input \"Text Field\" with Name = \"URL\"/] [Link to Execute/]<br/>\nor [Input \"File\" with Name = \"File\", Execute_On_Change = 1 /]<br/>\n\n<div>[Action as \"Result\" /]</div>','[Admin]\n	[Then]\n		[If Form.URL]	\n			[PHP]\n				$File_Paths = &New_Array();\n				$File_Paths\\[\\] = \'[Form.URL No_Wrap/]\';\n\n				$Database\\[\'Ready\'\\] = &New_Boolean(false);\n				Load_XML_Files($Database, $File_Paths);\n				$Database\\[\'Ready\'\\] = &New_Boolean(true);\n			[/PHP]	\n			Imported.\n		[/If]\n\n		[If Form.File]	\n			[PHP]\n				$File_Paths = &New_Array();\n				$File_Paths\\[\\] = \'[Form.File.Path No_Wrap /]\';\n\n				$Database\\[\'Ready\'\\] = &New_Boolean(false);\n				Load_XML_Files($Database, $File_Paths);\n				$Database\\[\'Ready\'\\] = &New_Boolean(true);\n			[/PHP]	\n			Imported.\n		[/If]\n\n		[External_Script]\n			// TODO - no url prefix here my friend\n			// document.location = \"/\";\n		[/External_Script]\n	[/Then]\n	[Else]\n		You must be logged in as an adminstrator account to import data.\n	[/Else]	\n[/Admin]',NULL,NULL,'Published'),('Join_Newsletter','2016-01-15 13:47:28',525,'Join Newsletter',32,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<label style=\"display:inline\">Subscribe for News</label> [Input \"Text Field\" with Name = \"Email\"/] [Link to Submit/]','[New Contact]\n	[Set Contact.Name to Form.Email/]\n	[Set Contact.Email_Address to Form.Email/]\n	[Save Contact/]\n	Thank you for joining our newsletter!\n[/New Contact]',NULL,NULL,'Published'),('Leave_Team','2016-01-15 13:47:28',526,'Leave Team',33,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- TODO privs -->\n\n<div class=\"Remove_Window\">\n	<!-- Close Link -->\n	<a href=\"#\" class=\"Close_Button\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n	\n	<!-- Pass value to action -->\n	[Input \"Hidden\" with Name = \"Value\", Value = Current_Action.Variables.Value /]\n	\n	<!-- Title -->\n	<div class=\"Header\">\n		Leave the team?\n	</div>\n	\n	<!-- Save/Cancel -->\n	<div class=\"Execute\">\n		[Action as Loading /]\n		[Link to Execute]Leave[/Link]	\n	</div>\n	\n	<!-- Result -->\n	<div>[Action as Result /]</div>\n</div>','[If Globals.Allow_Tracking]\n	[External_Script]\n		if (mixpanel)\n		{\n			mixpanel.track(\n					\"Team Left\", \n					{	\n						\"User Name\": \"[Format as \"Javascript String\"][Form.Value.Member.Name no_wrap/][/Format]\",\n						\"Team Name\": \"[Format as \"Javascript String\"][Form.Value.Team.Name no_wrap/][/Format]\",\n						\"Account\": \"[PHP]return $_SERVER\\[\"HTTP_HOST\"\\];[/PHP]\"\n					}\n				);\n		}\n	[/External_Script]\n[/If]\n\n<!-- TODO privs -->	\n[Remove Form.Value /]\n\n[External_Script]\n	Jelly.Handlers.Call_Handler_For_Target(\n		{\n			\"Event\": \"Item_Removed\",\n			\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\"),\n			\"Item\": \"[Current_Action.Target as Key No_Wrap /]\",\n			\"Item_Type\": \"[Current_Action.Target.Type as Alias No_Wrap /]\"\n		});\n\n	Jelly.Handlers.Call_Handler_For_Target(\n		{\n			\"Event\": \"Dismiss\",\n			\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\")\n		});\n[/External_Script]',NULL,NULL,'Published'),('List_Assistant','2016-01-15 13:47:28',527,'List Assistant',34,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Admin]\n	<!-- Admin only -->\n	<div class=\"Window Create_List\">\n		<div class=\"Close_Link\">\n			<a href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n		</div>\n		\n		[Ignore]\n		<div class=\"Create_List\">\n			<h1>Create a list using an existing structure:</h1>					\n		</div>\n\n		<div class=\"Links\">\n			[Type from Database where Starred and Name is not \"Site\" and Name is not \"Page\"]\n				<!-- TODO: Hacky -->\n				[Link to \"/type/[This.Alias no_wrap /]/create_child_list\" into \"Parent\"][This.Name /][/Link]\n			[/Type]\n			<br style=\"clear: both;\" />\n		</div>\n		[/Ignore]\n	\n		<div class=\"Blank_List\">\n			<h1>Create a blank list:</h1>\n		</div>\n	\n		<div class=\"Links\">\n			[Action \"Create List\"]\n				[Link to this into \"Parent\"]New Blank List[/Link]	\n			[/Action]\n		</div>\n	\n		<div class=\"Blank_List\">\n			<h1>Create a blank list:</h1>\n		</div>\n	\n		<div class=\"Links\">\n			[Action \"Create List\"]\n				[Link to this into \"Parent\"]Create List[/Link]	\n			[/Action]\n		</div>\n	</div>	\n[/Admin]\n\n<!-- Close Window on Save -->\n[External_Script]\n	Jelly.Handlers.Register_Handler(\n	{\n		\"Element\": document.getElementById(\"[Current_Action.Namespace no_wrap /]\"),\n		\"Event\": \"Added\",\n		\"Code\": function (Parameters)\n		{\n			Parameters\\[\"Event\"\\] = \"Dismiss\";\n			Jelly.Handlers.Call_Handler_For_Target(Parameters);\n		}\n	});\n[/External_Script]','<!-- Does nothing -->',NULL,NULL,'Published'),('Login','2016-01-15 13:47:28',528,'Login',35,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<span class=\"Page_Item\">\n	<h1>Register</h1>\n	\n	<span class=\"Block\">\n		<span class=\"Block\" style=\"width: 50%;\">\n			<span class=\"Block\" style=\"margin-bottom: 25px;\">\n				<!-- Facebook login -->\n				[Action \"Facebook Login\" /]\n			</span>\n			\n			<span class=\"Block\" style=\"margin-bottom: 25px;\">\n				<!-- Register User -->\n				[Action \"Register User\" /]\n			</span>\n			\n			<span class=\"Block\" style=\"margin-bottom: 25px;\">\n				<!-- Link to login -->\n				[Action \"Member Login\"]\n					Already have an account? [Link to \"/member_login\"]Login[/Link]\n				[/Action]\n			</span>\n		</span>\n	</span>\n</span>','<!-- Hack: copied code from member_login to support local editor -->\n\n[If Form.Username does not exist or Form.Username = \"\"]\n	[Show Error for \"Username\"]Please enter a username.[/Show]\n[/If]\n			\n[If Form.Password does not exist or Form.Password = \"\"]\n	[Show Error for \"Password\"]Please enter a password.[/Show]\n[/If]\n			\n[If not Form.Error]\n	[Set Action.Variables.Success to False/]\n	[User from Database where Alias = Globals.Form.Username or Name = Globals.Form.Username or Email_Address = Globals.Form.Username]\n		[If User.Password Is Hash Of Globals.Form.Password]\n			[Set Action.Variables.Success to True/]\n			[Set Current_Session.User to User /]\n			[Save Current_Session /]				\n			[Go to User by Alias /]\n			[External_Script]\n				// Trigger \"Dismiss\" event\n				var Parameters = {Event: \"Dismiss\", \'Target\': document.getElementById(\'[Current_Action.Namespace No_Wrap /]\')};\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n				\n				// Trigger \"Logged_In\" event\n				var Parameters = {Event: \"Logged_In\", \'Target\': document.getElementById(\'[Current_Action.Namespace No_Wrap /]\')};\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n				\n				// Refresh Toolbar\n				// TODO: Shouldn\'t manually do this\n				[Block \"Toolbar\" no_wrap]\n					Jelly.References.Trigger_Refresh({\'Kind\': \'Item\', \'Item_ID\': \'[This.ID no_wrap /]\'});Jelly.References.Refresh_All();\n				[/Block]\n				\n				// Show \"Toolbar\"\n				Jelly.Interface.Show_Toolbar();								\n			[/External_Script]\n		[/If]\n	[/User]					\n	[If not Action.Variables.Success]\n		Incorrect password.\n	[/If]\n[/If]',NULL,NULL,'Published'),('Logout','2016-01-15 13:47:28',529,'Logout',36,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Member]\n	[Link to execute with Class = \"Button\" /]\n[/Member]\n[Guest]\n	You are logged out.\n[/Guest]\n<div>[Action as \"Result\" /]</div>','<!-- Unset current user -->\n[Set Current_Session.User to null /]\n[Save Current_Session /]\n\n<!-- Go to home page -->\n[Go to \"/\" /] \n\nYou are now logged out.\n\n[External_Script]\n	// Hide Sidebar\n	Jelly.Interface.HIde_Sidebar();\n[/External_Script]',NULL,NULL,'Published'),('Member_Login','2016-01-15 13:47:28',530,'Member Login',37,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','[Guest]\n	[Then]\n		<span class=\"Page_Item\">\n			<h1>Login</h1>\n			<span class=\"Block\" style=\"margin-bottom: 25px;\">\n				<span class=\"Member_Login\">\n					<!-- Name -->\n					<span class=\"Input_Row_B\">\n						<label>Name/Email:</label>\n						[Input \"Text Field\" with Name = \"Username\" /]\n					</span>\n\n					<!-- Password -->\n					<span class=\"Input_Row_B\">\n						<label>Password:</label>\n						[Input \"Password Field\" with Name = \"Password\" /]\n					</span>\n\n					<!-- Execute -->\n					<span class=\"Input_Row_B\">\n						[Link to Execute with Class = \"Button\"]Login[/Link]\n					</span>\n				</span>\n			</span>\n			<div class=\"Error\">[Action as \"Result\" /]</div>\n			\n			<span class=\"Block\" style=\"margin-bottom: 25px;\">\n				[Link to \"/forgot_password\"]Don\'t have your password?[/Link]\n			</div>\n		</span>\n	[/Then]\n	[Else]\n		<span class=\"Page_Item\">\n			Logged in as [Current_Session.User.Name /].\n			[Link to logout /]\n		</span>\n	[/Else]\n[/Guest]','[If Form.Username does not exist or Form.Username = \"\"]\n	[Show Error for \"Username\"]Please enter a username.[/Show]\n[/If]\n			\n[If Form.Password does not exist or Form.Password = \"\"]\n	[Show Error for \"Password\"]Please enter a password.[/Show]\n[/If]\n			\n[If not Form.Error]\n	[Set Action.Variables.Success to False/]\n	[User from Database where Alias = Globals.Form.Username or Name = Globals.Form.Username or Email_Address = Globals.Form.Username]\n		[If User.Password Is Hash Of Globals.Form.Password]\n			[Set Action.Variables.Success to True/]\n			\n			<!-- Mark user as activated -->\n			[Set User.Activated to true /]\n			[Save User /]\n			\n			<!-- Set user for current session -->\n			[Set Current_Session.User to User /]\n			[Save Current_Session /]\n			\n			<!-- Go to user -->\n			[Go to User by Alias /]\n			\n			[External_Script]\n				// Trigger \"Dismiss\" event\n				var Parameters = {Event: \"Dismiss\", \'Target\': document.getElementById(\'[Current_Action.Namespace No_Wrap /]\')};\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n\n				// Trigger \"Logged_In\" event\n				var Parameters = {Event: \"Logged_In\", \'Target\': document.getElementById(\'[Current_Action.Namespace No_Wrap /]\')};\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n\n				// Refresh Toolbar\n				// TODO: Shouldn\'t manually do this\n				[Block \"Toolbar\" no_wrap]\n					Jelly.References.Trigger_Refresh({\'Kind\': \'Item\', \'Item_ID\': \'[This.ID no_wrap /]\'});Jelly.References.Refresh_All();\n				[/Block]\n\n				// Show \"Sidebar\"\n				[Admin]\n					Jelly.Interface.Show_Sidebar();\n				[/Admin]\n			[/External_Script]\n		[/If]\n	[/User]					\n	[If not Action.Variables.Success]\n		Incorrect password.\n	[/If]\n[/If]',NULL,NULL,'Published'),('Move_Down','2016-01-15 13:47:28',531,'Move Down',38,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','Use \\[Link to Move ITEM Up /\\]','[Set Current_Action to Action /]\n[Admin]\n	[If Action.Property_Alias exists]\n		[Then]\n			[Current_Action:Parent_Item:[Current_Action:Property_Alias No_Wrap /] where ID = Action.Item.ID]\n				[Move This Down /]\n			[/Action]\n		[/Then]\n		[Else]\n			[Move Action.Item Down /]\n		[/Else]\n	[/If]\n[/Admin]',NULL,NULL,'Published'),('Move_Up','2016-01-15 13:47:28',532,'Move Up',39,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','Use \\[Link to Move ITEM Up /\\]','[Set Current_Action to Action /]\n[Admin]\n	[If Action.Property_Alias Exists]\n		[Then]\n			[Current_Action:Parent_Item:[Current_Action:Property_Alias no_wrap /] where ID = Action.Item.ID]\n				[Move This Up /]\n			[/Action]\n		[/Then]\n		[Else]\n			[Move Action.Item Up /]\n		[/Else]\n	[/If]\n[/Admin]',NULL,NULL,'Published'),('Move','2016-01-15 13:47:28',533,'Move',40,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- Move Item Input -->\n[Input \"Hidden\" with Alias \"Move_Item\", Value Move_Item nowrap/]\n\n<!-- Directional or by Order -->\n[If Direction]\n	[Then]\n		<!-- Direction Input -->\n		[Input \"Hidden\" with Alias \"Direction\", Value Direction nowrap/]\n		\n		<!-- Execute Link -->\n		[Link to Execute]\n			[If Action.Label Exists]\n				[Then]\n					[Action:Label /]\n				[/Then]\n				[Else]\n					Move [Move_Item:Self_Type:Name /] [Action:Direction/]\n				[/Else]\n			[/If]\n		[/Link]\n	[/Then]\n	[Else]\n		<!-- Order Select -->\n		<select id=\"[Action:Namespace No_Wrap /]_Order_Value\" name=\"Order\" onchange=\"Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': this});return false;\">\n			[[Action:Move_Item:Self_Type:Alias No_Wrap /] from Database by Order nowrap]\n				<option value=\"[This:Order No_Wrap /]\" [If This.Order = Action.Move_Item.Order]selected=\"selected\"[/If]>[This:Order /]</option>\n			[/[Action:Move_Item:Self_Type:Alias No_Wrap /]]\n		</select>\n	[/Else]\n[/If]','<!-- Move Item -->\n[Move Form.Move_Item [If Form.Direction][Then][Form:Direction No_Wrap/][/Then][Else]To [Form:Order No_Wrap /][/Else][/If] /]\n\n<!-- Refresh Page -->\n[External_Script]\n	Jelly.References.Item_Changed(\"[Form:Move_Item:Self_Type as Alias No_Wrap /]\");\n	Jelly.References.Item_Changed([Form:Move_Item:ID No_Wrap /]);\n[/External_Script]',NULL,NULL,'Published'),('Reference_Payment_Action','2016-01-15 13:47:28',534,'Reference Payment Action',41,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','Enter a Credit Card Number:<br/>\n[Input \"Text Field\" with Sensitive = True, Name = \"Credit Card Number\", Value = \"4242424242424242\"/]<br/>\n<br/>\nEnter an Expiration Month:<br/>\n[Input \"Text Field\" with Sensitive = True, Name = \"Expiration Month\", Value = \"6\"/]<br/>\n<br/>\nEnter an Expiration Year:<br/>\n[Input \"Text Field\" with Sensitive = True, Name = \"Expiration Year\", Value = \"16\"/]<br/>\n<br/>\nEnter a Verification Number:<br/>\n[Input \"Text Field\" with Sensitive = True, Name = \"Card Verification Code\", Value = \"666\"/]<br/>\n<br/>\n\n[Payment_Service \"Stripe\" as \"Form_Plugin\"/]\n\n[Link to Submit/]<br/>','[New Payment]\n	[Set Payment.Amount to \"6.00\"/]\n	[Set Payment.Description to \"for Sample Item\"/]\n	[Payment.Action \"Charge\" as Execute/]\n	[Payment.Status_Message/]\n	[Save Payment/]\n[/New Payment]',NULL,NULL,'Published'),('Register_User','2016-01-15 13:47:28',535,'Register User',42,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<div class=\"Register_User\">\n	<!-- Name -->\n	<span class=\"Input_Row_B\">\n		<label>Name</label>\n		[Input \"Text Field\" with Name = \"Username\" /]\n	</span>\n\n	<!-- Email Address -->\n	<span class=\"Input_Row_B\">\n		<label>Email Address</label>\n		[Input \"Text Field\" with Name = \"Email Address\" /]\n	</span>\n\n	<!-- Password -->\n	<span class=\"Input_Row_B\">\n		<label>Password</label>\n		[Input \"Password Field\" with Name = \"Password\" /]\n	</span>\n\n	<!-- Execute -->\n	<span class=\"Input_Row_B\">\n		[Link to Execute with Class = \"Button\" /]\n	</span>\n</div>','<!-- Require a username -->\n[If Form.Username does not exist or Form.Username = \"\"]\n	[Show Error for \"Username\"]Please enter a username.[/Show]\n[/If]\n<!-- Require a password -->\n[If Form.Password does not exist or Form.Password = \"\"]\n	[Show Error for \"Password\"]Please enter a password.[/Show]\n[/If]\n\n<!-- Check if there was an error -->\n[If not Form.Error]\n	<!-- Create user -->\n	[New User with Name = Form.Username, Email_Address = Form.Email_Address, Password = Form.Password, Activated = True]\n		<!-- Save user -->\n		[Save This /]\n	\n		<!-- Add user to session -->\n		[Set Current_Session.User to This /]\n		[Save Current_Session /]\n		\n		[External_Script]\n			// Trigger \"Dismiss\" event\n			var Parameters = {Event: \"Dismiss\", \'Target\': document.getElementById(\'[Current_Action.Namespace No_Wrap /]\')};\n			Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			\n			// Trigger \"Logged_In\" event\n			var Parameters = {Event: \"Logged_In\", \'Target\': document.getElementById(\'[Current_Action.Namespace No_Wrap /]\')};\n			Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			\n			// Refresh Toolbar\n			// TODO: Shouldn\'t manually do this\n			[Block \"Toolbar\" no_wrap]\n				Jelly.References.Trigger_Refresh({\'Kind\': \'Item\', \'Item_ID\': \'[This.ID no_wrap /]\'});Jelly.References.Refresh_All();\n			[/Block]\n	\n			// Show \"Toolbar\"\n			Jelly.Interface.Show_Toolbar();\n		[/External_Script]\n	[/User]\n[/If]',NULL,NULL,'Published'),('Require_User','2016-01-15 13:47:28',536,'Require User',43,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','req.','[If Current_Session.User exists]\n	[Else]\n		[Action \"Setup User\"]\n			[Go to this into \"Window\" /]\n		[/Action]\n	[/Else]\n[/Current_Session.User]',NULL,NULL,'Published'),('Reset_Password','2016-01-15 13:47:28',537,'Reset Password',44,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','<!-- Check if a code was set -->\n[If This.Variables.Code]\n	[Then]\n		<!-- Search for a matching reset code -->\n		[Reset_Password_Code from database where Name is Current_Action.Variables.Code]\n			[Then]\n				<!-- Disambiguate -->\n				[Set Current_Reset_Password_Code to this /]\n\n				<!-- Check if the reset code was already used -->\n				[If Current_Reset_Password_Code.Used]\n					[Then]\n						<span class=\"Page_Item\">\n							Password successfully reset.\n						</span>\n					[/Then]\n					[Else]\n						<!-- Pass code on to action -->\n						[Input \"Hidden\" with Name = \"Code\", Value = Current_Action.Variables.Code /]\n						<span class=\"Page_Item\">\n							<h1>Create a New Password</h1>\n			\n							<span class=\"Block\" style=\"margin-bottom: 25px;\">\n								<span class=\"Block\" style=\"width: 50%;\">\n									<div class=\"Member_Login\">\n										<!-- New password -->\n										<span class=\"Input_Row_B\" style=\"width:232px;\">\n											<label>New Password:</label>\n											[Input \"Password Field\" with Name = \"New Password\" /]\n										</span>\n			\n										<!-- Execute -->\n										<span class=\"Input_Row_B\" style=\"width:232px;\">\n											[Link to Execute with Class = \"Button\"]Save[/Link]\n										</span>\n\n										<!-- Result --> \n										<span class=\"Input_Row_B\" style=\"width:232px;\">\n											[Current_Action as \"Result\"/]\n										</span>\n									</div>\n								</span>\n							</span>\n						</span>\n					[/Else]\n				[/If]\n			[/Then]\n			[Else]\n				The page you requested was not found.\n			[/Else]\n		[/Reset_Password_Code]\n	[/Then]\n	[Else]\n		The page you requested was not found.\n	[/Else]\n[/If]','<!-- Requires: Form.Code, Form.New_Password -->\n\n<!-- Check for code -->\n[If Form.Code]\n	[Then]\n		<!-- Check if a password was set -->\n		[If Form.New_Password]\n			[Then]\n				<!-- Search for a matching reset code -->\n				[Reset_Password_Code from database where Name is Form.Code]\n					[Then]\n						<!-- Disambiguate -->\n						[Set Current_Reset_Password_Code to this /]\n		\n						<!-- Check if the reset code was already used -->\n						[If Current_Reset_Password_Code.Used]\n							[Then]\n								Code was used.\n							[/Then]\n							[Else]\n								[Current_Reset_Password_Code.User]\n									[Then]\n										<!-- Disambiguate -->\n										[Set Current_User to this /]\n						\n										<!-- Mark reset code as used -->\n										[Set Current_Reset_Password_Code.Used to true /]\n										[Save Current_Reset_Password_Code /]\n						\n										<!-- Set new password -->\n										[Set Current_User.Password to Form.New_Password /]\n										\n										<!-- Mark user as activated -->\n										[Set Current_User.Activated to true /]\n										\n						