INSERT INTO `Block` VALUES ('Blog_Subscriptions','2016-01-15 13:47:28',558,'Blog Subscriptions',1,NULL,0,'2016-01-15 13:47:29','Website','Admin','<div class=\"Jelly_Menu_Item\">\n	<a href=\"[Directory No_Wrap /]/RSS\">Subscribe to RSS Feed</a>\n</div>\n<div class=\"Jelly_Menu_Item\">\n	<a href=\"[Directory No_Wrap /]/Atom\">Subscribe to Atom Feed</a>\n</div>',NULL,NULL,'Published'),('Calendar_Subscriptions','2016-01-15 13:47:28',559,'Calendar Subscriptions',2,NULL,0,'2016-01-15 13:47:29','Website','Admin','<div class=\"Jelly_Menu_Item\">\n	<a href=\"webcal://[HTTP_Host No_Wrap /]/ICS\">Subscribe using iCal</a>\n</div>\n<!--\n<div>\n	<a href=\"another\">Subscribe using Google Calendar</a>\n</div>\n-->\n<div class=\"Jelly_Menu_Item\">\n	<a href=\"[Directory No_Wrap /]/ICS\">Download iCal calendar</a>\n</div>',NULL,NULL,'Published'),('Date_Input_Element','2016-01-15 13:47:28',560,'Date Input Element',3,NULL,0,'2016-01-15 13:47:29','Website','Admin','<!-- Interface date input -->\n<input\n	type=\"text\"\n	name=\"[Input.Variables.Alias No_Wrap /]_Date_Input\"\n	id=\"[Input.Namespace No_Wrap /]_Date_Input\"\n	value=\"[Input.Variables.Value no_wrap][Month_Number no_wrap/]/[Date_Number no_wrap /]/[Year no_wrap /][/Input.Variables.Value]\"\n	class=\"Jelly_Text_Input Jelly_Date_Input[If Input.Variables.Class exists] [Input.Variables.Class no_wrap /][/If]\"\n/>\n\n[External_Script]\n	// Set default target on focus, prevent refreshing on focus, release hold on blur.\n	Jelly.jQuery(\"#\" + \"[Input.Namespace No_Wrap /]_Date_Input\").focus(function()\n		 {	\n			// Set default target\n			Jelly.Handlers.Set_Default_Target(this);\n			\n			// Block refresh\n			Jelly.References.Block_Refresh({\'Namespace\': \"[Current_Action.Namespace no_wrap /]\"});\n			\n			// TODO - Dont know what this does, or why it\'s here.\n			if (Jelly.Interface.Bubble_Event_Protection(\'Click\'))\n			{\n				this.click();\n			}\n		});\n		\n	Jelly.jQuery(\"#\" + \"[Input.Namespace No_Wrap /]_Date_Input\").blur(function()\n		 {	\n			// Release refresh\n			Jelly.References.Release_Refresh({\'Namespace\': \"[Current_Action.Namespace no_wrap /]\"});\n			\n			// Clean date input\n			Jelly.Interface.Clean_Date_Input(\"[Input.Namespace No_Wrap /]\");\n		});\n		\n	// Handle execute on change. \n	[If Input.Variables.Execute_On_Change]					\n		// Restart action execution timer on change\n		Jelly.jQuery(\"#\" + \"[Input.Namespace No_Wrap /]_Date_Input\").keyup(function()\n			 {			\n				// Restart action timer\n				// TODO - might have some clean date input conversion errors\n				Jelly.Actions.Restart_Action_Timer( {\'Namespace\': \"[Current_Action.Namespace no_wrap /]\" });\n			 });\n\n		// Trigger action execution timer on blur\n		Jelly.jQuery(\"#\" + \"[Input.Namespace No_Wrap /]_Date_Input\").blur(function()\n			 {	\n				Jelly.Actions.Execute_Action_Timer( {\'Namespace\': \"[Current_Action.Namespace no_wrap /]\" });\n			});\n	[/If]\n[/External_Script]\n\n\n[Ignore]\n	<!-- TODO - Old click handler--> \n	onclick=\"[Format as \"Single Line Code\"]\n		if (Jelly.Interface.Check_Event_Protection(\'Click\'))\n		{\n				this.Menu_Reference = Jelly.Interface.Create_Menu(\n						{\n							Alias: \'Date_Menu\',\n							Source_Element_ID: \'[Input.Namespace No_Wrap /]_Date_Menu\',\n							Attach: \'Element\',\n							Attach_Element: this,\n							Edge: \'Bottom\',\n							Do_Not_Focus_First_Item: true,\n							Do_Not_Focus_On_Hover: true,\n							Click_On_Hover: true\n						}\n					);\n				Jelly.Interface.Setup_Date_Selector({\'Element\': this.Menu_Reference\\[\'Element\'\\]});\n		}\n		[/Format]\"\n		\n	<!-- TODO - Old blur handler --> \n	Jelly.Handlers.Call_Handler_For_Target(\n			{\n				\'Event\': \'Dismiss\', \n				\'Target\': this.Menu_Reference\\[\'Wrapper_Element\'\\]\n			}\n		); \n\n	<!-- TODO: this needs to finished-->				\n	<div id=\"[Current_Action.Namespace No_Wrap /]_[Input.Variables.Alias No_Wrap /]_Date_Menu\" class=\"Jelly_Date_Menu\" style=\"display: none;\">\n	</div>\n	[External_Script]\n		Jelly.Interface.Generate_Date_Selector(\'[Current_Action.Namespace No_Wrap /]_[Input.Alias No_Wrap /]\');\n	[/External_Script]\n[/Ignore]',NULL,NULL,'Published'),('Email_Sent_Confirmation','2016-01-15 13:47:28',561,'Email Sent Confirmation',4,NULL,0,'2016-01-15 13:47:29','Website','Admin','<span class=\"Page_Item Clean_Window\" style=\"width:325px; padding:25px; padding-bottom:0px;\">\n	<!-- Close Button -->\n	<a class=\"Close_Button\" href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window();return false;\" style=\"float:right;width:30px;height:30px;\">\n		<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\">\n	</a>\n\n	<!-- Header -->\n	<span class=\"Page_Header\">\n		<span class=\"Page_Title\">\n			<span class=\"Left_And_Right\">\n				<span class=\"Page_Name\" style=\"float:none;\">\n					Email Sent\n				</span>\n			</span>\n	</span>\n\n	<section style=\"margin-top:25px;\">\n		<a onclick=\"Jelly.Interface.Close_Top_Window();return false;\" class=\"Button\" style=\"display:block;width:120px;margin-top:25px;cursor:pointer;\">Close</a>\n	</section>\n</span>',NULL,NULL,'Published'),('Forgot_Password_Confirmation','2016-01-15 13:47:28',562,'Forgot Password Confirmation',5,NULL,0,'2016-01-15 13:47:29','Website','Admin','<span class=\"Page_Item Clean_Window\" style=\"width:325px;\">\n	<!-- Close Button -->\n	<a class=\"Close_Button\" href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window();return false;\" style=\"float:right;width:30px;height:30px;\">\n		<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\">\n	</a>\n\n	<!-- Header -->\n	<span class=\"Page_Header\">\n		<span class=\"Page_Title\">\n			<span class=\"Left_And_Right\">\n				<span class=\"Page_Name\" style=\"float:none;\">\n					Reset Link Sent\n				</span>\n			</span>\n	</span>\n\n	<section style=\"margin-top:25px;\">\n		A reset link was sent to your email address.\n	</section>\n\n\n	<section style=\"margin-top:25px;\">\n		<a  onclick=\"Jelly.Interface.Close_Top_Window();return false;\" class=\"Button\" style=\"display:block;width:120px;margin-top:25px;\">Ok</a>\n	</section>\n</span>',NULL,NULL,'Published'),('Heading','2016-01-15 13:47:28',563,'Heading',6,NULL,0,'2016-01-15 13:47:29','Website','Admin','<script>\n	document.getElementById(\'Item_Heading\').innerHTML = \"[Format as \"Javascript String\"][This.Variables.Content/][/Format]\";\n	document.getElementById(\'Item_Heading\').style.display = \"block\";\n</script>',NULL,NULL,'Published'),('Home','2016-01-15 13:47:28',564,'Home',7,NULL,0,'2016-01-15 13:47:29','Website','Admin','<!-- Disambiguate -->\n[Set Current_Block to This /]\n\n<!-- Initialize found variable -->\n[Set Current_Block.Variables.Found_Item to false /]\n\n<!-- Find first visible item in root navigation -->\n[Navigation \"Root\" from Database]\n	<!-- Cycle over navigation items -->\n	[Navigation.Navigation_Item]\n		<!-- Check if a visible item was already found -->\n		[If not Current_Block.Variables.Found_Item]\n			<!-- Load specific item -->\n			[This.Specific_Item]\n				<!-- Disambiguate -->\n				[Set Current_Item to This /]\n				\n				<!-- Determine if item is visible to visitor -->\n				[Set Current_Item.Variables.Visible to true /]\n				[Admin]\n					<!-- Non-admins -->\n					[Else]\n						<!-- Check if the item is a type/page/action -->\n						[If Current_Item.Type.Alias is \"Type\" or Current_Item.Type.Alias is \"Page\" or Current_Item.Type.Alias is \"Action\"]\n							[Then]\n								<!-- Check if type/page is public -->\n								<!-- TODO - nuanced privacy --> \n								[If Current_Item.Viewable_By.Alias != \"Public\"]\n									[Set Current_Item.Variables.Visible to false /]\n								[/If]\n							[/Then]\n							[Else]\n								<!-- Check if regular item\'s type is public -->\n								<!-- TODO - nuanced privacy --> \n								[If Current_Item.Type.Viewable_By.Alias != \"Public\"]\n									[Set Current_Item.Variables.Visible to false /]\n								[/If]\n							[/Else]\n						[/If]\n					[/Else]\n				[/Admin]\n				\n				<!-- Check if item is visible to visitor -->\n				[If Current_Item.Variables.Visible]\n					<!-- Display the item -->\n					[Current_Item /]\n					\n					<!-- Note that a visible item was found -->\n					[Set Current_Block.Variables.Found_Item to true /]\n				[/If]\n			[/This.Specific_Item]\n		[/If]\n	[/Navigation.Navigation_Item]\n[/Navigation]',NULL,NULL,'Published'),('Import_Export_Menu','2016-01-15 13:47:28',565,'Import/Export Menu',8,NULL,0,'2016-01-15 13:47:29','Website','Admin','<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\n	<tr class=\"Jelly_Menu_Row\">\n		<td class=\"Jelly_Menu_Header\">\n			Import/Export\n		</td>\n	</tr>\n	<tr class=\"Jelly_Menu_Row\">\n		<td class=\"Jelly_Menu_Item\">\n			[Action \"Import\" from Database]\n				[Link to Action by Alias]Import...[/Link]\n			[/Action]\n		</td>\n	</tr>\n	<tr class=\"Jelly_Menu_Row\">\n		<td class=\"Jelly_Menu_Item\">\n			<a href=\"/Export\" style=\"width:100%;display:inline-block;\">Export...</a>\n		</td>\n	</tr>\n</table>',NULL,NULL,'Published'),('List','2016-01-15 13:47:28',566,'List',9,NULL,0,'2016-01-15 13:47:29','Website','Admin','<div>\n	<!-- Disambiguate Block --> \n	[Set Edit_Item to Current_Property.Variables.Edit_Item/]\n	\n	<!-- Items -->\n	[Edit_Item.Values.[Current_Property.Variables.Target_Alias No_Wrap /]]\n	\n		<!-- Store current list item -->\n		[Set List_Item to This /]\n		\n		<!-- Closed Menu -->\n		<div\n			class=\"Jelly_Closed_Menu\"\n			id=\"[Namespace No_Wrap /]_Menu\"\n			onmousemove=\"jQuery(this).addClass(\'Jelly_Closed_Menu_Hover\');\"\n			onmouseout=\"jQuery(this).removeClass(\'Jelly_Closed_Menu_Hover\');\"\n			onclick=\"[Format as \"Single Line Code\"]\n				var Menu_Reference = Jelly.Interface.Create_Menu(\n				{\n					Alias: \'Edit_Menu\',\n					Source_Element_ID: \'[List_Item.Namespace No_Wrap /]_Edit_Menu_Items\',\n					Attach: \'Element\',\n					Attach_Element: this\n				});\n\n				return false;\n			[/Format]\"\n		>\n			<!-- Badge -->\n			<div class=\"Jelly_Closed_Menu_Badge\">\n				[List_Item as Badge /]\n			</div>\n			\n			<!-- Arrow -->\n			<div class=\"Jelly_Closed_Menu_Arrow\">\n				&#x25BC;\n			</div>\n			\n			<!-- TODO: Should we really do it this way? Super inconsistent, if there\'s a URL alternative, then caching would handle this the advantage here.--> \n			<!-- TODO: - I guess this would be an attachment template? -->\n			<!-- Hidden Popup Menu -->\n			<div id=\"[List_Item.Namespace No_Wrap /]_Edit_Menu_Items\" style=\"display: none;\">\n				<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\n					<!-- Edit Link -->\n					<tr class=\"Jelly_Menu_Row\">\n						<td class=\"Jelly_Menu_Item\" colspan=\"2\">\n							[Link to Edit List_Item/]\n						</td>\n					</tr>\n					<!-- Edit Attachment and Remove Links -->	\n					[If List_Item.Is_Attachment_Value]\n						[Then]\n							<!-- Edit Attachment Link -->\n							<tr class=\"Jelly_Menu_Row\">\n								<td class=\"Jelly_Menu_Item\" colspan=\"2\">\n									[Link to Edit List_Item as Attachment]Edit Attachment[/Link]\n								</td>\n							</tr>\n		\n							<!-- Remove Attachment Link -->\n							<tr class=\"Jelly_Menu_Row\">\n								<td class=\"Jelly_Menu_Item\" colspan=\"2\">\n									[Link to Remove List_Item as Attachment]Remove Attachment[/Link]\n								</td>\n							</tr>\n						[/Then]\n						[Else]\n							<!-- Remove Link -->\n							<tr class=\"Jelly_Menu_Row\">\n								<td class=\"Jelly_Menu_Item\" colspan=\"2\">\n									[Link to Remove List_Item/]\n								</td>\n							</tr>										\n						[/Else]\n					[/If]\n				</table>\n			</div>\n		</div>\n	[/Edit_Item.Values.[Current_Property.Variables.Target_Alias No_Wrap /]]\n</div>\n\n<!-- Add Value -->\n<div style=\"margin-top: 6px;\">\n	[Edit_Item.Action Add_Value with Target_Property = Current_Property /]\n</div>',NULL,NULL,'Published'),('Navigator','2016-01-15 13:47:28',567,'Navigator',10,NULL,0,'2016-01-15 13:47:29','Website','Admin','<!-- Admin only -->\n[Admin Ignore_Preview_Mode]\n	<!-- Add -->\n	<span class=\"Add_Button_Wrapper\">\n		<a class=\"Add_Button\" href=\"#\" onclick=\"Jelly.Interface.Create_Menu({URL: \'/Action/Clean_Add_Assistant\', \'Alias\': \'New_Item_Menu\', Menu_Namespace: \'[Namespace no_wrap /]_Menu\', Attach: \'Element\', Attach_Element: this, Edge: \'Bottom\'});return false;\">\n			<span class=\"Content\">\n				<span class=\"Icon\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/plus.svg\"></span>\n				<span class=\"Label\">Add...</span>\n			</span>\n		</a>\n	</span>\n	\n	<!-- Trim empty items -->\n	[Item_Parent_Folder from database]\n		[This.Child_Item]\n			[Else]\n				[Remove Item_Parent_Folder /]\n			[/Else]\n		[/This.Child_Item]\n	[/Item_Parent_Folder]\n	\n	<!-- Root folder -->\n	<span class=\"My_Data\">\n		[Folder \"Root\" from database Item_Classes \"Folder\"]\n			<span class=\"Folder_Items\">\n				[This.Child_Item Iterator_Classes \"Sortable\" Item_Classes \"Sortable_Item\"]\n					[This.Specific_Item]\n						<span class=\"Folder_Item Item_[This.ID no_wrap /]\">\n							[If This.Type.Alias is \"Type\"]\n								<a class=\"Details\" href=\"#\" onclick=\"Jelly.Handlers.Visit_Link({URL: \'/[This.Alias no_wrap /]\'}); return false;\">\n									<span class=\"Icon\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/list.svg\"></span>\n									<span class=\"Label\">[If This.Plural_Name][Then][This.Plural_Name /][/Then][Else][This.Name /][/Else][/If]</span>\n								</a>\n								<a class=\"Browse\" href=\"#\" onclick=\"Jelly.Interface.Show_Browse_Bar({Type_Alias: \'[This.Alias no_wrap /]\'});  return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/caret.svg\"></a>\n							[/If]\n							[If This.Type.Alias is \"Folder\"]\n								<a class=\"Details Folder\" href=\"#\" onclick=\"Jelly.Handlers.Visit_Link({URL: \'/[This.Type.Alias no_wrap /]/[This.Alias no_wrap /]\'}); return false;\">\n									<span class=\"Icon\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/folder.svg\"></span>\n									<span class=\"Label\">[This.Name /]</span>\n								</a>\n							[/If]\n							[If This.Type.Alias is \"Dashboard\"]\n								<a class=\"Details\" href=\"#\" onclick=\"Jelly.Handlers.Visit_Link({URL: \'/[This.Alias no_wrap /]\'}); return false;\">\n									<span class=\"Icon\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/dashboard.svg\"></span>\n									<span class=\"Label\">[This.Name /]</span>\n								</a>\n							[/If]\n							[If This.Type.Alias is \"Page\"]\n								<a class=\"Details\" href=\"#\" onclick=\"Jelly.Handlers.Visit_Link({URL: \'/[This.Alias no_wrap /]\'}); return false;\">\n									<span class=\"Icon\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/document.svg\"></span>\n									<span class=\"Label\">[This.Name /]</span>\n								</a>\n							[/If]\n						</span>\n					[/This.Specific_Item]\n				[/This.Folder_Item]\n			</span>\n		[/Folder]\n		[External_Script]\n			Jelly.jQuery(\'.Sortable\').sortable({\n				update: function (event, ui) {\n					console.log(ui);\n					console.log(event);\n				}\n			});\n		[/External_Script]\n	</span>\n	<span class=\"Settings\">\n		[Link to \"/settings\" into \"Window\" with Class = \"Settings_Link\"]\n			<span class=\"Content\">\n				<span class=\"Icon\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/cog.svg\"></span>\n				<span class=\"Label\">Settings</span>\n			</span>\n		[/Link]\n	</span>\n	[ignore]\n		[Action from database][Link to this /][/Action]\n		[Type from database][Link to this /][/Action]\n	[/ignore]\n[/Admin]',NULL,NULL,'Published'),('Property_Menu','2016-01-15 13:47:28',568,'Property Menu',11,NULL,0,'2016-01-15 13:47:29','Website','Admin','<menu class=\"Better_Inspector_Property_Menu [If not Block.Variables.Property_ID]New_Property[/If]\">\n	[If not Block.Variables.Property_ID]\n		<menuitem>\n				<figure style=\"background-image:url(/jelly/images/types/text.png);\"></figure>\n				<a href=\"#\" onclick=\"[Format as \"Single Line Code\"]\n					Jelly.Handlers.Call_Handler_For_Target(\n						{\n							\'Event\': \'Selected\',\n							\'Target\': this,\n							\'Value_Type\': \'Text\',\n							\'Relation\': \'Simple\'\n						});		\n						return false;\n					[/Format]\">Text</a>\n		</menuitem>\n		<menuitem>\n			<figure style=\"background-image:url(/jelly/images/types/richtext.png);\"></figure>\n			<a href=\"#\" onclick=\"[Format as \"Single Line Code\"]\n				Jelly.Handlers.Call_Handler_For_Target(\n					{\n						\'Event\': \'Selected\',\n						\'Target\': this,\n						\'Value_Type\': \'Long_Text\',\n						\'Relation\': \'Simple\'\n					});		\n					return false;\n				[/Format]\">Rich Text</a>\n		</menuitem>\n		<menuitem>\n			<figure style=\"background-image:url(/jelly/images/types/password.png);\"></figure>\n			<a href=\"#\" onclick=\"[Format as \"Single Line Code\"]\n				Jelly.Handlers.Call_Handler_For_Target(\n					{\n						\'Event\': \'Selected\',\n						\'Target\': this,\n						\'Value_Type\': \'Password\',\n						\'Relation\': \'Simple\'\n					});		\n					return false;\n				[/Format]\">Password</a>\n		</menuitem>\n		<menuitem>\n			<figure style=\"background-image:url(/jelly/images/types/number.png);\"></figure>\n			<a href=\"#\" onclick=\"[Format as \"Single Line Code\"]\n				Jelly.Handlers.Call_Handler_For_Target(\n					{\n						\'Event\': \'Selected\',\n						\'Target\': this,\n						\'Value_Type\': \'Number\',\n						\'Relation\': \'Simple\'\n					});		\n					return false;\n				[/Format]\">Number</a>\n		</menuitem>\n		<menuitem>\n			<figure style=\"background-image:url(/jelly/images/types/currency.png);\"></figure>\n			<a href=\"#\" onclick=\"[Format as \"Single Line Code\"]\n				Jelly.Handlers.Call_Handler_For_Target(\n					{\n						\'Event\': \'Selected\',\n						\'Target\': this,\n						\'Value_Type\': \'Number\',\n						\'Relation\': \'Simple\'\n					});		\n					return false;\n				[/Format]\">Currency</a>\n		</