INSERT INTO `Input` VALUES ('Check_Box','2016-01-15 13:47:28',687,'Check Box',1,NULL,0,'2016-01-15 13:47:30','Interaction','Admin','<span class=\"Input_Checkbox_Wrapper\">\n	<input\n		name=\"[Input.Variables.Alias No_Wrap /]\"\n		id=\"[Input.Namespace No_Wrap /]_Value\"\n		class=\"Jelly_Check_Box_Input [Input.Variables.Class No_Wrap /]\"\n		style=\"[Input.Variables.Style No_Wrap /]\"\n		type=\"checkbox\" [If Input.Variables.Value]checked=\"checked\"[/If]\n		[If Input.Variables.Attributes exists][Input.Variables.Attributes No_Wrap /][/If]\n	/>\n	<label for=\"[Input.Namespace No_Wrap /]_Value\">\n		<span class=\"check\"></span>\n		<span class=\"box\"></span>\n	</label>\n</span>\n[External_Script]					\n	// Register input to the action namespace.\n	Jelly.Actions.Register_Action_Input( {\n			Namespace: \"[Current_Action.Namespace no_wrap /]\",\n			Element: document.getElementById(\"[Input.Namespace No_Wrap /]_Value\")\n		 });\n	\n	Jelly.jQuery(\"#\" + \"[Input.Namespace No_Wrap /]_Value\").focus(function()\n		 {	\n			// Set default target\n			Jelly.Handlers.Set_Default_Target(this);\n		});\n	\n	[If Input.Variables.Execute_On_Change]					\n		// Trigger action execution on change\n		Jelly.jQuery(\"#\" + \"[Input.Namespace No_Wrap /]_Value\").change(function()\n			 {	\n				Jelly.Actions.Execute_Action_Timer( {\'Namespace\': \"[Current_Action.Namespace no_wrap /]\" });\n			});\n	[/If]\n[/External_Script]',NULL,NULL,'Published'),('Date_Time','2016-01-15 13:47:28',688,'Date Time',2,NULL,0,'2016-01-15 13:47:30','Interaction','Admin','<!-- Instantiate default input value -->\n[If Input.Variables.Value Twas Not Set]\n	[Set Input.Variables.Value to Now /]\n[/If]\n\n<!-- Set value type to correct type--> \n<!-- TODO - should happen in \"Set\" --> \n[If not Input.Variables.Value.Is_Item]\n	[New Date_Time]\n		[Set Date_Time.Simple_Value to Input.Variables.Value/]\n		[Set Input.Variables.Value to Date_Time/]\n	[/New Date_Time]\n[/If]\n\n<!-- Hidden form input value --> \n<input\n	type=\"hidden\"\n	name=\"[Input.Variables.Alias No_Wrap /]\"\n	id=\"[Input.Namespace No_Wrap /]_Value\"\n	value=\"[Input.Variables.Value no_wrap][SQL_Value no_wrap /][/Input.Variables.Value]\"\n/>\n\n<!-- Hidden form input value type --> \n<input\n	type=\"hidden\"\n	name=\"[Input.Variables.Alias No_Wrap /]_Type\"\n	id=\"[Input.Namespace No_Wrap /]_Value_Type\"\n	value=\"Date_Time\"\n/>\n\n[Block \"Date Input Element\" with Execute_On_Change = Input.Variables.Execute_On_Change/]\n[Block \"Time Input Element\" with Execute_On_Change = Input.Variables.Execute_On_Change/]\n\n[External_Script]\n	// Register input to the current namespace.\n	Jelly.Actions.Register_Action_Input({Namespace: \"[Current_Action.Namespace No_Wrap /]\", Element: document.getElementById(\"[Input.Namespace No_Wrap /]_Value\")});\n	Jelly.Actions.Register_Action_Input({Namespace: \"[Current_Action.Namespace No_Wrap /]\", Element: document.getElementById(\"[Input.Namespace No_Wrap /]_Value_Type\")});\n[/External_Script]',NULL,NULL,'Published'),('Date','2016-01-15 13:47:28',689,'Date',3,NULL,0,'2016-01-15 13:47:30','Interaction','Admin','<!-- Instantiate default input value -->\n[If Input.Variables.Value Twas Not Set]\n	[Set Input.Variables.Value to Now /]\n[/If]\n\n<!-- Set value type to correct type--> \n<!-- TODO - should happen in \"Set\" --> \n[If not Input.Variables.Value.Is_Item]\n	[New Date]\n		[Set Date.Simple_Value to Input.Variables.Value/]\n		[Set Input.Variables.Value to Date/]\n	[/New Date]\n[/If]\n\n<!-- Hidden form input value --> \n<input\n	type=\"hidden\"\n	name=\"[Input.Variables.Alias No_Wrap /]\"\n	id=\"[Input.Namespace No_Wrap /]_Value\"\n	value=\"[Input.Variables.Value no_wrap][SQL_Value no_wrap /][/Input.Variables.Value]\"\n/>\n\n<!-- Hidden form input value type --> \n<input\n	type=\"hidden\"\n	name=\"[Input.Variables.Alias No_Wrap /]_Type\"\n	id=\"[Input.Namespace No_Wrap /]_Value_Type\"\n	value=\"Date\"\n/>\n\n[Block \"Date Input Element\" with Execute_On_Change = Input.Variables.Execute_On_Change/]\n\n[External_Script]\n	// Register input to the current namespace.\n	Jelly.Actions.Register_Action_Input({Namespace: \"[Current_Action.Namespace No_Wrap /]\", Element: document.getElementById(\"[Input.Namespace No_Wrap /]_Value\")});\n	Jelly.Actions.Register_Action_Input({Namespace: \"[Current_Action.Namespace No_Wrap /]\", Element: document.getElementById(\"[Input.Namespace No_Wrap /]_Value_Type\")});\n[/External_Script]',NULL,NULL,'Published'),('Design_Editor','2016-01-15 13:47:28',690,'Design Editor',4,NULL,0,'2016-01-15 13:47:30','Interaction','Admin','<!-- TODO : does this show up, is this used? untouched--> \n<div>					\n	<div class=\"Jelly_Design_Editor_Box\" id=\"[Input.Namespace No_Wrap /]_HTML_Editor\">\n		[Input \"Text Area\" with Name Input.Name, Class Input.Class, Style Input.Style, Attributes Input.Attributes, Value Input.Value, Parent_Input = This /]\n	</div>\n</div>',NULL,NULL,'Published'),('Drop_Area','2016-01-15 13:47:28',691,'Drop Area',5,NULL,0,'2016-01-15 13:47:30','Interaction','Admin','<!-- TODO: this is untouched, correct it next round -->\n\n<!-- Check if an input value is set -->\n[If Input.Value Exists]\n	[Then]\n		<!-- Input value is set -->\n		\n		[Set Input.Value_Type to Input.Value.Self_Type /]\n		\n		<!-- Create hidden input that the menu will set -->\n		[Input \"Hidden\" with Alias Input.Alias, Value Input.Value /]\n	[/Then]\n	[Else]\n		<!-- Input value is not set -->\n		\n		<!-- Check if input value type is not already an Item -->\n		[If Not Input.Value_Type.Is_Item]\n			<!-- Look up input value type -->\n			[Type \"[Input:Value_Type /]\"]\n				[Set Input.Value_Type to This /]\n			[/Type]\n		[/If]\n		\n		<!-- Create hidden input that the menu will set -->\n		[Input \"Hidden\" with Name Input.Name /]\n		[Input \"Hidden\" with Name \"[Input:Name no_wrap /] Type\", Value Input.Value_Type.Alias /]\n	[/Else]\n[/If]\n\n<div class=\"Jelly_Drop_Input\" id=\"[Input.Namespace No_Wrap /]_Drop_Area\">\n	[If Input.Label Exists]\n		[Then]\n			[Input:Label /]\n		[/Then]\n		[Else]\n			Drop Item\n		[/Else]\n	[/If]\n</div>\n\n[External_Script]\n	Jelly.Handlers.Register_Handler(\n	{\n		\'Element\': document.getElementById(\"[Current_Action:Namespace No_Wrap /]_[Input:Alias No_Wrap /]_Drop_Area\"),\n		\'Event\': \'Selected\',\n		\'Code\': function (Parameters)\n		{\n			document.getElementById(\'[Current_Action:Namespace No_Wrap /]_[Input:Alias No_Wrap /]_Value\').value = Parameters\\[\'Item\'\\];\n			document.getElementById(\'[Current_Action:Namespace No_Wrap /]_[Input:Alias No_Wrap /]_Drop_Area\').innerHTML = Parameters\\[\'Badge\'\\];\n			\n			[If Input.Execute_On_Change]\n				Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': document.getElementById(\"[Current_Action:Namespace No_Wrap /]_[Input:Alias No_Wrap /]_Drop_Area\")});\n			[/If]\n		}\n	});\n\n	/*\n	// TODO: disabled\n	jQuery(\"#[Current_Action:Namespace No_Wrap /]_[Input:Alias No_Wrap /]_Drop_Area\").droppable\n	(\n		{\n			hoverClass: \"Jelly_Droppable\",\n			tolerance: \"pointer\",\n			greedy: true,\n			drop: function (Event, UI)\n			{\n				var Draggable_Reference = Jelly.References.Get_Reference_For_Element(UI.draggable.context);\n				\n				if (Draggable_Reference.Type != \"[Input:Value_Type:Alias No_Wrap /]\")\n					return;\n				\n				Jelly.Handlers.Call_Handler_For_Target(\n				{\n					\'Event\': \'Selected\',\n					\'Target\': document.getElementById(\'[Current_Action:Namespace No_Wrap /]_[Input:Alias No_Wrap /]_Drop_Area\'),\n					\'Badge\': UI.draggable.context.innerHTML,\n					\'Item\': Draggable_Reference.ID,\n					\'Item_Alias\': Draggable_Reference.Alias,\n					\'Item_Type\': Draggable_Reference.Type\n				});\n			}\n		}\n	);\n	*/\n[/External_Script]',NULL,NULL,'Published'),('File','2016-01-15 13:47:28',692,'File',6,NULL,0,'2016-01-15 13:47:30','Interaction','Admin','[Input \"Hidden\" with Name = Current_Input.Variables.Name, Value = Current_Input.Variables.Value, Value_Type = \"File\"/]\n[New F