INSERT INTO `Template` VALUES ('Default','2016-01-15 13:47:28',326,'Default',1,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','text/html','<!-- Store action and namespace for disambiguation later. -->\n[Set Current_Action to This /]\n[Set Current_Action_Namespace to This.Namespace /]\n\n\n<!-- Show in toolbars --> \n[External_Script]\n	if (Jelly.jQuery(\"#Jelly_Sidebar .Folder_Item.Item_[Current_Action.ID no_wrap /]\").length)\n	{\n		Jelly.jQuery(\"#Jelly_Sidebar .Folder_Item\").removeClass(\"Selected\");\n		Jelly.jQuery(\"#Jelly_Sidebar .Folder_Item.Item_[Current_Action.ID no_wrap /]\").addClass(\"Selected\");\n	}\n	if (Jelly.jQuery(\"#Jelly_Toolbar .Navigation_Item.Item_[Current_Action.ID no_wrap /]\").length)\n	{\n		Jelly.jQuery(\"#Jelly_Toolbar .Navigation_Item\").removeClass(\"Selected\");\n		Jelly.jQuery(\"#Jelly_Toolbar .Navigation_Item.Item_[Current_Action.ID no_wrap /]\").addClass(\"Selected\");\n	}\n	if (Jelly.jQuery(\".Browse_Bar_Type .Browse_Bar_Item.Item_[Current_Action.ID no_wrap /]\").length)\n	{\n		Jelly.jQuery(\".Browse_Bar_Type .Browse_Bar_Item\").removeClass(\"Selected\");\n		Jelly.jQuery(\".Browse_Bar_Type .Browse_Bar_Item.Item_[Current_Action.ID no_wrap /]\").addClass(\"Selected\");\n	}\n[/External_Script]\n\n\n<!-- Register execute handler -->\n[External_Script]\n	Jelly.Handlers.Register_Handler({\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Execute\",\n			\"Code\": function() {\n					Jelly.Actions.Execute_By_Namespace(\n							{\n								Namespace: \"[Current_Action_Namespace No_Wrap /]\"\n							}\n						);\n				}\n		});\n[/External_Script]\n\n<!-- Set result behavior default to manual. -->\n[If not Current_Action.Variables.Result_Behavior]\n	[Set Current_Action.Variables.Result_Behavior = \"Append\"/]\n[/If]\n\n<!-- Store action in hidden input. -->\n<!-- TODO: necessary? Should Javascript handle this implicitly -->\n[Input \"Hidden\" with Name = \"Action\", Value = Current_Action.Alias /]\n\n<!-- Render content. -->\n[Current_Action.Content /]\n\n<!-- Handle result behaviors... --> \n			\n[If Current_Action.Variables.Result_Behavior = \"Append\"]\n	<!-- Handle append result behavior. --> \n	[Action as \"Result\"/]\n[/If]				\n			\n[If Current_Action.Variables.Result_Behavior = \"Replace\"]\n	<!-- Handle replace result behavior. --> \n	[External_Script]\n		Jelly.Actions.Register_Action_Result_Element(\n				{\n					Namespace: \"[Current_Action_Namespace No_Wrap /]\", \n					Result_Element: document.getElementById(\"[Current_Action_Namespace No_Wrap /]\")\n				}\n			);\n	[/External_Script]\n[/If]','label {display: block;}',NULL,NULL,'Published','Action'),('Execute','2016-01-15 13:47:28',327,'Execute',2,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','text/html','[Clean Newlines]\n	[Set Current_Action to Action /]\n	[Current_Action.Code /]\n[/Clean]',NULL,NULL,NULL,'Published','Action'),('Loading','2016-01-15 13:47:28',328,'Loading',3,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','text/html','<div id=\"[Current_Action_Namespace No_Wrap /]_Loading\" class=\"Jelly_Loading\"></div>\n\n[External_Script]\n	Jelly.Actions.Register_Action_Loading_Element({Namespace: \"[Current_Action_Namespace No_Wrap /]\", Loading_Element: document.getElementById(\"[Current_Action_Namespace No_Wrap /]_Loading\")});\n[/External_Script]',NULL,NULL,NULL,'Published','Action'),('Result','2016-01-15 13:47:28',329,'Result',4,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','text/html','<span class=\"Jelly_Action_Result\" id=\"[Current_Action_Namespace No_Wrap /]_Result\"></span>\n[External_Script]\n	Jelly.Actions.Register_Action_Result_Element(\n			{\n				Namespace: \"[Current_Action_Namespace No_Wrap /]\", \n				Result_Element: document.getElementById(\"[Current_Action_Namespace No_Wrap /]_Result\")\n			}\n		);\n[/External_Script]',NULL,NULL,NULL,'Published','Action'),('Default','2016-01-15 13:47:28',330,'Default',5,NULL,0,'2016-01-15 13:47:29','Website','Admin','text/html','[Block.Content /]',NULL,NULL,NULL,'Published','Block'),('Input','2016-01-15 13:47:28',331,'Input',6,NULL,0,'2016-01-15 13:47:29','Simple','Admin','text/html','[Input \"Check Box\" with Alias Boolean.Alias, Value Boolean.Value/]',NULL,NULL,NULL,'Published','Boolean'),('Default','2016-01-15 13:47:28',332,'Default',7,NULL,0,'2016-01-15 13:47:29','Website','Admin','text/html','[Content /]',NULL,NULL,NULL,'Published','Browser_Control'),('File','2016-01-15 13:47:28',333,'File',8,NULL,0,'2016-01-15 13:47:29','Files','Admin','text/css','[Header \"Content-Type\" Value \"text/css\" /][Header \"Content-Disposition\" Value \"[Format as \"Jelly Attribute\"]inline;filename=\"[Format as \"File Name\"][CSS_File.Name No_Wrap /][/Format]\"[/Format]\" /][CSS_File.Content /]',NULL,NULL,NULL,'Published','CSS_File'),('Header','2016-01-15 13:47:28',334,'Header',9,NULL,0,'2016-01-15 13:47:29','Files','Admin','text/html','<link href=\"[Globals.URL_Prefix No_Wrap /][If CSS_File.Source][Then]/File/[CSS_File.Source.ID No_Wrap /]/File[/Then][Else]/CSS_File/[CSS_File.ID No_Wrap /]/File[/Else][/If]\" rel=\"stylesheet\" type=\"text/css\" />',NULL,NULL,NULL,'Published','CSS_File'),('Default','2016-01-15 13:47:28',335,'Default',10,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Content_Module to This /]\n\n<!-- Display specific item -->\n<!-- TODO Shouldn\'t need no_refresh when system is aware of properties from outside current item -->\n[This.Specific_Item with Current_Item = This.Variables.Current_Item /]',NULL,NULL,NULL,'Published','Content_Module'),('Editing','2016-01-15 13:47:28',336,'Editing',11,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Display specific item -->\n<!-- TODO Shouldn\'t need no_refresh when system is aware of properties from outside current item -->\n[This.Specific_Item no_refresh]\n	<!-- Disambiguate -->\n	[Set Current_Content_Module to This /]\n	[Set Current_Content_Module_Namespace to This.Namespace /]\n	\n	<!-- Editing template -->\n	[This as Editing no_refresh /]\n\n	[Admin]\n		<!-- Admin Only... -->\n	\n		<!-- Edit Controls -->\n		[Ignore]\n		<span class=\"Jelly\">\n			<div id=\"[Current_Content_Module.ID no_wrap /]_Edit_Link_Finder\">\n				[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module.Namespace no_wrap /]\" with Class = \"Module_Edit_Link Active Icon\" [If Current_Content_Module.Type.Alias is \"Text_Module\" or Current_Content_Module.Type.Alias is \"Property_Module\"], On_Click = \"Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': Jelly.jQuery(\'#[Current_Content_Module.Namespace no_wrap /] \\[data-kind=Item\\]\\[data-type=Type_Action\\]\\[data-alias=Edit_Inline\\]\').get(0)});return false;\"[/If]]\n					<img class=\"iconic iconic-sm\" data-src=\"/jelly/libraries/iconic/svg/smart/pencil.svg\" >\n				[/Link]\n			</div>\n		</span>\n		[/Ignore]\n	[/Admin]\n[/This.Specific_Item]',NULL,NULL,NULL,'Published','Content_Module'),('Default','2016-01-15 13:47:28',337,'Default',12,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','[Set Current_Text_Module to this /]\n\n<!-- Body -->\n<span class=\"Module_Content Text_Module\" data-module=\"Text_Module\" data-size=\"[This.Size no_wrap /]\" data-width=\"[This.Width no_wrap /]\">\n	\n	<span class=\"Module_Settings\" id=\"[Current_Text_Module.Namespace no_wrap /]_Settings\">\n		<span class=\"Setting_Row\">\n			<span class=\"Setting_Label\" style=\"width:56px;\">Move:</span>\n			<span class=\"Setting_Value\">\n				[Current_Text_Module.Action \"Move Up\"/] [Current_Text_Module.Action \"Move Down\"/]\n			</span>\n		</span>	\n		<span class=\"Setting_Row\">\n			[Link to Remove Current_Text_Module]Remove[/Link]\n		</span>\n	</span>\n	\n	[Admin]\n		[Then]\n			[This.Action \"Clean Edit Inline\" with Property_Alias = \"Body\" /]\n		[/Then]\n		[Else]\n			[This.Body /]\n		[/Else]\n	[/Admin]\n\n	[Admin]\n		<!-- Admin Only... -->\n		\n		<!-- Edit Controls -->\n		<div id=\"[This.ID no_wrap /]_Edit_Link_Finder\">\n			<a href=\"#\" onclick=\"Jelly.jQuery(this).toggleClass(\'Selected\');Jelly.jQuery(\'#[Current_Text_Module.Namespace no_wrap /]_Settings\').toggleClass(\'Visible\'); return false;\" class=\"Module_Edit_Link\">\n				<img class=\"iconic iconic-sm\" data-src=\"/jelly/libraries/iconic/svg/smart/pencil.svg\">\n			</a>\n		</div>\n	[/Admin]\n</span>',NULL,NULL,NULL,'Published','Text_Module'),('Editing','2016-01-15 13:47:28',338,'Editing',13,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Disambiguate -->\n	[Set Current_Text_Module to This /]\n	\n	<!-- TODO use better html tags for sizes -->\n	\n	<!-- Default template -->\n	[Set Globals.Editing to True /]\n	[This as Default /]\n	\n	<!-- Controls -->\n	<div class=\"Module_Controls\">\n		<!-- Change Size -->\n		<div class=\"Group\">\n			[Current_Text_Module.Action \"Change Size\" /]\n		</div>\n		\n		<!-- Change Width -->\n		<div class=\"Group\">\n			[Current_Text_Module.Action \"Change Width\" /]\n		</div>\n		\n		<!-- Delete -->\n		<div class=\"Group\">\n			[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/trash.svg\"> Delete[/Link]\n		</div>\n		\n		<!-- Done -->\n		<div class=\"Group\">\n			<a href=\"#\" onclick=\"Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': Jelly.jQuery(\'#[Current_Content_Module_Namespace no_wrap /] \\[data-kind=Item\\]\\[data-type=Type_Action\\]\\[data-alias=Edit_Inline\\]\').get(0)});return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/check-thin.svg\"> Done</a>\n		</div>\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Text_Module'),('Default','2016-01-15 13:47:28',339,'Default',14,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Picture_Module to This /]\n[Set Current_Picture to Current_Picture_Module.Picture /]\n\n<!-- Picture -->\n<span class=\"Module_Content Picture_Module\" data-module=\"Picture_Module\" data-size=\"[Current_Content_Module.Size no_wrap /]\" data-width=\"[Current_Content_Module.Width no_wrap /]\">\n	\n	<span class=\"Module_Settings\" id=\"[Current_Picture_Module.Namespace no_wrap /]_Settings\">\n		<span class=\"Setting_Row\">\n			<span class=\"Setting_Label\" style=\"width:56px;\">Move:</span>\n			<span class=\"Setting_Value\">\n				[Current_Picture_Module.Action \"Move Up\"/] [Current_Picture_Module.Action \"Move Down\"/]\n			</span>\n		</span>	\n		<span class=\"Setting_Row\">\n			[Link to Remove Current_Picture_Module]Remove[/Link]\n		</span>\n	</span>\n	\n	<span class=\"Picture\" style=\"background-image: url(/Picture/[Current_Picture.ID no_wrap /]/File); padding-top: calc(100% / [Current_Picture.Aspect_Ratio no_wrap /]);\" data-aspect-ratio=\"[Current_Picture.Aspect_Ratio no_wrap /]\">\n		<span style=\"display: none;\">\n			[This.Picture /]\n		</span>\n	</span>\n\n	[Admin]\n		<!-- Admin Only... -->\n		\n		<!-- Edit Controls -->\n		<div id=\"[This.ID no_wrap /]_Edit_Link_Finder\">\n			<a href=\"#\" onclick=\"Jelly.jQuery(this).toggleClass(\'Selected\');Jelly.jQuery(\'#[Current_Picture_Module.Namespace no_wrap /]_Settings\').toggleClass(\'Visible\'); return false;\" class=\"Module_Edit_Link\">\n				<img class=\"iconic iconic-sm\" data-src=\"/jelly/libraries/iconic/svg/smart/pencil.svg\">\n			</a>\n		</div>\n	[/Admin]\n</span>',NULL,NULL,NULL,'Published','Picture_Module'),('Editing','2016-01-15 13:47:28',340,'Editing',15,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Picture_Module to This /]\n[Set Current_Picture to Current_Picture_Module.Picture /]\n\n<!-- Picture -->\n<span class=\"Module_Content\" data-module=\"Picture_Module\" data-size=\"[Current_Content_Module.Size no_wrap /]\" data-width=\"[Current_Content_Module.Width no_wrap /]\">\n	<span class=\"Picture\" style=\"background-image: url(/Picture/[Current_Picture.ID no_wrap /]/File); padding-top: calc(100% / [Current_Picture.Aspect_Ratio no_wrap /]);\" data-aspect-ratio=\"[Current_Picture.Aspect_Ratio no_wrap /]\">\n		<span style=\"display: none;\">\n			[This.Picture /]\n		</span>\n	</span>\n</span>\n\n[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Editing Controls -->\n	<div class=\"Module_Controls\">\n		\n		<!-- Change Width -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Width\" /]\n		</div>\n		\n		<!-- Change Size -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Size\" /]\n		</div>\n		\n		<!-- Change Picture -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Picture_Module/[This.ID no_wrap /]/Change_Picture\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/image.svg\" data-orientation=\"landscape\" > Change Picture[/Link]\n		\n		<!-- Delete -->\n		<div class=\"Group\">\n			[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/trash.svg\"> Delete[/Link]\n		</div>\n		\n		<!-- Done -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/check-thin.svg\"> Done\n		[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Picture_Module'),('Default','2016-01-15 13:47:28',341,'Default',16,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Video_Module to This /]\n[Set Current_Video to Current_Video_Module.Video /]\n\n<!-- Video -->\n<span class=\"Module_Content\" data-module=\"Video_Module\" data-size=\"[Current_Content_Module.Size no_wrap /]\" data-width=\"[Current_Content_Module.Width no_wrap /]\">\n	\n	<span class=\"Module_Settings\" id=\"[Current_Video_Module.Namespace no_wrap /]_Settings\">\n		<span class=\"Setting_Row\">\n			<span class=\"Setting_Label\" style=\"width:56px;\">Move:</span>\n			<span class=\"Setting_Value\">\n				[Current_Video_Module.Action \"Move Up\"/] [Current_Video_Module.Action \"Move Down\"/]\n			</span>\n		</span>	\n\n		<span class=\"Setting_Row\">\n			[Link to Remove Current_Video_Module]Remove[/Link]\n		</span>\n	</span>\n\n	[This.Video /]\n\n	[Admin]\n		<!-- Admin Only... -->\n		\n		<!-- Edit Controls -->\n		<div id=\"[This.ID no_wrap /]_Edit_Link_Finder\">\n			<a href=\"#\" onclick=\"Jelly.jQuery(this).toggleClass(\'Selected\');Jelly.jQuery(\'#[Current_Video_Module.Namespace no_wrap /]_Settings\').toggleClass(\'Visible\'); return false;\" class=\"Module_Edit_Link\">\n				<img class=\"iconic iconic-sm\" data-src=\"/jelly/libraries/iconic/svg/smart/pencil.svg\">\n			</a>\n		</div>\n	[/Admin]\n</span>',NULL,NULL,NULL,'Published','Video_Module'),('Editing','2016-01-15 13:47:28',342,'Editing',17,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Picture_Module to This /]\n[Set Current_Picture to Current_Picture_Module.Picture /]\n\n<!-- Video -->\n<span class=\"Module_Content\" data-module=\"Video_Module\" data-size=\"[Current_Content_Module.Size no_wrap /]\" data-width=\"[Current_Content_Module.Width no_wrap /]\">\n	[This.Video /]\n</span>\n\n[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Editing Controls -->\n	<div class=\"Module_Controls\">\n		\n		<!-- Change Width -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Width\" /]\n		</div>\n		\n		<!-- Change Size -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Size\" /]\n		</div>\n		\n		<!-- Change Picture -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Video_Module/[This.ID no_wrap /]/Change_Video\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/video.svg\" > Change Video[/Link]\n		\n		<!-- Delete -->\n		<div class=\"Group\">\n			[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/trash.svg\"> Delete[/Link]\n		</div>\n		\n		<!-- Done -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/check-thin.svg\"> Done\n		[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Video_Module'),('Default','2016-01-15 13:47:28',343,'Default',18,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Script -->\n[This.Script /]',NULL,NULL,NULL,'Published','Scripting_Module'),('Editing','2016-01-15 13:47:28',344,'Editing',19,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Script -->\n[This.Script /]\n\n[Admin]\n	<!-- Admin Only... -->\n	\n	<div class=\"Module_Controls\">\n		<!-- Save -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module.Namespace no_wrap /]\"]\n			Done\n		[/Link]\n		<!-- Change Script -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Scripting_Module/[This.ID no_wrap /]/Change_Scripting\" into \"Window\"]Change Scripting[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Scripting_Module'),('Default','2016-01-15 13:47:28',345,'Default',20,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Requires: Current_Item -->\n<!-- Disambiguate -->\n[Set Current_Property_Module to This /]\n\n<!-- Get the property module\'s property -->\n[Current_Property_Module.Property_Item]\n	<!-- Disambiguate -->\n	[Set Current_Property to This /]\n	\n	[Set Current_Property_Module_Property_Alias to Current_Property.Alias /]\n	<!-- Property Module (Alias: [Current_Property_Module_Property_Alias no_wrap /]) -->\n	\n	[If Path_Variables.Display]\n		[Then]\n			[Set Current_Property_Module_Display to Path_Variables.Display/]\n		[/Then]\n		[Else]\n			[Set Current_Property_Module_Display to Current_Property_Module.Display/]\n		[/Else]\n	[/If]\n	\n	<!-- Resolve Direction -->\n	[If Current_Property_Module.Reverse]\n		[Then]\n			<!-- Swap and store reverse property values -->\n			[Set Current_Property.Variables.Current_Type to Current_Property.Values.Value_Type /]\n			[Set Current_Property.Variables.Current_Value_Type to Current_Property.Values.Type /]\n			[Set Current_Property.Variables.Current_Label to Current_Property.Values.Reverse_Label /]\n			[Set Current_Property.Variables.Current_Name to Current_Property.Values.Reverse_Name /]\n			[Set Current_Property.Variables.Current_Plural_Name to Current_Property.Values.Reverse_Plural_Name /]\n			[Set Current_Property.Variables.Current_Alias to Current_Property.Values.Reverse_Alias /]\n			[If Current_Property.Values.Relation is \"Many-To-One\"]\n				[Set Current_Property.Variables.Current_Relation to \"One-To-Many\" /]\n			[/If]\n			[If Current_Property.Values.Relation is \"One-To-Many\"]\n				[Set Current_Property.Variables.Current_Relation to \"Many-To-One\" /]\n			[/If]\n			[If Current_Property.Values.Relation is \"Many-To-Many\"]\n				[Set Current_Property.Variables.Current_Relation to Current_Property.Values.Relation /]\n			[/If]\n		[/Then]\n		[Else]\n			<!-- Store forward property values -->\n			[Set Current_Property.Variables.Current_Type to Current_Property.Values.Type /]\n			[Set Current_Property.Variables.Current_Value_Type to Current_Property.Values.Value_Type /]\n			[Set Current_Property.Variables.Current_Label to Current_Property.Values.Label /]\n			[Set Current_Property.Variables.Current_Name to Current_Property.Values.Name /]\n			[Set Current_Property.Variables.Current_Alias to Current_Property.Values.Alias /]\n			[If Current_Property.Variables.Current_Value_Type is complex type]\n				[Set Current_Property.Variables.Current_Plural_Name to Current_Property.Values.Plural_Name /]\n				[Set Current_Property.Variables.Current_Relation to Current_Property.Values.Relation /]\n			[/If]\n		[/Else]\n	[/If]\n	[If Current_Property.Variables.Current_Relation is \"One-To-Many\" or Current_Property.Variables.Current_Relation is \"Many-To-Many\"]\n		[Set Current_Property.Variables.Current_Multiple to true /]\n	[/If]\n	[If Current_Property.Variables.Current_Value_Type is complex type]\n		[Set Current_Property.Variables.Current_Complex to true /]\n	[/If]\n	\n	<!-- TODO finish -->\n	\n	<!-- Determine editable setting --> \n\n	<!-- Default to not editable -->\n	[Set Current_Property.Variables.Editable to false /]\n\n	<!-- Allow admins to edit all properties -->\n	[Admin]\n		[Set Current_Property.Variables.Editable to true /]\n	[/Admin]\n	\n	<!-- Allow members to edit their own properties, unless they\'re admin properties -->\n	[Member]\n		[If Current_Item.Type.Alias is \"User\"]\n			[If Current_Item.ID is Current_Session.User.ID]	\n				[If Current_Property.Viewable_By.Alias is not \"Admin\"]\n					[Set Current_Property.Variables.Editable to true /]\n				[/If]\n			[/If]\n		[/If]\n	[/Member]\n	\n	<!-- Special case --> \n	<!-- Do not allow editing of Admin team\'s name -->\n	[If Current_Item.Type.Alias is \"Team\" and Current_Item.Name is \"Admin\" and Current_Property.Variables.Current_Alias is \"Name\"]\n		[Set Current_Property.Variables.Editable_Override to true /]\n	[/If]\n	\n	<!-- Determine modifiable setting --> \n	\n	<!-- Default to modifiable --> \n	[Set Current_Property.Variables.Modifiable to Current_Property.Variables.Editable /]\n\n	<!-- If core property, no one can modify it -->\n	[If Current_Property.Core]\n		[Set Current_Property.Variables.Modifiable to false /]\n	[/If]\n		\n	<!-- Determine visibility setting --> \n	\n	<!-- Default to visible -->\n	[Set Current_Property.Variables.Show to true /]\n	\n	[If Current_Session.Preview_Mode]\n		[Set Current_Property.Variables.Editable to false/]\n		[Set Current_Property.Variables.Modifiable to false/]\n	[/If]\n	\n	<!-- Everything editable is visible --> \n	[If Not Current_Property.Variables.Editable]\n		<!-- Set offset of properties used as a cover -->\n		[If Current_Item.Type.Cover_Property_Item.ID is Current_Property.ID]\n			[Set Current_Property.Variables.Offset to 1 /]\n		[/If]\n		\n		<!-- Hide blank value properties --> \n		[If Current_Property.Variables.Current_Complex]\n			[Then]\n				[1 Current_Item.[Current_Property_Module_Property_Alias no_wrap /][If Current_Property.Variables.Offset] at [Current_Property.Variables.Offset no_wrap /][/If]]\n					[Else]\n						[Set Current_Property.Variables.Show to false /]\n					[/Else]\n				[/Current_Item.[Current_Property_Module_Property_Alias no_wrap /]]\n			[/Then]\n			[Else]\n				[If (Current_Property.Variables.Current_Value_Type.Alias is \"Text\" or Current_Property.Variables.Current_Value_Type.Alias is \"Long_Text\" or Current_Property.Variables.Current_Value_Type.Alias is \"Date\" or Current_Property.Variables.Current_Value_Type.Alias is \"Time\" or Current_Property.Variables.Current_Value_Type.Alias is \"Date_Time\") and not Current_Item.[Current_Property_Module_Property_Alias no_wrap /]]\n					[Set Current_Property.Variables.Show to false /]\n				[/If]\n			[/Else]\n		[/If]\n		\n		<!-- Hide members only properties from guests --> \n		[If Current_Property.Viewable_By.Alias = \"Members\"]\n			[Guest]\n				[Set Current_Property.Variables.Show to false /]\n			[/Guest]\n		[/If]\n		\n		<!-- Hide private properties from anyone but the admin and the current member --> \n		[If Current_Property.Viewable_By.Alias = \"Private\"]\n			[If Current_Item.ID is not Current_Session.User.ID]\n				[Admin]\n					[Else]\n						[Set Current_Property.Variables.Show to false /]\n					[/Else]\n				[/Admin]\n			[/If]\n			[If Current_Session.Preview_Mode][Set Current_Property.Variables.Show to false /][/]\n		[/If]\n		\n		<!-- Hide admin-only properties from anyone but the admin --> \n		[If Current_Property.Viewable_By.Alias = \"Admin\"]\n			[Admin]\n				[Else]\n					[Set Current_Property.Variables.Show to false /]\n				[/Else]\n			[/Admin]\n		[/If]\n\n		<!-- Special cases --> \n		[If Current_Property.Variables.Current_Alias is \"Name\"]\n			[Set Current_Property.Variables.Show to false /]\n		[/If]\n		[If Current_Property.Variables.Current_Alias is \"First_Name\"]\n			[Set Current_Property.Variables.Show to false /]\n		[/If]\n		[If Current_Property.Variables.Current_Alias is \"Last_Name\"]\n			[Set Current_Property.Variables.Show to false /]\n		[/If]\n		\n	\n		<!-- TODO - We need per item privacy eventually...-->\n		[If Current_Item.Type.Alias = \"User\" and Current_Property.Variables.Current_Alias is \"Phone_Number\"]\n			[If not Current_Item.Allow_Community_Members_to_Contact_Me]\n				[Set Current_Property.Variables.Show to false /]							\n			[/If]\n		[/If]\n		[If Current_Item.Type.Alias = \"User\" and Current_Property.Variables.Current_Alias is \"Email_Address\"]\n			[If not Current_Item.Allow_Community_Members_to_Contact_Me]\n				[Set Current_Property.Variables.Show to false /]							\n			[/If]\n		[/If]\n	\n		[If Current_Item.Type.Alias is \"Team\" and not Current_Item.Allow_Anyone_To_See_Members and Current_Property.Variables.Current_Alias is \"Member\"]\n			[Set Current_Property.Variables.Show to false /]\n		[/If]\n	[/If]\n	\n	[If Current_Property.Variables.Show]\n		<!-- Check if showing label -->\n		<!-- TODO broken if clean these up -->\n		<span class=\"Property_Module Module_Content\" data-module=\"Property_Module\" data-size=\"[This.Size no_wrap /]\" data-width=\"[This.Width no_wrap /]\" data-overlay=\"[This.Overlay no_wrap /]\" data-show-label=\"[This.Show_Label no_wrap /]\" data-display=\"[Current_Property_Module_Display no_wrap /]\">\n			<span class=\"Property_Module_Header\">\n				<!-- Look up current property -->\n				<!-- TODO broken if used inline without tag-in-tag -->\n\n				<span class=\"Property_Module_Label_Content\">\n					<!-- Label -->\n					<span class=\"Property_Module_Label_Value\">\n						[If Current_Property.Variables.Current_Type.Alias = \"User\" and Current_Property.Variables.Current_Name = \"Team\"]\n							[Then]\n								Reach out to me about\n							[/Then]\n							[Else]\n								[If Current_Property.Variables.Current_Complex]\n									[Then]\n										[If Current_Property.Variables.Current_Multiple]\n											[Then]\n												[If Current_Property.Variables.Current_Label]\n													[Then]\n														[Current_Property.Variables.Current_Label /]\n													[/Then]\n													[Else]\n														[If Current_Property.Variables.Current_Plural_Name]\n															[Then][Current_Property.Variables.Current_Plural_Name /][/Then]\n															[Else][Current_Property.Variables.Current_Name /][/Else]\n														[/If]\n													[/Else]\n												[/If]\n											[/Then]\n											[Else]\n												[If Current_Property.Variables.Current_Label]\n													[Then][Current_Property.Variables.Current_Label /][/Then]\n													[Else][Current_Property.Variables.Current_Name /][/Else]\n												[/If]\n											[/Else]\n										[/If]\n									[/Then]\n									[Else]\n										[If Current_Property.Variables.Current_Label]\n											[Then][Current_Property.Variables.Current_Label /][/Then]\n											[Else][Current_Property.Variables.Current_Name /][/Else]\n										[/If]\n									[/Else]\n								[/If]\n							[/Else]\n						[/If]\n					</span>\n					[If Current_Property.Variables.Modifiable]\n						[Admin]\n							<!-- Settings Link -->\n							<a href=\"#\" class=\"Property_Module_Settings_Link Property_Module_[Current_Property_Module.ID no_wrap /]\" onclick=\"Jelly.jQuery(\'.Page_Settings.Property_Module_[Current_Property_Module.ID no_wrap /]\').toggleClass(\'Visible\');Jelly.jQuery(\'.Property_Module_Settings_Link.Property_Module_[Current_Property_Module.ID no_wrap /]\').toggleClass(\'Selected\');return false;\" tabindex=\"-1\">\n								<img class=\"iconic iconic-sm\" data-src=\"/jelly/libraries/iconic/svg/smart/chevron.svg\" data-direction=\"bottom\">\n							</a>\n						[/Admin]\n					[/If]\n					[If Current_Property.Variables.Editable or Current_Session.Preview_Mode]\n						[Set Current_Property.Variables.Current_Privacy = Current_Property.Viewable_By.Alias/]\n						[Set Current_Property.Variables.Current_Privacy_Explanation = Current_Property.Viewable_By.Explanation/]\n		\n						<!-- Special cases --> \n						<!-- TODO : until someone changes this...--> \n						[If Current_Property.Variables.Current_Type.Alias = \"User\" and Current_Property.Variables.Current_Name = \"Title\"]\n							[Set Current_Property.Variables.Current_Privacy = \"Special\"/]\n							[Set Current_Property.Variables.Current_Privacy_Explanation = \"Everyone can see this, but only admins can edit this.\"/]\n						[/If]\n		\n						[If Current_Property.Variables.Current_Type.Alias = \"Team\" and Current_Property.Variables.Current_Name = \"Tagline\"]\n							[Set Current_Property.Variables.Current_Privacy = \"Public\"/]\n							[Set Current_Property.Variables.Current_Privacy_Explanation = \"Everyone can see this.\"/]\n						[/If]\n		\n						<!-- We need per item per property privacy or maybe not --> \n						[If Current_Item.Type.Alias = \"User\" and (Current_Property.Variables.Current_Alias is \"Phone_Number\" or Current_Property.Variables.Current_Alias is \"Email_Address\")]\n							[If not Current_Item.Allow_Community_Members_to_Contact_Me]\n								[Set Current_Property.Variables.Current_Privacy = \"Private\"/]\n								[Privacy \"Private\" from Database]\n									[Set Current_Property.Variables.Current_Privacy_Explanation = This.Explanation/]\n								[/Privacy]\n							[/If]\n						[/If]\n		\n						[If Current_Item.Type.Alias is \"Team\" and not Current_Item.Allow_Anyone_To_See_Members and Current_Property.Variables.Current_Alias is \"Member\"]\n							[Set Current_Property.Variables.Current_Privacy = \"Private\"/]\n							[Privacy \"Private\" from Database]\n								[Set Current_Property.Variables.Current_Privacy_Explanation = This.Explanation/]\n							[/Privacy]\n						[/If]\n\n						[If Current_Property.Variables.Editable or (Current_Session.Preview_Mode and Current_Property.Variables.Current_Privacy = \"Members\")]\n							<span class=\"Property_Module_Privacy [Current_Property.Variables.Current_Privacy no_wrap/]\">\n								<img class=\"iconic iconic-sm\" data-src=\"/jelly/images/privacy/[Current_Property.Variables.Current_Privacy no_wrap/].svg\" data-direction=\"bottom\">\n								<span class=\"Explanation\">[Current_Property.Variables.Current_Privacy_Explanation/]</span>\n							</span>\n						[/If]\n					[/If]\n					<span class=\"Property_Module_Controls\">\n						[If Current_Property.Variables.Current_Relation = \"Many-To-Many\" and (Current_Property.Variables.Current_Type.Alias != \"User\" or Current_Property.Variables.Current_Name != \"Team\")]\n							<span class=\"Property_Module_Display_Wrapper\">\n							[If Current_Property.Variables.Current_Complex]\n								<span class=\"Display\">\n									[New [Current_Property.Variables.Current_Value_Type.Alias no_wrap/]]\n										[1 This.Property where Value_Type = \"Location\"]\n											<a href=\"#\" \n												onclick=\"[Format as \"Single Line Code\"]\n														var Property_Module_Namespace = \'[Current_Property_Module.Namespace no_wrap/]\';\n														var Property_Module_Element = document.getElementById(Property_Module_Namespace);\n														var Property_Module_Reference = Jelly.References.Get_Reference_For_Element(Property_Module_Element);\n\n														Property_Module_Reference\\[\'Variables\'\\]\\[\'Display\'\\] = \'Map\';\n														Jelly.References.Trigger_Refresh({\'Kind\': \'Element\', \'Element\': Property_Module_Element});\n														Jelly.References.Refresh_All();\n\n														return false;\n													[/Format]\" \n												class=\"Display_Item Display_List\">\n												<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/map-marker.svg\">\n											</a>\n										[/1]\n									[/New]\n									<a href=\"#\" \n										onclick=\"[Format as \"Single Line Code\"]\n												var Property_Module_Namespace = \'[Current_Property_Module.Namespace no_wrap/]\';\n												var Property_Module_Element = document.getElementById(Property_Module_Namespace);\n												var Property_Module_Reference = Jelly.References.Get_Reference_For_Element(Property_Module_Element);\n\n												Property_Module_Reference\\[\'Variables\'\\]\\[\'Display\'\\] = \'Grid\';\n												Jelly.References.Trigger_Refresh({\'Kind\': \'Element\', \'Element\': Property_Module_Element});\n												Jelly.References.Refresh_All();\n\n												return false;\n											[/Format]\" \n										class=\"Display_Item Display_Grid\">\n										<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/grid.svg\">\n									</a>\n									<a href=\"#\" \n										onclick=\"[Format as \"Single Line Code\"]\n												var Property_Module_Namespace = \'[Current_Property_Module.Namespace no_wrap/]\';\n												var Property_Module_Element = document.getElementById(Property_Module_Namespace);\n												var Property_Module_Reference = Jelly.References.Get_Reference_For_Element(Property_Module_Element);\n\n												Property_Module_Reference\\[\'Variables\'\\]\\[\'Display\'\\] = \'List\';\n												Jelly.References.Trigger_Refresh({\'Kind\': \'Element\', \'Element\': Property_Module_Element});\n												Jelly.References.Refresh_All();\n\n												return false;\n											[/Format]\" \n										class=\"Display_Item Display_List\">\n										<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/list.svg\">\n									</a>\n								</span>\n							[/If]\n						</span>\n						[/If]\n						[If Current_Property.Variables.Editable]\n							[If Current_Property.Variables.Current_Complex]\n								[If Current_Property.Variables.Current_Multiple]\n									[Then]\n										<span class=\"Property_Module_Add_Wrapper\">\n											[If Current_Property.Variables.Current_Type.Alias = \"User\" and Current_Property.Variables.Current_Name = \"Team\"]\n												[Then]\n													[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Add_Value_Clean:Property_Alias=[Current_Property.Variables.Current_Alias no_wrap /]\" into \"Window\" with Class = \"Property_Module_Add_Link\"]Join a  [Current_Property.Variables.Current_Value_Type.Name /][/Link]\n												[/Then]\n												[Else]\n													[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Add_Value_Clean:Property_Alias=[Current_Property.Variables.Current_Alias no_wrap /]\" into \"Window\" with Class = \"Property_Module_Add_Link\"]Add [Current_Property.Variables.Current_Value_Type.Name /][/Link]\n												[/Else]\n											[/If]\n										</span>\n									[/Then]\n									[Else]\n										<span class=\"Property_Module_Set_Wrapper\">\n											[Link to \"/Action/Set_Complex_Value:Item=[Current_Item.ID no_wrap /],Item_Type=[Current_Item.Type.Alias no_wrap /],Property_Alias=[Current_Property.Variables.Current_Alias no_wrap /]\" into \"Window\" with Class = \"Property_Module_Set_Link\"]Set [Current_Property.Variables.Current_Value_Type.Name /][/Link]\n										</span>\n									[/Else]\n								[/If]\n								[Ignore]\n									<!-- Todo I think this is tristan\'s security work --> \n									[If Current_Property.Variables.Current_Value_Type.Alias is \"Picture\" or Current_Property.Variables.Current_Value_Type.Alias is \"Video\" or Current_Property.Variables.Current_Value_Type.Alias is \"File\" or Current_Property.Variables.Current_Value_Type.Alias is \"Website\"]\n									[/If]\n								[/Ignore]\n							[/If]\n						[/If]\n					</span>\n					<!-- TODO Search/Display -->\n				</span>\n				[Admin]\n					<span class=\"Page_Settings Property_Module_[Current_Property_Module.ID no_wrap /]\">\n						<span class=\"Setting_Row\">\n							<span class=\"Setting_Label\">\n								Name:\n							</span>\n							<span class=\"Setting_Value\">\n								[If Current_Property_Module.Reverse]\n									[Then]\n										<!-- TODO reverse label shit --> \n										[Current_Property.Action \"Edit Text Property\" with Property_Alias = \"Reverse_Label\" /]\n									[/Then]\n									[Else]\n										[Current_Property.Action \"Edit Text Property\" with Property_Alias = \"Label\" /]\n									[/Else]\n								[/If]\n							</span>\n						</span>\n		\n						<span class=\"Setting_Row\">\n							<span class=\"Setting_Label\">\n								Privacy:\n							</span>\n							<span class=\"Setting_Value\">\n								<a class=\"Setting_Privacy_Menu_Link\" href=\"#\" onclick=\"Jelly.Interface.Create_Menu({URL: \'/Action/Set_Property_Privacy_Clean:Item=[Current_Property.ID no_wrap /],Item_Type=Property\', \'Alias\': \'Privacy_Menu\', Menu_Namespace: \'[Namespace no_wrap /]_Menu\', Attach: \'Element\', Attach_Element: this, Edge: \'Bottom\'});return false;\">\n									[Current_Property.Viewable_By.Alias/]\n								</a>\n								<span class=\"Setting_Privacy_Menu_Chevron\">\n									<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/chevron.svg\" data-direction=\"bottom\">\n								</span>									\n							</span>\n						</span>\n						[Ignore]\n							[If Current_Property.Variables.Current_Multiple]\n								<span class=\"Setting_Row\">\n									<span class=\"Setting_Label\">\n										Plural Name:\n									</span>\n									<span class=\"Setting_Value\">\n										[If Current_Property_Module.Reverse]\n											[Then]\n												[Current_Property.Action \"Edit Text Property\" with Property_Alias = \"Reverse_Plural_Name\" /]\n											[/Then]\n											[Else]\n												[Current_Property.Action \"Edit Text Property\" with Property_Alias = \"Plural_Name\" /]\n											[/Else]\n										[/If]\n									</span>\n								</span>\n							[/If]\n						[/Ignore]\n						[Ignore]\n							<!-- TODO -->\n							Hide Property\n						[/Ignore]\n	\n						[Ignore]\n							<!-- TODO Changing value types -->\n							Value Type: [Current_Property.Variables.Current_Value_Type.Alias /]<br/>\n						[/Ignore]\n						[Ignore]\n							[If Current_Property.Variables.Current_Complex]\n								<span class=\"Setting_Row\">\n									<!-- Relation property -->\n									[Action \"Set Property Relation Clean\" with Current_Property = Current_Property /]\n								</span>\n							[/If]\n						[/Ignore]\n	\n						<!-- Check if the property is of the current type instead of a parent type -->\n						[If Current_Property.Variables.Current_Type.ID = Current_Item.Type.ID]\n							<span class=\"Setting_Row\">\n								<!-- Delete property -->\n								<span class=\"Setting_Value\">\n									[Link to remove Current_Property]Delete[/Link] [Current_Property_Module.Action \"Move Up\"/]  [Current_Property_Module.Action \"Move Down\"/]\n								</span>\n							</span>\n						[/If]\n					</span>\n				[/Admin]\n			</span>\n			\n			[Member]\n				[If Current_Property.Variables.Current_Value_Type.Alias = \"User\"]\n					<span class=\"Property_Module_Communications\">\n						[Link to \"/[Current_Item.Type.Alias no_wrap/]/[Current_Item.ID no_wrap/]/Email_Group:Property_Alias=[Current_Property.Variables.Current_Alias no_Wrap/]\" into \"Window\" with Class=\"Big_List_Item_Mail\"]\n							<img data-state=\"closed\" data-src=\"/jelly/libraries/iconic/svg/smart/envelope.svg\" class=\"iconic\" alt=\"mail\" />\n							<span class=\"Label\">Email</span>\n						[/Link]\n\n						[Link to \"/[Current_Item.Type.Alias no_wrap/]/[Current_Item.ID no_wrap/]/Text_Group:Property_Alias=[Current_Property.Variables.Current_Alias no_Wrap/]\" into \"Window\" with Class=\"Big_List_Item_Text\"]\n							<img data-state=\"closed\" data-src=\"/jelly/images/flaticon/chat81.svg\" class=\"iconic\" alt=\"mail\" />\n							<span class=\"Label\">Text</span>\n						[/Link]\n					</span>\n				[/If]\n			[/Member]\n			\n			<!-- Value -->\n			<span class=\"Property_Module_Value\">\n				<!-- Check if property is simple or complex -->\n				[If Current_Property.Variables.Current_Value_Type is simple type]\n					[Then]\n						<!-- Simple Value Type -->\n						[If Current_Property.Variables.Editable and not Current_Property.Variables.Editable_Override]\n							[Then]\n								[Current_Item.Action \"Edit Text Property\" with Property_Alias = Current_Property_Module_Property_Alias, Property_Value_Type = Current_Property.Variables.Current_Value_Type.Alias /]\n							[/Then]\n							[Else]\n								[If Current_Property.Variables.Current_Value_Type.Alias is \"Boolean\"]\n									[Then]\n										[If Current_Item.[Current_Property_Module_Property_Alias no_wrap /]]\n											[Then]Yes[/Then]\n											[Else]No[/Else]\n										[/If]\n									[/Then]\n									[Else]\n										[Current_Item.[Current_Property_Module_Property_Alias no_wrap /] /]\n									[/Else]\n								[/If]\n							[/Else]\n						[/If]\n					[/Then]\n					[Else]\n						<!-- Complex Value Type -->\n		\n						<!-- TODO: Single vs. Multiple -->\n						<!-- TODO: Passing this in as a Data_Module in order to use the same kind of property list -->\n		\n						<!-- Update cover -->\n						<!-- TODO should this be here? -->\n						[This.Action \"Set Cover Property Item\" as Execute /]\n		\n						<!-- Update explanation -->\n						[This.Action \"Set Explanation Property Item\" as Execute /]\n		\n						[If Current_Property_Module_Display = \"Map\"]\n							[Then]\n								<!-- Map -->\n								<span class=\"Big_List Map\">\n									<span class=\"Big_List_Items\">\n										<span class=\"List_Map\" id=\"[Current_Property_Module.Namespace no_wrap /]_Map\">\n										</span>\n									</span>\n								</span>\n								[External_Script]\n									Jelly.Interface.Generate_Map({\n											Alias: \"[Current_Property_Module.Namespace no_wrap /]_Map\",\n											Locate_User: true\n										});\n								[/External_Script]\n\n								[Current_Item.[Current_Property.Variables.Current_Alias no_wrap /] [If Current_Property_Module.Where_Clause]where [Current_Property_Module.Where_Clause no_wrap /][/If]]\n									[Set Current_Value to This /]\n									[1 Current_Value.Property where Value_Type is \"Location\"]\n										[Set Current_Location_Property to This/]\n										[Current_Value.[Current_Location_Property.Alias no_wrap/]]\n											[External_Script]\n												// Add marker to map\n												Jelly.Interface.Create_Location(\n														{\n															[If Current_Value.Type.Alias = \"User\"]\"Icon\": \"heart\",[/If]\n															\"Map_Alias\": \"[Current_Property_Module.Namespace no_wrap /]_Map\",\n															\"Position\": \\[[This.Latitude no_wrap /], [This.Longitude no_wrap /]\\],\n															\"Content\": \"[Format as \"Javascript String\"]\n																<span class=\"Big_List Popup\">\n																	<span class=\"Big_List_Item\">\n																		[Link to Current_Value by Alias with Class = \"Big_List_Item_Details\"]\n																			<span class=\"Big_List_Item_Cover\">\n																				[1 Current_Value.Property where Value_Type = \"Picture\"]\n																					[Set Current_Cover_Property to This/]\n																					[Current_Value.[Current_Cover_Property.Alias no_wrap/] as Cover/]\n																				[/]\n																			</span>\n																			<span class=\"Big_List_Item_Name\">\n																				[Current_Value.Name /]\n																			</span>\n																		[/Link]\n																		<span class=\"Big_List_Item_Actions\">\n																			[Admin]\n																				[If not Current_Item.Core]\n																					[Link to \"/Type/[Current_Value.Type.Alias no_wrap /]/[Current_Value.ID no_wrap /]/Remove\" into \"Window\" with Class = \"Delete\"]\n																						<span class=\"Content\">\n																							<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/trash.svg\">\n																						</span>\n																					[/Link]\n																				[/If]\n																			[/Admin]\n																		</span>\n																	</span>\n																</span>\n															[/Format]\"\n														}\n													);\n											[/External_Script]\n										[/]\n									[/1 Current_Value.Property]\n								[/]\n								[External_Script]\n									Jelly.Interface.Show_All_Locations({\"Map_Alias\": \"[Current_Property_Module.Namespace no_wrap /]_Map\"});\n								[/External_Script]\n							[/Then]\n							[Else]\n								<!-- Items -->\n								<span class=\"Big_List [If Current_Property_Module_Display][Then][Current_Property_Module_Display no_wrap /][/Then][Else]List[/Else][/If] [If Current_Property_Module.Size][Then][Current_Property_Module.Size no_wrap /][/Then][Else]Normal[/Else][/If]\">\n									[Current_Item.[Current_Property.Variables.Current_Alias no_wrap /]\n										[If Current_Property_Module.Where_Clause]where [Current_Property_Module.Where_Clause no_wrap /][/If]\n										[If Current_Property.Variables.Offset]at [Current_Property.Variables.Offset no_wrap /][/If]\n										[If Current_Property_Module.Sort_Property_Alias]by [Current_Property_Module.Sort_Property_Alias no_wrap/][If Current_Property_Module.Sort_Reverse] Descending[/If][/If]														\n										as Property_Module_List_Item \n										iterator_classes \"Big_List_Items\" \n										item_classes \"Big_List_Item Hover\" \n										with \n											Data_Module = Current_Property_Module, \n											Current_Item = Current_Item, \n											Current_Property_Value_Type = Current_Property.Variables.Current_Value_Type, \n											Current_Property_Alias = Current_Property.Variables.Current_Alias, \n											Editable = Current_Property.Variables.Editable \n									/]\n								</span>\n\n							[/Else]\n						[/If]		\n					[/Else]\n				[/If]\n			</span>\n		</span>\n	[/If]\n[/Current_Property_Module.Property_Item]',NULL,NULL,NULL,'Published','Property_Module'),('Editing','2016-01-15 13:47:28',346,'Editing',21,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Show default template -->\n	[Set Globals.Editing to True /]\n	[This as Default /]\n\n	<!-- Disambiguate -->\n	[Set Current_Property_Module to This /]\n	[Set Current_Property_Module_Property_Item to Current_Property_Module.Property_Item /]\n	\n	<!-- Controls -->\n	<div class=\"Module_Controls\">\n		<!-- Change Size -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Size\" /]\n		</div>\n		\n		<!-- Change Width -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Width\" /]\n		</div>\n		\n		<!-- Show/Hide Label -->\n		<div class=\"Group\">\n			[Current_Property_Module.Action \"Change Label\" /]\n		</div>\n		\n		<!-- Change Overlay (for complex values) -->\n		[If Current_Property_Module_Property_Item.Value_Type is complex type]\n			<div class=\"Group\">\n				[Current_Content_Module.Action \"Change Overlay\" /]\n			</div>\n		[/If]\n		\n		<!-- Display (for complex values) -->\n		[If Current_Property_Module_Property_Item.Value_Type is complex type]\n			<div class=\"Group\">\n				[Current_Property_Module.Action \"Change Display\" /]\n			</div>\n		[/If]\n		\n		<!-- Add link (for complex values) -->\n		[If Current_Property_Module_Property_Item.Value_Type is complex type]\n			<div class=\"Group\">\n				[Current_Property_Module.Action \"Add Item\" /]\n			</div>\n		[/If]\n		\n		<!-- Delete -->\n		[Link to \"/Property_Module/[This.ID no_wrap /]/Hide\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/trash.svg\"> Hide[/Link]\n		\n		<!-- Done -->\n		[If Current_Property_Module_Property_Item.Value_Type is simple type]\n			[Then]\n				<!-- Save Edit Inline Action -->\n				<a href=\"#\" onclick=\"Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': Jelly.jQuery(\'#[Current_Content_Module_Namespace no_wrap /] \\[data-kind=Item\\]\\[data-type=Type_Action\\]\\[data-alias=Edit_Inline\\]\').get(0)});return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/check-thin.svg\"> Done</a>\n			[/Then]\n			[Else]\n				<!-- Switch to viewing -->\n				[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n					<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/check-thin.svg\"> Done\n				[/Link]\n			[/Else]\n		[/If]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Property_Module'),('Default','2016-01-15 13:47:28',347,'Default',22,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Required Variables: Data_Module -->\n\n<!-- Disambiguate -->\n[Set Current_Data_Module to This /]\n[Set Current_Type to This.List /]\n\n<!-- Update cover -->\n<!-- TODO implement Data Module-specific cover property -->\n[Current_Type.Action \"Set Cover Property Item\" as Execute /]\n[Current_Type.Action \"Set Explanation Property Item\" as Execute /]\n\n<!-- Add unadded properties -->\n[Ignore]\n	<!-- TODO -->\n	[Current_Data_Module.Action \"Add Unadded Properties\" as Execute /]\n[/Ignore]\n\n<!-- Body -->\n<span class=\"Module_Content Data_Module\" data-module=\"Data_Module\" data-size=\"[This.Size no_wrap /]\" data-width=\"[This.Width no_wrap /]\" data-overlay=\"[This.Overlay no_wrap /]\" data-display=\"[This.Display no_wrap /]\">\n	\n	<span class=\"Module_Settings\" id=\"[Current_Data_Module.Namespace no_wrap /]_Settings\">\n		<span class=\"Setting_Row\">\n			[Link to Remove Current_Data_Module]Remove[/Link]\n		</span>\n	</span>\n	\n	[ignore]\n		<!-- TODO Fix map -->\n		<!-- Map -->\n		<span class=\"List_Map_Wrapper\">\n			<span class=\"List_Map\" id=\"[Content_Module.Namespace no_wrap /]_Map\">\n			</span>\n		</span>\n		[External_Script]\n			Jelly.Interface.Generate_Map({Alias: \"[Content_Module.Namespace no_wrap /]_Map\"});\n		[/External_Script]\n	[/ignore]\n	\n	<!-- Iterate over items -->\n	<!-- TODO add No_Child_Types Support --> \n	<!-- TODO filters -->\n	<span class=\"Big_List [This.Display no_wrap /]\">\n		[[Current_Type.Alias No_Wrap /] from Database By Name Iterator_Classes \"Data_Module_List [This.Display no_wrap /]\" Item_Classes \"Data_Module_List_Item\" as Data_Module_List_Item iterator_classes \"Big_List_Items\" item_classes \"Big_List_Item Hover\" with Current_Data_Module = This /]\n	</span>\n				\n	[Admin]\n		<!-- Admin Only... -->\n		\n		<!-- Edit Controls -->\n		<div id=\"[This.ID no_wrap /]_Edit_Link_Finder\">\n			<a href=\"#\" onclick=\"Jelly.jQuery(this).toggleClass(\'Selected\');Jelly.jQuery(\'#[Current_Data_Module.Namespace no_wrap /]_Settings\').toggleClass(\'Visible\'); return false;\" class=\"Module_Edit_Link\">\n				<img class=\"iconic iconic-sm\" data-src=\"/jelly/libraries/iconic/svg/smart/pencil.svg\">\n			</a>\n		</div>\n	[/Admin]\n</span>',NULL,NULL,NULL,'Published','Data_Module'),('Editing','2016-01-15 13:47:28',348,'Editing',23,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Show default template -->\n[This as Default /]\n\n[Admin]\n	<!-- Admin Only... -->\n	\n	<div class=\"Module_Controls\">\n		<!-- Change View -->\n		<!-- TODO Clean up link -->\n		<div class=\"Group\">\n			[Link to \"/Data_Module/[This.ID no_wrap /]/Change_Data\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/wrench.svg\"> View[/Link]\n		</div>\n		\n		<!-- Change Size -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Display\" /]\n		</div>\n		\n		<!-- Change Size -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Size\" /]\n		</div>\n		\n		<!-- Change Width -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Width\" /]\n		</div>\n		\n		<!-- Change Overlay -->\n		<div class=\"Group\">\n			[Current_Content_Module.Action \"Change Overlay\" /]\n		</div>\n		\n		<!-- Delete -->\n		<div class=\"Group\">\n			[Link to \"/Data_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/trash.svg\"> Delete[/Link]\n		</div>\n		\n		<!-- Done -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/check-thin.svg\"> Done\n		[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Data_Module'),('Default','2016-01-15 13:47:28',349,'Default',24,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Follow_Module to This /]\n\n<!-- Follow Button -->\n[Current_Item.Action \"Follow\" /]',NULL,NULL,NULL,'Published','Follow_Module'),('Editing','2016-01-15 13:47:28',350,'Editing',25,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Follow_Module to This /]\n\n<!-- Show default template -->\n[This as Default /]\n\n[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Module controls -->\n	<div class=\"Module_Controls\">\n		<!-- Save -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			Done\n		[/Link]\n		\n		<!-- Delete Module -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]Delete[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Follow_Module'),('Default','2016-01-15 13:47:28',351,'Default',26,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_Get_Involved_Module to This /]\n\n<!-- Module Content -->\n<span class=\"Get_Involved_Module Module_Content\" data-module=\"Get_Involved_Module\">\n	[Set Get_Involved_Module.Variables.Show = False/]\n	[Admin Ignore_Preview_Mode][Set Get_Involved_Module.Variables.Show = True/][/Admin]\n	[Guest][Set Get_Involved_Module.Variables.Show = True/][/Guest]\n	<!-- Follow Button -->\n	[If Get_Involved_Module.Variables.Show]\n		[Link to \"/Get_Involved\" with Class = \"Button\"]\n			Join the Community\n		[/Link]\n	[/If]\n</span>',NULL,NULL,NULL,'Published','Get_Involved_Module'),('Default','2016-01-15 13:47:28',352,'Default',27,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Schedule Lunch Action -->\n[Current_Item.Action \"Schedule Lunch Button\" /]',NULL,NULL,NULL,'Published','Lunch_Scheduling_Module'),('Editing','2016-01-15 13:47:28',353,'Editing',28,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Show default template -->\n[This as Default /]\n\n[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Module controls -->\n	<div class=\"Module_Controls\">\n		<!-- Save -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			Done\n		[/Link]\n		\n		<!-- Delete Module -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]Delete[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Lunch_Scheduling_Module'),('Default','2016-01-15 13:47:28',354,'Default',29,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Purchase -->\n<!-- TODO implement label and which property to use for price -->\n<!-- TODO no_wrap prevents the type_action from refreshing, but type_actions should never refresh without their parents -->\n[Current_Item.Action \"Purchase\" no_refresh /]',NULL,NULL,NULL,'Published','Purchase_Module'),('Editing','2016-01-15 13:47:28',355,'Editing',30,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Show default template -->\n[This as Default /]\n\n[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Module controls -->\n	<div class=\"Module_Controls\">\n		<!-- Save -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			Done\n		[/Link]\n		\n		<!-- Delete Module -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]Delete[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Purchase_Module'),('Default','2016-01-15 13:47:28',356,'Default',31,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Disambiguate -->\n[Set Current_RSVP_Module to This /]\n\n<!-- RSVP -->\n[Current_Item.Action \"RSVP\" no_refresh /]',NULL,NULL,NULL,'Published','RSVP_Module'),('Editing','2016-01-15 13:47:28',357,'Editing',32,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Show default template -->\n[This as Default /]\n\n[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Module controls -->\n	<div class=\"Module_Controls\">\n		<!-- Save -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			Done\n		[/Link]\n		\n		<!-- Delete Module -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]Delete[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','RSVP_Module'),('Default','2016-01-15 13:47:28',358,'Default',33,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Purchase -->\n<!-- TODO implement label and which property to use for price -->\n<!-- TODO no_wrap prevents the type_action from refreshing, but type_actions should never refresh without their parents -->\n\n[Action \"Donate\" no_refresh /]',NULL,NULL,NULL,'Published','Donate_Module'),('Editing','2016-01-15 13:47:28',359,'Editing',34,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- Show default template -->\n[This as Default /]\n\n[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Module controls -->\n	<div class=\"Module_Controls\">\n		<!-- Save -->\n		[Link to \"/[Current_Item.Type.Alias no_wrap /]/[Current_Item.ID no_wrap /]/Viewing_Content_Module:Content_Module_ID=[Current_Content_Module.ID no_wrap /]\" into \"[Current_Content_Module_Namespace no_wrap /]\"]\n			Done\n		[/Link]\n		\n		<!-- Delete Module -->\n		<!-- TODO Clean up link -->\n		[Link to \"/Content_Module/[This.ID no_wrap /]/Delete\" into \"Window\"]Delete[/Link]\n	</div>\n[/Admin]',NULL,NULL,NULL,'Published','Donate_Module'),('Default','2016-01-15 13:47:28',360,'Default',35,NULL,0,'2016-01-15 13:47:29','Design','Admin','text/html','<!-- TODO support for non-property input modules -->\n\n[Set Current_Input_Module to This /]\n[Set Current_Property to This.Property_Item /]\n[Set Current_Property_Value_Type to Current_Property.Value_Type /]\n[Set Current_Property_Type to Current_Property.Values.Type /]\n\n[If Current_Property.Label]\n	[Then][Set Current_Property.Display_Name to Current_Property.Label/][/Then]\n	[Else][Set Current_Property.Display_Name to Current_Property.Name/][/Else]\n[/If]\n\n<!-- Body -->\n<span class=\"Module_Content Input_Module\" data-module=\"Input_Module\">\n	<span class=\"Module_Settings\" id=\"[Current_Input_Module.Namespace no_wrap /]_Settings\">\n		<span class=\"Setting_Row\">\n			<span class=\"Setting_Label\" style=\"width:56px;\">Move:</span>\n			<span class=\"Setting_Value\">\n				[Current_Input_Module.Action \"Move Up\"/] [Current_Input_Module.Action \"Move Down\"/]\n			</span>\n		</span>\n\n		[If Current_Property.Name is not \"Name\" and Current_Property.Name is not \"Email Address\"]\n			<span class=\"Setting_Row\">\n				<span class=\"Setting_Value\">\n					[Link to Remove Current_Input_Module]Hide \"[Current_Property.Display_Name /]\"[/Link]\n				</span>\n			</span>\n		[/If]\n	</span>\n\n	<span class=\"Label\">\n		[Current_Property.Display_Name /]\n	</span>\n	<span class=\"Value\">\n		[If Current_Property_Value_Type is complex type]\n			[Then]\n				[If Current_Property_Value_Type.Alias is \"Picture\" or Current_Property_Value_Type.Alias is \"Video\" or Current_Property_Value_Type.Alias is \"Sound\" or Current_Property_Value_Type.Alias is \"Website\" or Current_Property_Value_Type.Alias = \"Social_Media\" or Current_Property_Value_Type.Alias = \"Location\"]\n					[Then]\n						<span class=\"Block\" style=\"padding: 10px; border-style: solid; border-width: 1px; border-color: #e7e7e7;\">\n							[If Current_Property_Value_Type.Alias is \"Picture\"]\n								[Input \"Picture\" with Name = Current_Property.Name /]\n							[/If]\n							[If Current_Property_Value_Type.Alias is \"Video\"]\n								[Input \"Video\" with Name = Current_Property.Name /]\n							[/If]\n							[If Current_Property_Value_Type.Alias is \"Sound\"]\n								[Input \"Sound\" with Name = Current_Property.Name /]\n							[/If]\n							[If Current_Property_Value_Type.Alias is \"Website\"]\n								[Input \"Website\" with Name = Current_Property.Name /]\n							[/If]\n							[If Current_Property_Value_Type.Alias is \"Social_Media\"]\n								[Input \"Social Media\" with Name = Current_Property.Name /]\n							[/If]\n							[If Current_Property_Value_Type.Alias is \"Location\"]\n								[Input \"Location\" with Name = Current_Property.Name /]\n							[/If]\n						</span>\n					[/Then]\n					[Else]\n						[Input \"Select Clean\" with Name = Current_Property.Name, Value_Type = Current_Property_Value_Type[If Current_Property.Relation = \"Many-To-Many\" or Current_Property.Relation = \"One-To-Many\"], Allow_Multiple_Values = True[/If]/]\n					[/Else]\n				[/If]\n			[/Then]\n			[Else]\n				[Current_Property_Value_Type.Default_Input with Name = Current_Property.Name, Value_Type = Current_Property_Value_Type /]\n			[/Else]\n		[/If]\n	</span>\n\n	[Admin]\n		<!-- Admin Only... -->\n\n		<!-- Edit Controls -->\n		<div id=\"[This.ID no_wrap /]_Edit_Link_Finder\">\n			<a href=\"#\" onclick=\"Jelly.jQuery(this).toggleClass(\'Selected\');Jelly.jQuery(\'#[Current_Input_Module.Namespace no_wrap /]_Settings\').toggleClass(\'Visible\'); return false;\" class=\"Module_Edit_Link\">\n				<img class=\"iconic iconic-sm\" data-src=\"/jelly/libraries/iconic/svg/smart/pencil.svg\">\n			</a>\n		</div>\n	[/Admin]\n</span>',NULL,NULL,NULL,'Published','Input_Module'),('Default','2016-01-15 13:47:28',361,'Default',36,NULL,0,'2016-01-15 13:47:29','Interaction','Admin','text/html','[Dashboard.Content /]',NULL,NULL,NULL,'Published','Dashboard'),('Default','2016-01-15 13:47:28',362,'Default',37,NULL,0,'2016-01-15 13:47:29','Simple','Admin','text/html','[This.Month /] [This.Date /], [This.Year /]',NULL,NULL,NULL,'Published','Date'),('Input','2016-01-15 13:47:28',363,'Input',38,NULL,0,'2016-01-15 13:47:29','Simple','Admin','text/html','[Input \"Date\" with Alias Date.Alias, Value Date.Value/]',NULL,NULL,NULL,'Published','Date'),('XML_Value','2016-01-15 13:47:28',364,'XML Value',39,NULL,0,'2016-01-15 13:47:29','Simple','Admin','text/html','[Year_Number /]-[Month_Number/]-[Date_Number/]',NULL,NULL,NULL,'Published','Date'),('Badge','2016-01-15 13:47:28',365,'Badge',40,NULL,0,'2016-01-15 13:47:29','Simple','Admin','text/html','[Month /] [Date /], [Year /], [Hour /]:[Minute/] [Period/]',NULL,NULL,NULL,'Published','Date_Time'),('Default','2016-01-15 13:47:28',366,'Default',41,NULL,0,'2016-01-15 13:47:29','Simple','Admin','text/html','[Month /] [Date /], [Year /] at [Hour /]:[Minute/] [Period/]',NULL,NULL,NULL,'Published','Date_Time'),('Input','2016-01-15 13:47:28',367,'Input',42,NULL,0,'2016-01-15 13:47:29','Simple','Admin','text/html','[Input \"Date Time\" with Alias Date_Time.Alias, Value Date_Time.Value/]',NULL,NULL,NULL,'Published','Date_Time'),('XML_Value','2016-01-15 13:47:28',368,'XML Value',43,NULL,