INSERT INTO `Type_Action` VALUES ('Change_Picture','2016-01-15 13:47:28',593,'Change Picture',1,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin Only... -->\n	\n	<div style=\"width: 800px; height: 600px;\">\n		<!-- Picture Input Grid -->\n		[Type \"Picture\"]\n			[Input \"Menu\" with Name = \"Picture\", Value_Type = This, Execute_On_Change = 1, Display = \"Grid\", Display_Menu = \"False\", Display_Value = \"False\" /]\n		[/Type]\n	</div>\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Edited\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Set Picture -->\n	[Set Current_Action.Target.Picture to Form.Picture /]\n\n	<!-- Save Item -->\n	[Save Current_Action.Target /]\n	\n	<!-- Trigger Edited Event -->\n	[External_Script]\n		Jelly.Handlers.Call_Handler_For_Target(\n			{\n				\"Event\": \"Edited\",\n				\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\"),\n				\"Item\": \"[Current_Action.Target as Key No_Wrap /]\",\n				\"Item_Type\": \"[Current_Action.Target.Type as Alias No_Wrap /]\",\n				\"Badge\": \"[Format as \"Javascript String\"][Current_Action.Target as Badge with No_Drag = 1/][/Format]\"\n			});\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Picture_Module'),('Change_Video','2016-01-15 13:47:28',594,'Change Video',2,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin Only... -->\n	\n	<div style=\"width: 800px; height: 600px;\">\n		<!-- Video Input Grid -->\n		[Type \"Video\"]\n			[Input \"Menu\" with Name = \"Video\", Value_Type = This, Execute_On_Change = 1, Display = \"Grid\", Display_Menu = \"False\", Display_Value = \"False\" /]\n		[/Type]\n	</div>\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Edited\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Set Video -->\n	[Set Current_Action.Target.Video to Form.Video /]\n\n	<!-- Save Item -->\n	[Save Current_Action.Target /]\n	\n	<!-- Trigger Edited Event -->\n	[External_Script]\n		Jelly.Handlers.Call_Handler_For_Target(\n			{\n				\"Event\": \"Edited\",\n				\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\"),\n				\"Item\": \"[Current_Action.Target as Key No_Wrap /]\",\n				\"Item_Type\": \"[Current_Action.Target.Type as Alias No_Wrap /]\",\n				\"Badge\": \"[Format as \"Javascript String\"][Current_Action.Target as Badge with No_Drag = 1/][/Format]\"\n			});\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Video_Module'),('Change_Scripting','2016-01-15 13:47:28',595,'Change Scripting',3,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin Only... -->\n	\n	[Input \"Text Area\" with Name = \"Script\", Value = Current_Action.Target.Script /]\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Edited\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Set Picture -->\n	[Set Current_Action.Target.Script to Form.Script_Original /]\n\n	<!-- Save Item -->\n	[Save Current_Action.Target /]\n	\n	<!-- Trigger Edited Event -->\n	[External_Script]\n		Jelly.Handlers.Call_Handler_For_Target(\n			{\n				\"Event\": \"Edited\",\n				\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\"),\n				\"Item\": \"[Current_Action.Target as Key No_Wrap /]\",\n				\"Item_Type\": \"[Current_Action.Target.Type as Alias No_Wrap /]\",\n				\"Badge\": \"[Format as \"Javascript String\"][Current_Action.Target as Badge with No_Drag = 1/][/Format]\"\n			});\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Scripting_Module'),('Add_Item','2016-01-15 13:47:28',596,'Add Item',4,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin only -->\n	\n	<!-- Pass current item to action -->\n	[Input \"Hidden\" with Name = \"Current Item\", Value = Current_Item /]\n	\n	<!-- Link to select property -->\n	[Link to Execute]\n		<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/plus-thin.svg\">\n		Add\n	[/Link]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Disambiguate -->\n	[Set Current_Property_Module to Current_Action.Target /]\n	[Set Current_Property_Module_Property_Item to Current_Property_Module.Property_Item /]\n	[Set Current_Item to Form.Current_Item /]\n	\n	<!-- Create Item -->\n	[New [Current_Property_Module_Property_Item.Value_Type.Alias no_wrap /]]\n		<!-- Disambiguate -->\n		[Set Current_New_Item to This /]\n		\n		<!-- Save Item -->\n		[Save Current_New_Item /]\n		\n		<!-- TODO: Reverse? -->\n		[Add Current_New_Item to Current_Item.[Current_Property_Module_Property_Item.Alias no_wrap /] /]\n		\n		<!-- Show Inspector -->\n		[External_Script]\n			Jelly.Interface.Inspect({Type_Alias: \"[Current_New_Item.Type.Alias no_wrap /]\", Item_ID: [Current_New_Item.ID no_wrap /]});\n		[/External_Script]\n	[/New]\n	\n		\n	<!-- Trigger Edited Event -->\n	<!-- TODO -->\n[/Admin]',NULL,NULL,'Published','Property_Module'),('Change_Label','2016-01-15 13:47:28',597,'Change Label',5,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin only -->\n	\n	<!-- Link to select property -->\n	<div class=\"Inner_Group\">\n		<div class=\"Items\" data-target-property=\"Show_Label\" data-value=\"[Current_Action.Target.Show_Label no_wrap /]\">\n			<span id=\"[Current_Action.Namespace no_wrap /]_Show_Label_Input_Finder\">\n				[Input \"Hidden\" with Name = \"Show Label\", Execute_On_Change = 1 /]\n			</span>\n			\n			[Set Data_Target_Property = \"Show_Label\" /]\n			[Set Data_Target_Attribute = \"data-show-label\" /]\n			\n			[Set Data_Value = True /]\n			[Set Data_Name = \"On\" /]\n			<a href=\"#\" [Data_Target_Attribute no_wrap /]=\"[Data_Value no_wrap /]\" [If Current_Action.Target.[Data_Target_Property no_wrap /] is Data_Value]class=\"Selected\"[/If] onclick=\"[Format as \"Single Line Code\"]\n					Jelly.jQuery(\'#[Current_Action.Target.Namespace no_wrap /] .Module_Content\').attr(\'[Data_Target_Attribute no_wrap /]\', \'[Data_Value no_wrap /]\');\n					Jelly.jQuery(this).siblings(\'a\').removeClass(\'Selected\');\n					Jelly.jQuery(this).addClass(\'Selected\');\n					Jelly.jQuery(\'#[Current_Action.Namespace no_wrap /]_[Data_Target_Property no_wrap /]_Input_Finder \\[name=[Data_Target_Property no_wrap /]\\]\').val(\'[Data_Value no_wrap /]\').change();\n					return false;\n			[/Format]\">[Data_Name no_wrap /]</a>\n			\n			[Set Data_Value = False /]\n			[Set Data_Name = \"Off\" /]\n			<a href=\"#\" [Data_Target_Attribute no_wrap /]=\"[Data_Value no_wrap /]\" [If Current_Action.Target.[Data_Target_Property no_wrap /] is Data_Value]class=\"Selected\"[/If] onclick=\"[Format as \"Single Line Code\"]\n					Jelly.jQuery(\'#[Current_Action.Target.Namespace no_wrap /] .Module_Content\').attr(\'[Data_Target_Attribute no_wrap /]\', \'[Data_Value no_wrap /]\');\n					Jelly.jQuery(this).siblings(\'a\').removeClass(\'Selected\');\n					Jelly.jQuery(this).addClass(\'Selected\');\n					Jelly.jQuery(\'#[Current_Action.Namespace no_wrap /]_[Data_Target_Property no_wrap /]_Input_Finder \\[name=[Data_Target_Property no_wrap /]\\]\').val(\'[Data_Value no_wrap /]\').change();\n					return false;\n			[/Format]\">[Data_Name no_wrap /]</a>\n		</div>\n		<div class=\"Label\">\n			Label\n		</div>\n	</div>\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Hide Label -->\n	[Set Current_Action.Target.Show_Label to Form.Show_Label /]\n	\n	<!-- Save Property Module -->\n	[Save Current_Action.Target no_refresh /]\n		\n	<!-- Trigger Edited Event -->\n	<!-- TODO: Add global Deleted event -->\n	[External_Script]\n		Jelly.Handlers.Call_Handler_For_Target(\n			{\n				\"Event\": \"Edited\",\n				\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\")\n			});\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Property_Module'),('Hide','2016-01-15 13:47:28',598,'Hide',6,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Execute Link -->\n	<div style=\"width: 300px;\">\n		Hide this property? You can always show it again later by adding it back.\n	</div>\n	[Link to Execute /]\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Edited\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Hide Property Module -->\n	[Set Current_Action.Target.Visible to false /]\n	\n	<!-- Save Property Module -->\n	[Save Current_Action.Target /]\n		\n	<!-- Trigger Edited Event -->\n	<!-- TODO: Add global Deleted event -->\n	[External_Script]\n		Jelly.Handlers.Call_Handler_For_Target(\n			{\n				\"Event\": \"Edited\",\n				\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\")\n			});\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Property_Module'),('Set_Cover_Property_Item','2016-01-15 13:47:28',599,'Set Cover Property Item',7,NULL,0,'2016-01-15 13:47:29','Design','Admin','<!-- TODO: Currently disabled -->\n\n[Admin]\n	<!-- Admin Only -->\n	[Link to Execute /]\n[/Admin]','<!-- TODO: Currently disabled -->\n\n<!-- Disambiguate -->\n[Set Current_Data_Module to Current_Action.Target /]\n[Set Current_Data_Module_Type to Current_Data_Module.Type_Item /]\n\n<!-- Check if a cover property has already been set -->\n<!-- TODO better way to figure out when to run this? -->\n[If Current_Data_Module.Cover_Property_Item does not exist]\n	<!-- Check if the type has a property with a cover -->\n	<!-- TODO check Pictures first, etc. -->\n	[New [Current_Data_Module_Type.Alias no_wrap /]]\n		<!-- Disambiguate -->\n		[Set Current_Cover_Item to This /]\n		\n		<!-- Find a property that can be used as a cover -->\n		<!-- TODO the count is not passed in to Property value queries -->\n		[1 Current_Cover_Item.Properties Where Value_Type = \"Picture\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Data_Module.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Data_Module.Cover_Property_Item to This.ID /]\n					[Save Current_Data_Module /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n		[1 Current_Cover_Item.Properties Where Value_Type = \"Video\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Data_Module.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Data_Module.Cover_Property_Item to This.ID /]\n					[Save Current_Data_Module /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n		[1 Current_Cover_Item.Properties Where Value_Type = \"Location\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Data_Module.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Data_Module.Cover_Property_Item to This.ID /]\n					[Save Current_Data_Module /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n		[1 Current_Cover_Item.Properties Where Value_Type is \"Website\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Data_Module.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Data_Module.Cover_Property_Item to This.ID /]\n					[Save Current_Data_Module /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n	[/New]\n[/If]',NULL,NULL,'Published','Property_Module'),('Add_Unadded_Properties','2016-01-15 13:47:28',600,'Add Unadded Properties',8,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Execute link -->\n	[Link to Execute /]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Add Default Properties -->\n	<!-- TODO: Convert to a better way to get properties -->\n	<!-- TODO: What about reverse properties? -->\n	[New [Current_Action.Target.List.Alias no_wrap /]]\n		<!-- Only add properties that are not advanced -->\n		[This.Property where not Advanced]\n			<!-- Disambiguate -->\n			[Set Current_Property to This /]\n			\n			<!-- Check if property is one of the defaults -->\n			<!-- Check if property already added -->\n			[Current_Action.Target.Child_Property_Item where Alias is Current_Property.Alias]\n				[Else]\n					<!-- Add Property -->\n					[Add Current_Property to Current_Action.Target.Child_Property_Item /]\n					\n					<!-- Set default visibility -->\n					<!-- TODO this can be done better when Add executes its content within an attachment -->\n					[Current_Action.Target.Child_Property_Item where Alias is Current_Property.Alias]\n						<!-- Disambiguate -->\n						[Set Current_Child_Property_Item to This.Attachment /]\n						\n						<!-- Check if the property is one of the default visible ones -->\n						<!-- TODO Better way to specify default properties? -->\n						[Set Current_Child_Property_Item.Visible to false /]\n						[If\n							Current_Property.Name is \"Name\" or\n							Current_Property.Name is \"Date\" or\n							Current_Property.Name is \"Start Date\" or\n							Current_Property.Name is \"Start Time\" or\n							Current_Property.Name is \"Location\" or\n							Current_Property.Name is \"Picture\" or\n							Current_Property.Name is \"Image\" or\n							Current_Property.Name is \"Cover Image\"\n						]\n							[Then]\n								<!-- Only show properties that are simple -->\n								[If Current_Property.Value_Type is simple type]\n									<!-- For default properties, make them visible -->\n									[Set Current_Child_Property_Item.Visible to true /]\n								[/If]\n							[/Then]\n						[/If]\n						\n						[Save Current_Child_Property_Item /]\n					[/Current_Action.Target.Child_Property_Item]\n				[/Else]\n			[/Current_Action.Target.Child_Property_Item]\n		[/This.Property]\n	[/New]\n[/Admin]',NULL,NULL,'Published','Data_Module'),('Change_Data','2016-01-15 13:47:28',601,'Change Data',9,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin only -->\n	\n	<div class=\"Window Change_Data\">\n		<div class=\"Close_Link\">\n			<a href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n		</div>\n		\n		<!-- TODO: Hack to add to refreshing -->\n		[Current_Action.Target.Specific_Item][/Current_Action.Target.Specific_Item]\n	\n		<!-- List Menu -->\n		<!-- DISABLED -->\n		[Ignore]\n			<div>\n				[Current_Action.Target.Action \"Change List\" /]\n			</div>\n		[/Ignore]\n	\n		<!-- List Filters -->\n		<!-- DISABLED -->\n		[Ignore]\n			[Current_Action.Target.List_Filter]\n				<!-- Edit List Filter -->\n				[This.Specific_Item]\n					<div>\n						[This.Action \"Change List Filter\" /]\n					</div>\n				[/This.Specific_Item]\n			[/Current_Action.Target.List_Filter]\n			[Current_Action.Target.Action \"Add List Filter\" /]\n		[/Ignore]\n	\n		<!-- Reset data module properties -->\n		[Current_Action.Target.Action \"Reset Properties\" as Execute /]\n	\n		<!-- Properties -->\n		<div class=\"Properties\">\n			[Current_Action.Target.Child_Property_Item]\n				<div class=\"Clearer\" style=\"margin-bottom: 3px;\">\n				\n					<!-- Show/Hide links -->\n					<div class=\"Left\" style=\"width: 60px; margin-right: 10px;\">\n						[If This.Attachment.Visible]\n							[Then]								\n								[Current_Action.Target.Action \"Make Property Invisible\" with Data_Module_Child_Property_Item = This.Attachment /]\n							[/Then]\n							[Else]\n								[Current_Action.Target.Action \"Make Property Visible\" with Data_Module_Child_Property_Item = This.Attachment /]\n							[/Else]\n						[/If]\n					</div>\n					\n					<!-- Property Name -->\n					<div class=\"Left\" style=\"padding-top: 5px;\">\n						[This.Name /]\n					</div>\n				</div>\n			[/Current_Action.Target.Child_Property_Item]\n		</div>\n	</div>\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Edited\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','<!-- Does nothing -->',NULL,NULL,'Published','Data_Module'),('Change_List','2016-01-15 13:47:28',602,'Change List',10,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- List Menu -->\n	[Type \"Type\" from Database]\n		[Input \"Menu\" with Name = \"List\", Value_Type = This, Value = Current_Action.Target.List, Execute_On_Change = \"True\" /]\n	[/Type]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Set Picture -->\n	[Set Current_Action.Target.List to Form.List /]\n	\n	<!-- Remove Properties -->\n	[Current_Action.Target.Action \"Reset Properties\" as Execute /]\n	\n	<!-- Save Item -->\n	[Save Current_Action.Target /]\n[/Admin]',NULL,NULL,'Published','Data_Module'),('Delete_All_Properties','2016-01-15 13:47:28',603,'Delete All Properties',11,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Link to execute -->\n	[Link to Execute /]\n[/Admin]','[Admin]\n	<!-- Admin Only... -->\n	\n	<!-- Remove properties -->\n	[Current_Action.Target.Child_Property_Item]\n		<!-- Remove property -->\n		[Remove This.Attachment /]\n	[/Current_Action.Target.Child_Property_Item]\n[/Admin]',NULL,NULL,'Published','Data_Module'),('Delete','2016-01-15 13:47:28',604,'Delete',12,NULL,0,'2016-01-15 13:47:29','Design','Admin','[Admin]\n	<div class=\"Window\">\n		<!-- Execute Link -->\n		[Link to Execute /]\n	\n		<!-- Close Window on Save -->\n		[External_Script]\n			Jelly.Handlers.Register_Handler(\n			{\n				\"Element\": document.get