ray(\n			\'Email_Address\' => \'services@better.space\'\n		);\n\n		// Add sender\n		$Reply_To = array(\n			\'Name\' => \'[Format as \"PHP Single Quoted String\"][Form.Sender.Name no_wrap /][/Format]\',\n			\'Email_Address\' => \'[Format as \"PHP Single Quoted String\"][Form.Sender.Email_Address no_wrap /][/Format]\'\n		);\n		\n		$To = array();\n		// Add recipient\n		[Set Current_Action.Variables.Recipient_Count = 0 /]\n		[Current_Action.Target.Member where Activated and Email_Address exists and Email_Address is not \"\" and Allow_Community_Members_To_Contact_Me and ID is not Current_Session.User.ID no_wrap]\n			[Set Current_Member to This/]\n			[If Form.Recipient_[Current_Member.ID no_wrap/]]\n				[Set Current_Action.Variables.Recipient_Count = [Math Current_Action.Variables.Recipient_Count + 1 /] /]\n				$To\\[\\]  = array(\n					\'Name\' => \'[Format as \"PHP Single Quoted String\"][Current_Member.Name no_wrap /][/Format]\',\n					\'Email_Address\' => \'[Format as \"PHP Single Quoted String\"][Current_Member.Email_Address no_wrap /][/Format]\'\n				);\n			[/If]\n		[/]\n\n		// Add sender\n		// TODO - add cc\n//		$CC = array(\n//			\'Name\' => \'[Format as \"PHP Single Quoted String\"][Form.Sender.Name no_wrap /][/Format]\',\n//			\'Email_Address\' => \'[Format as \"PHP Single Quoted String\"][Form.Sender.Email_Address no_wrap /][/Format]\'\n//		);\n\n		$To\\[\\] = array(\n			\'Name\' => \'[Format as \"PHP Single Quoted String\"][Form.Sender.Name no_wrap /][/Format]\',\n			\'Email_Address\' => \'[Format as \"PHP Single Quoted String\"][Form.Sender.Email_Address no_wrap /][/Format]\'\n		);\n\n		// Send message\n		try \n		{\n			// TODO - add cc\n			$Email_Response = sendGridEmail($Subject, $Body, $From, $To, $Reply_To, $BCC);\n			if($Email_Response\\[\'error\'\\])\n			{\n				die(\'Error: Message could be sent. \'. $Email_Response\\[\'error\'\\]);\n			}\n		}\n		catch (exception $Exception) \n		{\n			echo (\'Error: Message could be sent.\');\n			traverse($Exception->getMessage());\n			die();\n		}\n	[/PHP]\n	\n	[External_Script]\n		[If Globals.Allow_Tracking]\n			if (mixpanel)\n			{\n				mixpanel.track(\n						\"Message Sent\", \n						{	\n							\"Via\": \"Email\",\n							\"Sender\": \"[Format as \"Javascript String\"][Form.Sender.Name no_wrap/][/Format]\", \n							\"Recipient Team\": \"[Format as \"Javascript String\"][Current_Action.Target.Name no_wrap/][/Format]\",\n							\"Recipient Count\": [Current_Action.Variables.Recipient_Count no_wrap/],\n							\"Account\": \"[PHP]return $_SERVER\\[\"HTTP_HOST\"\\];[/PHP]\"\n						}\n					);\n			}\n		[/If]\n		[Block \"Email Sent Confirmation\"]\n			[Go to This into \"[Form.Calling_Namespace no_wrap/]\"/]\n		[/Block]\n	[/External_Script]\n[/If]',NULL,NULL,'Published','Team'),('Send_Text_to_Team','2016-01-15 13:47:28',670,'Send Text to Team',78,NULL,0,'2016-01-15 13:47:30','Accounts','Admin','[Set Current_Action.Variables.Visible = False/]\n[If Current_Action.Target.Allow_Any_Member_To_Text_Entire_Team]	\n	[Then][Member][Set Current_Action.Variables.Visible = True/][/Member][/Then]\n	[Else][Admin][Set Current_Action.Variables.Visible = True/][/Admin][/Else]\n[/If]\n\n[If Current_Action.Variables.Visible]\n	<span class=\"Send_Text_Window\">	\n		<!-- Close Button -->\n		<a class=\"Close_Button\" href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window();return false;\">\n			<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\">\n		</a>\n\n		<span class=\"Header\">\n			Send Text to [Action.Target.Name /] Team\n		</span>	\n	\n		[Action as Result/]	\n		\n		[Set Action.Variables.Team_Has_Valid_Recipients to 	False/]\n		[1 Action.Target.Member where Activated and Phone_Number exists and Phone_Number is not \"\" and Allow_Community_Members_To_Contact_Me and ID is not Current_Session.User.ID]\n			[Set Action.Variables.Team_Has_Valid_Recipients to 	True/]\n		[/]\n\n		[If Action.Variables.Team_Has_Valid_Recipients]\n			[Then]\n				<span class=\"Recipients\">\n					<label>To:</label>\n		\n					<div class=\"Input_Wrapper Big_List Grid Tiny\">\n						[Action.Target.Member where Activated and Phone_Number exists and Phone_Number is not \"\" and Allow_Community_Members_To_Contact_Me and ID is not Current_Session.User.ID iterator_classes \"Big_List_Items\" item_classes \"Big_List_Item Hover\"]\n							[Set Current_Member to this /]\n\n							<span id=\"[Current_Action.Namespace no_wrap /]_Recipient_[Current_Member.ID no_wrap /]_Value_Input_Finder\">\n								[Input \"Hidden\" with Name = \"Recipient [Current_Member.ID no_wrap /]\", Value = \"1\"/]\n							</span>\n\n							<!-- Details -->\n							<a class=\"Big_List_Item_Details Selected\" id=\"[Current_Action.Namespace no_wrap /]_Recipient_[Current_Member.ID no_wrap /]\" href=\"#\" onclick=\"[Format as \"Single Line Code\"]\n									Jelly.jQuery(this).toggleClass(\'Selected\');\n									Jelly.jQuery(\'#\' + this.id + \'_Value_Input_Finder input\').val(Jelly.jQuery(this).hasClass(\'Selected\') & 1);\n									return false;\n								[/Format]\">\n			\n								<!-- Cover Image -->\n								<span class=\"Big_List_Item_Cover\">\n									[Current_Member as Cover /]\n								</span>\n	\n								<span class=\"Big_List_Item_Name\">\n									[Current_Member.Name /]\n								</span>\n	\n								<span class=\"Big_List_Item_Check\">√</span>\n							</a>\n						[/]		\n					</div>		\n				</span>\n		\n				<span class=\"Author\">\n					<!-- Set sender to current user, or site owner as fallback --> \n					[If Current_Session.User]\n						[Then][Set Current_Action.Variables.Sender = Current_Session.User/][/Then]\n						[Else][1 Site from Database][Set Current_Action.Variables.Sender to Site.Owner/][/1][/Else]\n					[/If]		\n					[Input \"Hidden\" with Name = \"Sender\", Value = Current_Action.Variables.Sender/]\n				</span>\n		\n				<span class=\"Message\">\n					<label>Message:</label>\n					[Input \"Text Area\" with Name = \"Body\"/]\n		\n					[Set Current_Action.Variables.Signature to \"&#x1f4e8; Sent from [Site.Name/]! Respond to [Current_Action.Variables.Sender.First_Name/] @ [Current_Action.Variables.Sender.Phone_Number/]. (Don\'t reply)\"/] \n					[Input \"Hidden\" with Name = \"Signature\", Value = Current_Action.Variables.Signature/]\n					<div class=\"Signature\">[Current_Action.Variables.Signature/]</div>\n				</span>\n	\n				<span class=\"Execute\">\n					<!-- Execute link -->\n					[Link to Execute]Send[/Link]\n				</span>\n			[/Then]\n			[Else]\n				<div style=\"padding:25px;\">\n					There are no members of this team that you can contact this way.\n				</div>\n				<span class=\"Execute\">\n					<!-- close link -->\n					<a href=\"javascript:Jelly.Interface.Close_Top_Window();\" class=\"Button\">Close</a>\n				</span>\n			[/Else]\n		[/If]\n	</span>\n[/If]','[If not Form.Body]\n	[Show Error for \"Body\"]Please enter a message[/Show]\n[/If]\n\n[If not Form.Sender.First_Name or Form.Sender.Phone_Number does not exist or Form.Sender.Phone_Number = \"\"]\n	[Show Error]Complete your profile with your name and phone number to send team texts.[/Show]\n[/If]\n\n[Set Current_Action.Variables.Traversed = False/]\n[Current_Action.Target.Member where Activated and Phone_Number exists and Phone_Number is not \"\" and Allow_Community_Members_To_Contact_Me and ID is not Current_Session.User.ID]\n	[Set Current_Member to This/]\n	[If Form.Recipient_[Current_Member.ID no_wrap/]][Set Current_Action.Variables.Traversed = True/][/If]\n[/]\n[If not Current_Action.Variables.Traversed]\n	[Show Error]Please select at least one recipient.[/Show Error]\n[/If]\n\n[If not Form.Error]\n	<!-- Send message --> \n	[PHP]	\n		// this line loads the library \n		require_once(\'jelly/php/libraries/twilio/twilio.php\'); \n	\n		// TODO - generalize configuration process for API keys etc\n		$Configuration = &Get_Environment_Variables();\n	\n		$Account_SID = $Configuration\\[\'API_Keys\'\\]\\[\'Twilio\'\\]\\[\'Account_SID\'\\];\n		$Auth_Token = $Configuration\\[\'API_Keys\'\\]\\[\'Twilio\'\\]\\[\'Auth_Token\'\\];\n		$From = $Configuration\\[\'API_Keys\'\\]\\[\'Twilio\'\\]\\[\'From\'\\];\n		$Client = new Services_Twilio($Account_SID, $Auth_Token); \n	\n		[Set Current_Action.Variables.Recipient_Count = 0 /]\n		[Current_Action.Target.Member where Activated and Phone_Number exists and Phone_Number is not \"\" and Allow_Community_Members_To_Contact_Me and ID is not Current_Session.User.ID no_wrap]\n			[Set Current_Member to This/]\n			[If Form.Recipient_[Current_Member.ID no_wrap/]]\n				[Set Current_Action.Variables.Recipient_Count = [Math Current_Action.Variables.Recipient_Count + 1 /] /]\n				$Client->account->messages->create(array( \n					\'To\' => \'[Current_Member.Phone_Number no_wrap/]\', \n					\'From\' => $From,\n					\'Body\' => html_entity_decode(\"[Form.Body no_wrap/]\" . \" \" . \"[Form.Signature no_wrap/]\")\n				));\n			[/If]\n		[/Current_Action]\n	[/PHP]	\n\n	[External_Script]\n		[If Globals.Allow_Tracking]\n			if (mixpanel)\n			{\n				mixpanel.track(\n						\"Message Sent\", \n						{	\n							\"Via\": \"Text\",\n							\"Sender\": \"[Format as \"Javascript String\"][Form.Sender.Name no_wrap/][/Format]\", \n							\"Recipient Team\": \"[Format as \"Javascript String\"][Current_Action.Target.Name no_wrap/][/Format]\",\n							\"Recipient Count\": [Current_Action.Variables.Recipient_Count no_wrap/],\n							\"Account\": \"[PHP]return $_SERVER\\[\"HTTP_HOST\"\\];[/PHP]\"\n						}\n					);\n			}\n		[/If]\n		[Block \"Text Sent Confirmation\"]\n			[Go to This into \"[Form.Calling_Namespace no_wrap/]\"/]\n		[/Block]\n	[/External_Script]\n[/If]',NULL,NULL,'Published','Team'),('Add_Property','2016-01-15 13:47:28',671,'Add Property',79,NULL,0,'2016-01-15 13:47:30','Core','Admin','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Execute link for manual implementations -->\n	[Input \"Text Field\" with Name = \"Property Name\" /]\n	\n	[Link to Execute]Create[/Link]\n[/Admin]','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Create property -->\n	<!-- TODO set type not as alias? -->\n	[New Property with Name = Form.Property_Name, Type = Current_Action.Target.Alias]\n		<!-- Save item -->\n		[Save This /]\n	[/New]\n		\n	<!-- Trigger Edited Event -->\n	[External_Script]\n		<!-- TODO: Calling handlers this many levels down doesn\'t seem to be working -->\n		Jelly.Interface.Close_Top_Window();				\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Type'),('Add_Question','2016-01-15 13:47:28',672,'Add Question',80,NULL,0,'2016-01-15 13:47:30','Core','Admin','<!-- Admin only -->\n[Admin]\n	<span class=\"Input_Row\">\n		<span class=\"Description\">\n			Question:\n		</span>\n		<span class=\"Input\">\n			[Input \"Text Field\" with Name = \"Question\"/]\n		</span>\n	</span>\n	\n	<span class=\"Input_Row\">\n		<span class=\"Description\">\n			Type of Answer:\n		</span>\n		<span class=\"Input\">\n			[Input \"Value Type Menu\" with Name = \"Answer Type\"/]\n		</span>\n	</span>\n	\n	<span class=\"Input_Row\">\n		<span class=\"Link\">\n			[Link to Execute]Add Question[/Link]\n		</span>\n	</span>	\n[/Admin]','<!-- Store action namespace --> \n[Set Current_Action_Namespace to This.Namespace/]\n\n<!-- Admin only -->\n[Admin]\n	<!-- Disambiguate -->\n	[Set Current_Type to Action.Target /]\n	\n	<!-- Get value type -->\n	[Form.Values.Answer_Type]\n		<!-- Disambiguate -->\n		[Set Current_Value_Type to This /]\n		\n		<!-- Create property -->\n		[New Property]\n			<!-- Disambiguate -->\n			[Set New_Property to This /]\n			\n			<!-- Set property type -->\n			[Set New_Property.Type to Current_Type.Alias /]\n			\n			<!-- Set property value type -->\n			[Set New_Property.Value_Type to Current_Value_Type.Alias /]\n			\n			<!-- Auto set name (lol) --> \n			[If Form.Question exists and Form.Question is not \"\"]\n				[Then]	\n					[Set New_Property.Name to Form.Question/]\n				[/Then]\n				[Else]\n					[If Current_Value_Type is simple type]\n						[Then]\n							<!-- Set default property name -->\n							[Set New_Property.Name to Form.Question/]\n	\n							<!-- Special case value types -->\n							[If Current_Value_Type.Alias is \"Long_Text\"]\n								[Current_Item.Property where Name = \"Description\"]\n									[Else][Set New_Property.Name to \"Description\"/][/Else]\n									[Then][Current_Item.Target.Property where Name = \"Details\"][Else][Set New_Property.Name to \"Details\"/][/Else][/Current_Action.Target.Property][/Then]\n								[/Current_Action.Target.Property]\n							[/If]\n							[If Current_Value_Type.Alias is \"Number\"]\n								[Current_Item.Property where Name = \"Amount\"]\n									[Else][Set New_Property.Name to \"Amount\" /][/Else]\n								[/Current_Action.Target.Property]\n							[/If]\n							[If Current_Value_Type.Alias is \"Date_Time\"]\n								[Current_Item.Property where Name = \"Start Time\"]\n									[Else][Set New_Property.Name to \"Start Time\"/][/Else]\n									[Then][Current_Item.Target.Property where Name = \"End Time\"][Else][Set New_Property.Name to \"End Time\"/][/Else][/Current_Action.Target.Property][/Then]\n								[/Current_Action.Target.Property]\n							[/If]\n						[/Then]\n						[Else]\n							<!-- Set property name to value type\'s name -->\n							[Set New_Property.Name to Current_Value_Type.Name /]\n							[Set New_Property.Plural_Name to Current_Value_Type.Plural_Name /]\n			\n							<!-- Special case value types -->\n							[If Current_Value_Type.Alias is \"Location\"]\n								[Current_Item.Property where Name = \"Location\"]\n									[Else][Set New_Property.Name to \"Location\"/][/Else]\n								[/Current_Action.Target.Property]\n							[/If]\n						[/Else]\n					[/If]\n				[/Else]\n			[/If]\n			\n			[If Current_Value_Type is simple type]\n				[Else]		\n					<!-- Set relation to multiple -->\n					[Set New_Property.Relation to \"Many-To-Many\"/]\n	\n					<!-- Special case value types -->\n					[If Current_Value_Type.Alias is \"Location\"]\n						[Set New_Property.Relation to \"Many-To-One\"/]\n					[/If]\n				[/Else]\n			[/If]\n			\n			<!-- Save new property -->\n			[Save New_Property No_Refresh/]\n			\n			<!-- Scripts --> \n			[External_Script]\n				Jelly.Handlers.Call_Handler_For_Target(\n						{\n							\'Event\': \'Created\',\n							\'Item\': [New_Property.ID No_Wrap/],\n							\'Target\': document.getElementById(\'[Current_Action_Namespace No_Wrap /]\')\n						}\n					);\n			[/External_Script]\n		[/New]\n	[/Type]\n[/Admin]',NULL,NULL,'Published','Type'),('Create_Child_List','2016-01-15 13:47:28',673,'Create Child List',81,NULL,0,'2016-01-15 13:47:30','Core','Admin','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Execute link for manual implementations -->\n	[Input \"Text Field\" with Name = \"Type Name\" /]\n	[Link to Execute]Create[/Link]\n[/Admin]','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Create type -->\n	<!-- TODO set parent type not as alias? -->\n	[New Type with Name = Form.Type_Name, Parent_Type = Current_Action.Target.Alias, Package = \"Local\", Starred = True]\n		<!-- Save item -->\n		[Save This /]\n	\n		<!-- Go to new item -->\n		<!-- TODO Clean up -->\n		[Go to \"/type/[This.Alias no_wrap /]/manage\" /]\n	[/New]\n		\n	<!-- Trigger Edited Event -->\n	[External_Script]\n		<!-- TODO: Calling handlers this many levels down doesn\'t seem to be working -->\n		Jelly.Interface.Close_Top_Window();				\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Type'),('Create_Item','2016-01-15 13:47:28',674,'Create Item',82,NULL,0,'2016-01-15 13:47:30','Core','Admin','[Admin]\n	<!-- Admin Only -->\n	\n	<div class=\"Window Create_Item\">\n		<a href=\"#\" class=\"Close_Button\" onclick=\"Jelly.Interface.Close_Top_Window(); return false;\"><img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\"></a>\n		\n		<span class=\"Header\">\n			Name your [Current_Action.Target.Name /]\n		</span>\n		\n		<span class=\"Section\">\n			<span class=\"Input\">\n				[Input \"Text Field\" with Name = \"Name\" /]\n			</span>\n		</span>\n		\n		<span class=\"Execute\">\n			[Link to Execute]Create[/Link]\n		</span>\n	</div>	\n	\n	<!-- Close Window on Save -->\n	[External_Script]\n		Jelly.Handlers.Register_Handler(\n		{\n			\"Element\": document.getElementById(\"[Current_Action_Namespace No_Wrap /]\"),\n			\"Event\": \"Added\",\n			\"Code\": function (Parameters)\n			{\n				Parameters\\[\"Event\"\\] = \"Dismiss\";\n				Jelly.Handlers.Call_Handler_For_Target(Parameters);\n			}\n		});\n	[/External_Script]\n[/Admin]','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Disambiguate -->\n	[Set Current_Type to Current_Action.Target /]\n	\n	<!-- Create item -->\n	[New [Current_Type.Alias no_wrap /]]\n		<!-- Disambiguate -->\n		[Set New_Item to This /]\n		\n		<!-- Name -->\n		[Set New_Item.Name to Form.Name /]\n		\n		<!-- For new users, auto-set first and last name -->\n		[If Current_Type.Alias is \"User\"]\n			[If New_Item.Name]\n				[If [PHP]return (int)(strpos(\'[Format as \"PHP Single Quoted String\"][New_Item.Name no_wrap /][/Format]\', \' \') !== false);[/PHP]]\n					[Then]\n						[Set New_Item.First_Name to \"[Format as \"Jelly Attribute\"][PHP]return substr(\'[Format as \"PHP Single Quoted String\"][New_Item.Name no_wrap /][/Format]\', 0, strpos(\'[Format as \"PHP Single Quoted String\"][New_Item.Name no_wrap /][/Format]\', \' \'));[/PHP][/Format]\" /]\n						[Set New_Item.Last_Name to \"[Format as \"Jelly Attribute\"][PHP]return substr(\'[Format as \"PHP Single Quoted String\"][New_Item.Name no_wrap /][/Format]\', strpos(\'[Format as \"PHP Single Quoted String\"][New_Item.Name no_wrap /][/Format]\', \' \') + 1);[/PHP][/Format]\" /]\n					[/Then]\n					[Else]\n						[Set New_Item.First_Name to New_Item.Name /]\n					[/Else]\n				[/If]\n			[/If]\n		[/If]\n		\n		<!-- Save item -->\n		[Save New_Item /]\n		\n		<!-- If creating a page, set up default content modules -->\n		[If Current_Type.Alias is \"Page\"]\n			<!-- Create a sample text module -->\n			[New Text_Module]\n				<!-- Disambiguate -->\n				[Set New_Text_Module to this /]\n				\n				<!-- Set sample body -->\n				[Set New_Text_Module.Body to \"Some new content for your new page.\" /]\n				\n				<!-- Save the new text module -->\n				[Save New_Text_Module no_refresh /]\n				\n				<!-- Add the new text module to the page -->\n				[Add New_Text_Module to New_Item.Content_Module /]\n			[/New]\n		[/If]\n\n		<!-- Add types/pages/dashboards/folders to navigation root -->\n		[If This.Type.Alias is \"Type\" or This.Type.Alias is \"Page\" or This.Type.Alias is \"Dashboard\" or This.Type.Alias is \"Folder\"]\n			[Folder \"Root\" from database]\n				[Set Current_Folder to this /]\n				[Add New_Item to Folder.Child_Item /]\n			[/Folder]\n		[/If]\n	\n		<!-- Go to new item for most items-->\n		<!-- TODO - this should be a setting, not a heuristic -->\n		[If Current_Type.Package.Name is not \"Local\"]\n			[Go to New_Item by ID /]\n		[/If]\n	[/New]\n		\n	<!-- Trigger Edited Event -->\n	[External_Script]\n		Jelly.Handlers.Call_Handler_For_Target(\n			{\n				\"Event\": \"Added\",\n				\"Target\": document.getElementById(\"[Form.Metadata_Namespace No_Wrap /]\")\n			});\n	[/External_Script]\n[/Admin]',NULL,NULL,'Published','Type'),('Publish','2016-01-15 13:47:28',675,'Publish',83,NULL,0,'2016-01-15 13:47:30','Core','Admin','[Link to Execute with Class = \"Better_Publish\"]Publish[/Link]\n<span style=\"display:none;\">\n	[Action as \"Loading\"/]\n</span>','[Admin]\n	<!-- Admin only -->\n	[Set Current_Action to This/]\n	[Set Current_Type to Current_Action.Target /]\n\n	[If Current_Type.Plural_Name]\n		[Then]\n			[Set Current_Action.Variables.Page_Name to Current_Type.Plural_Name/]\n		[/Then]\n		[Else]\n			[Set Current_Action.Variables.Page_Name to Current_Type.Name/]\n		[/Else]\n	[/If]\n	\n	<!-- Create page for published list -->\n	[New Page with Name = Current_Action.Variables.Page_Name, Visible = 1]\n		<!-- Disambiguate -->\n		[Set Current_Page to This/]\n		[Save Current_Page /]\n		\n		<!-- Create text module for list name -->\n		[New Text_Module]\n			<!-- Disambiguate -->\n			[Set New_Text_Module to This /]\n			\n			<!-- Set new text module body to type name -->\n			[Set New_Text_Module.Body to Current_Type.Name /]\n			\n			<!-- Specify new text module as heading size -->\n			[Set New_Text_Module.Size to \"Large\" /]\n			\n			<!-- Save new text module -->\n			[Save New_Text_Module /]\n			\n			<!-- Add new text module to page -->\n			[Add New_Text_Module to Current_Page.Content_Module /]\n			\n			<!-- Save page -->\n			[Save Current_Page /]\n		[/New]\n		\n		<!-- Create text module for content text -->\n		[New Text_Module]\n			<!-- Disambiguate -->\n			[Set New_Text_Module to This /]\n			\n			<!-- Set new text module body -->\n			[Set New_Text_Module.Body to \"All \" + Current_Type.Name /]\n			\n			<!-- Save new text module -->\n			[Save New_Text_Module /]\n			\n			<!-- Add new text module to page -->\n			[Add New_Text_Module to Current_Page.Content_Module /]\n			\n			<!-- Save page -->\n			[Save Current_Page /]\n		[/New]\n		\n		<!-- Create data module for list items -->\n		[New Data_Module]\n			<!-- Disambiguate -->\n			[Set Current_New_Item to This /]\n\n			<!-- Set data module\'s list to Presentation -->\n			[Set Current_New_Item.List to Current_Type/]\n			\n			<!-- Display as grid -->\n			[Set Current_New_Item.Display to \"Grid\" /]\n			\n			<!-- Save data module -->\n			[Save Current_New_Item /]\n			\n			<!-- Reset presentation\'s properties -->\n			[Current_New_Item.Action \"Reset Properties\" as Execute/]\n			\n			<!-- Add data module to current page -->\n			[Add Current_New_Item to Current_Page.Content_Module /]\n			\n			<!-- Save page -->\n			[Save Current_Page /]\n			\n			<!-- Save data module -->\n			[Save Current_New_Item /]\n			\n			[External_Script]\n				// Hack \n				window.setTimeout(function(){\n						Jelly.Handlers.Handle_Link_From_Click(null, {\n								\"Namespace\":\"Jelly_Globals_1_Page_New\",\n								\"URL\":\"\\/page\\/[Format as \"Lowercase\"][Current_Page.Alias no_wrap /][/Format]\"\n							});\n						Jelly.Interface.Hide_Inspector();\n					}, 2500);	\n			[/External_Script]\n		[/New]\n	[/New Page]\n[/Admin]',NULL,NULL,'Published','Type'),('Refresh_Google_Sheet','2016-01-15 13:47:28',676,'Refresh Google Sheet',84,NULL,0,'2016-01-15 13:47:30','Core','Admin','Importing items...\n\n<!-- Auto-execute the action -->\n[External_Script]\n	Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': document.getElementById(\'[This.Namespace no_wrap /]\')});\n[/External_Script]\n\n[Action as \"Loading\"/]','<!-- Disambiguate -->\n[Set Current_Type to Current_Action.Target /]\n\n<!-- Delete existing items of the current type -->\n[[Current_Type.Alias no_wrap /] from database]\n	[Remove this /]\n[/[Current_Type.Alias no_wrap /]]\n\n[PHP]\n	$Type_ID = &New_String(\'[Format as \"PHP Single Quoted String\"][Current_Type.ID no_wrap /][/Format]\');\n	$URL = &New_String(\'[Format as \"PHP Single Quoted String\"][Current_Type.Remote_URL no_wrap /][/Format]\');\n	\n	// Check if the file is coming from Google Docs\n	if (strpos($URL, \'docs.google.com/spreadsheets\') !== false)\n	{\n		// Generate new URL by finding the document ID, in case they didn\'t pass in the correct published CSV URL\n		$Request_Document_ID = substr($URL, strpos($URL, \'/d/\') + strlen(\'/d/\'));\n		$Request_Document_ID = substr($Request_Document_ID, 0, strpos($Request_Document_ID, \'/\'));\n		$CSV_Request_URL = &New_String(\'https://docs.google.com/spreadsheets/d/\' . $Request_Document_ID . \'/pub?output=csv\');\n	}\n	else\n		$CSV_Request_URL = &$URL;\n	\n	// Get Type Item\n	$Type_Item_Command_String = &New_String(\'1 Type from Database where ID is \"\' . $Type_ID . \'\" as reference\');\n	$Type_Item_Result = Process_Command_String($Database, $Type_Item_Command_String, $Memory_Stack_Reference);\n	$Type_Item = $Type_Item_Result\\[\'Chunks\'\\]\\[0\\]\\[\'Item\'\\];\n				\n	// TODO plural types seem to get properties from existing types with same single name\n	\n	// TODO improve value mapping (use heuristics to check for repeated values that would indicate a dropdown list, booleans, dates, numbers, etc)\n	\n	// Get type\n	$Type_Name = &New_String(\'[Format as \"PHP Single Quoted String\"][Current_Type.Name no_wrap /][/Format]\');\n	$Type_Alias = &New_String(\'[Format as \"PHP Single Quoted String\"][Current_Type.Alias no_wrap /][/Format]\');\n	$Cached_Type = &Get_Cached_Type($Database, $Type_Alias);\n	\n	// Fetch URL again to get header\n	$Handle = fopen($CSV_Request_URL.\'&\'.mt_rand(), \"r\");\n	if (!$Handle)\n		return \"Error: could not open the connection.\";\n	\n	// Fetch the header data\n	$Header_Data = fgetcsv($Handle, null, \",\");\n	if ($Header_Data === FALSE)\n		die(\"Error reading CSV header.\");\n	\n	// Read the remaining data\n	$Lines = &New_Array();\n	while (($Line_Data = fgetcsv($Handle, null, \",\")) !== FALSE)\n		$Lines\\[\\] = $Line_Data;\n	\n	// Create Properties\n	$Header_Property = array();\n	$Found_Name_Column = false;\n	foreach ($Header_Data as $Header_Property_Index => &$Header_Property_Name)\n	{\n		// Create the property unless an existing property can be used\n		$Create_Property = true;\n		\n		// Check if a property with that name already exists...\n		\n		// Use the header as the starting property name\n		$Property_Base_Name = &$Header_Property_Name;\n		\n		// Turn number property names into strings\n		// TODO support number property names\n		if (is_numeric($Property_Base_Name))\n			$Property_Base_Name = &New_String(\'Property \' . $Property_Base_Name);\n		\n		// Check if property name is blank\n		if (!$Property_Base_Name)\n			$Property_Base_Name = \'New Property\';\n		\n		// Check if this is a Name column\n		if ($Property_Base_Name == \'Name\')\n			$Found_Name_Column = true;\n		\n		// Search for existing properties in all parent types\n		$Property_Name = &$Property_Base_Name;\n		$Property_Index = 1;\n		while (true)\n		{\n			$Recursive_Type = &$Cached_Type;\n			$Found_Property = false;\n			while ($Recursive_Type)\n			{\n				// TODO sanitize?\n				\n				// Get type alias\n				$Recursive_Type_Alias = &$Recursive_Type\\[\'Alias\'\\];\n				\n				// Check if property exists for type\n				$Check_Property_Command_String = &New_String(\'Property from database where Type is \"\' . Jelly_Format($Recursive_Type_Alias, \'jelly attribute\') . \'\" and Name is \"\' . Jelly_Format($Property_Name, \'jelly attribute\') . \'\" as reference\');\n				$Check_Property_Result = Process_Command_String($Database, $Check_Property_Command_String, $Memory_Stack_Reference);\n				$Check_Property_Item = $Check_Property_Result\\[\'Chunks\'\\]\\[0\\]\\[\'Item\'\\];\n				\n				// If property exists, stop searching\n				if (!$Check_Property_Item\\[\'End_Of_Results\'\\])\n				{\n					$Found_Property = true;\n					break;\n				}\n				\n				// Move to parent type\n				$Recursive_Type = &$Recursive_Type\\[\'Cached_Parent_Type\'\\];\n			}\n			\n			// If a property already exists, check if it can be used\n			if ($Found_Property)\n			{\n				switch (strtolower($Property_Name))\n				{\n					// TODO automate this?\n					case \'id\':\n					case \'created\':\n					case \'modified\':\n					case \'alias\':\n					case \'package\':\n					case \'viewable_by\':\n					case \'created_by\':\n					case \'modified_by\':\n					case \'status\':\n					case \'order\':\n					{\n						// If the property is metadata, generate a new unique property name\n						$Property_Index++;\n						$Property_Name = &New_String($Property_Base_Name . \' \' . $Property_Index);\n						break;\n					}\n					case \'name\':\n					default:\n					{\n						// Re-use existing properties that are not metadata\n						$Property_Alias = &$Check_Property_Item\\[\'Data\'\\]\\[\'Alias\'\\];\n						$Cached_Property = &$Cached_Type\\[\'Cached_Property_Lookup\'\\]\\[strtolower($Property_Alias)\\];\n						$Header_Property\\[\\] = &$Cached_Property;\n						\n						$Create_Property = false;\n						break 2;\n					}\n				}\n			}\n			else\n			{\n				// If property does not already exist, create it\n				break;\n			}\n		}\n		\n		// Check if a property should be created\n		if ($Create_Property)\n		{\n			// Create the property\n			$Property_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, \'Property\'));\n			\n			// Set the property\'s values\n			Set_Value($Property_Item, \'Type\', $Type_Alias);\n			Set_Value($Property_Item, \'Name\', Jelly_Format($Property_Name, \'jelly text block\'));\n			Set_Simple($Property_Item, \'Viewable_By\', \'Public\'	);\n			\n			switch (strtolower($Property_Base_Name))\n			{	\n				// Set value type based on header base name for special cases\n				case \'address\':\n					$Value_Type = \'Location\';\n					break;\n					\n				// Determine value type statistically\n				default:\n					// Count how many times values validate for different value types\n					$Matches_By_Type = array(\'Number\' => 0, \'Date\' => 0, \'Date_Time\' => 0, \'Time\' => 0, \'Boolean\' => 0, \'URL\' => 0, \'Video\' => 0);\n					$Value_String_Matches = array();\n					$Value_Count = 0;\n					foreach ($Lines as &$Line)\n					{\n						$Line_Data_Value = &$Line\\[$Header_Property_Index\\];\n				\n						if ($Line_Data_Value)\n						{\n							$Value_Count++;\n					\n							if (!isset($Value_String_Matches\\[$Line_Data_Value\\]))\n								$Value_String_Matches\\[$Line_Data_Value\\] = 0;\n							$Value_String_Matches\\[$Line_Data_Value\\]++;\n					\n							// Check if the value is numeric\n							if (is_numeric($Line_Data_Value))\n							{\n								// TODO Add checks for meaningful leading zero (i.e. not a zero followed by a decimal)\n								$Matches_By_Type\\[\'Number\'\\]++;\n							}\n					\n							// Ignore date handling for special values\n							// TODO not sure why these are parsed as valid dates\n							if (strtolower($Line_Data_Value) != \'y\')\n							{\n								// Check if the value has date/time data\n								$Date_Time_Value = strtotime($Line_Data_Value, 0);\n								if ($Date_Time_Value !== false)\n								{\n									$Matches_By_Type\\[\'Date_Time\'\\]++;\n							\n									// Check if the time portion is midnight (indicating a date value)\n									if (date(\'H:i:s\', $Date_Time_Value) == \'00:00:00\')\n										$Matches_By_Type\\[\'Date\'\\]++;\n							\n									// Check if year is unaffected by the value (indicating a time value)\n									if (date(\'Y-m-d\', $Date_Time_Value) == \'1970-01-01\')\n										$Matches_By_Type\\[\'Time\'\\]++;\n								}\n							}\n						\n							// Check if the value is a standard URL or validates with the http prefix\n							// TODO better URL checking\n							if (strpos($Line_Data_Value, \'http\') === 0 || (filter_var((\'http://\' . $Line_Data_Value), FILTER_VALIDATE_URL) !== false && strpos($Line_Data_Value, \'.\') !== false))\n							{\n								// TODO add checks for media types\n								$Matches_By_Type\\[\'URL\'\\]++;\n							}\n					\n							// Check if the value includes youtube\n							// TODO better video checking\n							if (strpos($Line_Data_Value, \'youtube\') !== false)\n							{\n								// TODO add checks for media types\n								$Matches_By_Type\\[\'Video\'\\]++;\n							}\n					\n							// Check if the value includes youtube\n							// TODO better video checking\n							if ((strpos($Line_Data_Value, \'.jpg\') !== false) || (strpos($Line_Data_Value, \'.jpeg\') !== false) || (strpos($Line_Data_Value, \'.gif\') !== false) || (strpos($Line_Data_Value, \'.png\') !== false))\n							{\n								// TODO add checks for media types\n								$Matches_By_Type\\[\'Picture\'\\]++;\n							}\n						}\n					}\n			\n					// Choose value type based on percentage matches of the data validation\n					if ($Value_Count > 0)\n					{\n						if ($Matches_By_Type\\[\'Date\'\\] / $Value_Count >= .75)\n							$Value_Type = &New_String(\'Date\');\n						elseif ($Matches_By_Type\\[\'Time\'\\] / $Value_Count >= .75)\n							$Value_Type = &New_String(\'Time\');\n						elseif ($Matches_By_Type\\[\'Date_Time\'\\] / $Value_Count >= .75)\n							$Value_Type = &New_String(\'Date_Time\');\n						else if ($Matches_By_Type\\[\'Number\'\\] / $Value_Count >= .75)\n							$Value_Type = &New_String(\'Number\');\n						else if ($Matches_By_Type\\[\'Video\'\\] / $Value_Count >= .75)\n							$Value_Type = &New_String(\'Video\');\n						else if ($Matches_By_Type\\[\'Picture\'\\] / $Value_Count >= .75)\n							$Value_Type = &New_String(\'Picture\');\n						else if ($Matches_By_Type\\[\'URL\'\\] / $Value_Count >= .75)\n							$Value_Type = &New_String(\'Website\');\n						else\n							$Value_Type = &New_String(\'Long_Text\');\n					}\n					else\n						$Value_Type = &New_String(\'Long_Text\');\n					break;\n			}\n			\n			// Set value type\n			Set_Value($Property_Item, \'Value_Type\', $Value_Type);\n			\n			// Save the property\n			Save_Item($Property_Item);\n			\n			// Fetch the cached property for the property that was just created\n			$Property_Alias = &$Property_Item\\[\'Data\'\\]\\[\'Alias\'\\];\n			$Cached_Property = &$Cached_Type\\[\'Cached_Property_Lookup\'\\]\\[strtolower($Property_Alias)\\];\n			$Header_Property\\[\\] = &$Cached_Property;\n		}\n	}\n	\n	// Continue fetching data to get items\n	$Item_Index = &New_Number(0);\n	foreach ($Lines as &$Line)\n	{\n		// Check if all values are blank\n		$All_Values_Are_Blank = true;\n		foreach ($Line as $Line_Data_Index => &$Line_Data_Value)\n		{\n			if ($Line_Data_Value)\n				$All_Values_Are_Blank = false;\n		}\n		\n		// Only create item if not all values are blank\n		if (!$All_Values_Are_Blank)\n		{\n			// Increment index of item\n			$Item_Index = &New_Number($Item_Index + 1);\n			\n			// Create a new item for each line\n			$Item = &Create_Memory_Item($Database, $Cached_Type);\n			\n			// Set each value for the item\n			foreach ($Line as $Line_Data_Index => &$Line_Data_Value)\n			{\n				// Fetch the mapped property alias\n				$Cached_Property = &$Header_Property\\[$Line_Data_Index\\];\n				$Property_Alias = &$Cached_Property\\[\'Alias\'\\];\n				\n				$Cached_Property_Value_Type = $Cached_Property\\[\'Cached_Value_Type\'\\];\n				$Cached_Property_Value_Type_Alias = $Cached_Property_Value_Type\\[\'Alias\'\\];\n				switch (strtolower($Cached_Property_Value_Type_Alias))\n				{\n					case \'text\':\n					case \'long_text\':\n					{\n						// Set text values by escaping it first\n						Set_Value($Item, $Property_Alias, Jelly_Format($Line_Data_Value, \'jelly text block\'));\n						break;\n					}\n					case \'number\':\n					{\n						// Revalidate the number (in case it was one of the outliers before)\n						if (is_numeric($Line_Data_Value))\n						{\n							// Set the value directly\n							Set_Value($Item, $Property_Alias, $Line_Data_Value);\n						}\n						break;\n					}\n					case \'date\':\n					case \'time\':\n					case \'date_time\':\n					{\n						// Revalidate the date (in case it was one of the outliers before)\n						if (strtotime($Line_Data_Value) !== false)\n						{\n							// Set the value directly\n							$Converted_Line_Data_Value = &New_Number(strtotime($Line_Data_Value));\n							Set_Value($Item, $Property_Alias, $Converted_Line_Data_Value);\n						}\n						break;\n					}\n					case \'picture\':\n					case \'video\':\n					case \'website\':\n					{\n						// Check if a value has been set\n						if ($Line_Data_Value)\n						{\n							// Revalidate the link and don\'t set if not a link\n							if (strpos($Line_Data_Value, \'http\') === 0 || (filter_var((\'http://\' . $Line_Data_Value), FILTER_VALIDATE_URL) !== false && strpos($Line_Data_Value, \'.\') !== false))\n							{\n								// For websites, prepend http prefix if needed\n								if (strtolower($Cached_Property_Value_Type_Alias) == \'website\')\n								{\n									if (strpos($Line_Data_Value, \'http\') === 0)\n									{\n									}\n									else if (filter_var((\'http://\' . $Line_Data_Value), FILTER_VALIDATE_URL) !== false && strpos($Line_Data_Value, \'.\') !== false)\n									{\n										// If adding the http prefix creates a valid URL, add the prefix to the value\n										$Line_Data_Value = &New_String(\'http://\' . $Line_Data_Value);\n									}\n								}\n								\n								// Create a picture/video item and set its path\n								$URL_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, $Cached_Property_Value_Type_Alias));\n								Set_Value($URL_Item, \'Path\', New_String(Jelly_Format($Line_Data_Value, \'jelly text block\')));\n								Save_Item($URL_Item);\n								\n								// Set the value to the new item\n								Set_Value($Item, $Property_Alias, $URL_Item);\n							}\n						}\n						\n						break;\n					}\n					\n					case \'location\':									\n						// Create location item\n						$Location_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, \'Location\'));\n						\n						// Clean row value \n						$Address_Value = &Jelly_Format($Line_Data_Value, \'jelly text block\');\n						\n						// Set location values...\n						\n						// Set original address\n						Set_Value($Location_Item, \'Original_Address\', $Address_Value);\n						\n						// Get geocoded values\n						$Geocoded_Address = &Geocode($Address_Value);\n						\n						// Set geocoded values\n						if ($Geocoded_Address !== false)\n						{\n							Set_Value($Location_Item, \'Latitude\', $Geocoded_Address\\[\'lat\'\\]);\n							Set_Value($Location_Item, \'Longitude\', $Geocoded_Address\\[\'lng\'\\]);\n							Set_Value($Location_Item, \'Name\', $Geocoded_Address\\[\'Address\'\\]);\n						}\n						\n						// Save location item\n						Save_Item($Location_Item);\n						\n						// Set location item to row value item\n						Set_Value($Item, $Property_Alias, $Location_Item);\n						break;\n						\n					default:\n					{\n						traverse($Cached_Property);\n						die(\"Could not match property value type.\");\n						break;\n					}\n				}\n			}\n			\n			// If no name column is specified, generate a name for it\n			if (!$Found_Name_Column)\n			{\n				// Use the type name and item index to generate the name\n				$Generated_Item_Name = &New_String($Type_Name . \' \' . \'Item\' . \' \' . $Item_Index);\n				\n				// Set the item\'s name\n				Set_Value($Item, \'Name\', Jelly_Format($Generated_Item_Name, \'jelly text block\'));\n			}\n			\n			// Save the new item\n			Save_Item($Item);\n		}\n	}\n	\n	// Close the CSV reading handle\n	fclose($Handle);\n[/PHP]\n\n<!-- Add to navigation root -->\n[Folder \"Root\" from database]\n	[Set Current_Folder to this /]\n	[Add Current_Type to Folder.Child_Item /]\n[/Folder]\n\n<!-- Add to toolbar root -->\n[Navigation \"Root\" from database]\n	[Set Current_Navigation to this /]\n	[Add Current_Type to Current_Navigation.Navigation_Item /]\n[/Folder]\n\n<!-- Set Star -->\n[Set Current_Type.Starred to true /]\n[Save Current_Type /]\n\n<!-- Go to type -->\n[Go to Current_Type by alias /]\n\n<!-- Close window -->\n[External_Script]\n	Jelly.Interface.Close_Top_Window();\n[/External_Script]',NULL,NULL,'Published','Type'),('Refresh_Item_Website_Properties','2016-01-15 13:47:28',677,'Refresh Item Website Properties',85,NULL,0,'2016-01-15 13:47:30','Core','Admin','<!-- TODO: Disabled to try out asynchronous website loading -->\n\nDownloading website previews (#[Path_Variables.Offset /])....\n\n[Input \"Hidden\" with Name = \"Offset\", Value = Path_Variables.Offset, Do_Not_Persist_Value = \"True\" /]\n\n<!-- Auto-execute the action -->\n[External_Script]\n	Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': document.getElementById(\'[This.Namespace no_wrap /]\')});\n[/External_Script]\n\n[Action as \"Loading\"/]','<!-- TODO: Disabled to try out asynchronous website loading -->\n\n<!-- Disambiguate -->\n[Set Current_Type to Current_Action.Target /]\n\n[Set Current_Action.Variables.More to false /]\n[If Form.Offset]\n	[Then]\n		[Set Current_Action.Variables.Offset to Form.Offset /]\n	[/Then]\n	[Else]\n		[Set Current_Action.Variables.Offset to 0 /]\n	[/Else]\n[/If]\n\n<!-- Get next item to refresh -->\n[1 [Current_Type.Alias no_wrap /] from database at [Current_Action.Variables.Offset no_wrap /]]\n	<!-- Refresh item\'s website properties -->\n	[This.Action \"Refresh Website Properties\" as Execute /]\n	\n	<!-- Store that there might be more items to refresh -->\n	[Set Current_Action.Variables.More to true /]\n	\n	<!-- Store the next offset -->\n	[Set Current_Action.Variables.Offset to Current_Action.Variables.Offset + 1 /]\n[/[Current_Type.Alias no_wrap /]]\n\n[If Current_Action.Variables.More]\n	[Then]\n		<!-- Refresh the websites for all loaded items -->\n		[Go to \"/Type/[Current_Type.Alias no_wrap /]/Refresh_Item_Website_Properties:Offset=[Current_Action.Variables.Offset no_wrap /]\" into \"Parent\" /]\n	[/Then]\n	[Else]\n		<!-- Publish list to new page -->\n		<!-- TODO only do first time or implement type publishing -->\n		[New Page]\n			<!-- Disambiguate -->\n			[Set Current_Page to This /]\n			\n			<!-- Set Page Name -->\n			[Set Current_Page.Name to Current_Type.Name /]\n			\n			<!-- Save Page -->\n			[Save Current_Page /]\n			\n			<!-- Create a Data Module -->\n			[New Data_Module]\n				<!-- Disambiguate -->\n				[Set Current_Data_Module to This /]\n				\n				<!-- Set the data module\'s list -->\n				[Set Current_Data_Module.List to Current_Type /]\n				\n				<!-- Save new data module -->\n				[Save Current_Data_Module /]\n\n				<!-- Reset data module properties -->\n				[Current_Data_Module.Action \"Reset Properties\" as Execute /]\n				\n				<!-- Add data module to the page -->\n				[Add Current_Data_Module to Current_Page.Content_Module /]\n			[/New]\n			\n			<!-- Go to the new page -->\n			[Go to Current_Page by alias /]\n		[/New]\n\n		<!-- Trigger Edited Event -->\n		[External_Script]\n			Jelly.Interface.Close_Top_Window();\n		[/External_Script]\n	[/Else]\n[/If]',NULL,NULL,'Published','Type'),('Set_Cover_Property_Item','2016-01-15 13:47:28',678,'Set Cover Property Item',86,NULL,0,'2016-01-15 13:47:30','Core','Admin','[Admin]\n	<!-- Admin Only -->\n	[Link to Execute /]\n[/Admin]','<!-- TODO: Reverse -->\n\n<!-- Disambiguate -->\n[Set Current_Type to This.Target /]\n\n<!-- Check if a cover property has already been set -->\n<!-- TODO better way to figure out when to run this? -->\n[If Current_Type.Cover_Property_Item does not exist]\n	<!-- Check if the type has a property with a cover -->\n	<!-- TODO check Pictures first, etc. -->\n	[New [Current_Type.Alias no_wrap /]]\n		<!-- Disambiguate -->\n		[Set Current_Cover_Item to This /]\n		\n		<!-- Find a property that can be used as a cover -->\n		<!-- TODO the count is not passed in to Property value queries -->\n		[1 Current_Cover_Item.Properties Where Value_Type = \"Picture\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Type.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Type.Cover_Property_Item to This.ID /]\n					[Save Current_Type /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n		[1 Current_Cover_Item.Properties Where Value_Type = \"Video\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Type.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Type.Cover_Property_Item to This.ID /]\n					[Save Current_Type /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n		[1 Current_Cover_Item.Properties Where Value_Type = \"Location\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Type.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Type.Cover_Property_Item to This.ID /]\n					[Save Current_Type /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n		[1 Current_Cover_Item.Properties Where Value_Type is \"Website\"]\n			<!-- Check if a cover property has already been found -->\n			[If Current_Type.Cover_Property_Item does not exist]\n				<!-- Check if the cover property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Cover_Item.Type]\n					<!-- Set cover property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Type.Cover_Property_Item to This.ID /]\n					[Save Current_Type /]\n				[/If]\n			[/If]\n		[/Current_Cover_Item.Properties]\n	[/New]\n[/If]',NULL,NULL,'Published','Type'),('Set_Explanation_Property_Item','2016-01-15 13:47:28',679,'Set Explanation Property Item',87,NULL,0,'2016-01-15 13:47:30','Core','Admin','[Admin]\n	<!-- Admin Only -->\n	[Link to Execute /]\n[/Admin]','<!-- TODO: Reverse (MAYBE, left over from cover) -->\n\n<!-- Disambiguate -->\n[Set Current_Type to This.Target /]\n\n<!-- Check if a explanation property has already been set -->\n<!-- TODO better way to figure out when to run this? -->\n[If Current_Type.Explanation_Property_Item does not exist]\n	<!-- Check if the type has a property with a explanation -->\n	<!-- TODO check Pictures first, etc. -->\n	[New [Current_Type.Alias no_wrap /]]\n		<!-- Disambiguate -->\n		[Set Current_Explanation_Item to This /]\n		\n		<!-- Find a property that can be used as an explanation -->\n\n		<!-- TODO the count is not passed in to Property value queries -->\n		[1 Current_Explanation_Item.Properties Where Name = \"Tagline\" or Name = \"Explanation\" by Name Descending]\n			[If Current_Type.Explanation_Property_Item does not exist]\n				<!-- Check if the explanation property is actually a property of the current item -->\n				<!-- TODO this seems hacky -->\n				[If This.Values.Type is Parent Type of Current_Explanation_Item.Type]\n					<!-- Set explanation property for data module -->\n					<!-- TODO sets by ID as a hack because the item won\'t be available later during the current page load after this iterator moves on -->\n					[Set Current_Type.Explanation_Property_Item to This.ID /]\n					[Save Current_Type /]\n				[/If]\n			[/If]\n		[/Current_Explanation_Item.Properties]\n	[/New]\n[/If]',NULL,NULL,'Published','Type'),('Update_Detail_Properties','2016-01-15 13:47:28',680,'Update Detail Properties',88,NULL,0,'2016-01-15 13:47:30','Core','Admin','[Admin]\n	<!-- Admin Only -->\n	\n	<!-- Execute link for manual implementations -->\n	[Link to Execute /]\n[/Admin]','<!-- Disambiguate -->\n[Set Current_Type to Current_Action.Target /]\n\n<!-- Disabled Properties -->\n\n<!-- Special case top property module --> \n\n<!-- Special case: Teams--> \n[If Current_Type.Alias = \"User\"]\n	[1 Property from Database where Type = \"User\" and Name = \"Team\"]\n		[Set Current_Property to This /]\n		[Current_Type.Content_Module restrict_type Property_Module where Property_Item is Current_Property]\n			[Else]\n				[New Property_Module]\n					[Set New_Property_Module to This /]\n					[Set New_Property_Module.Property_Item to Current_Property /]\n					[Save New_Property_Module No_Refresh/]\n					[Add New_Property_Module to Current_Type.Content_Module /]\n				[/New]\n			[/Else]\n		[/Current_Type.Content_Module]\n	[/1 Property]\n[/If]\n\n<!-- Special case: Members --> \n[If Current_Type.Alias = \"Team\"]\n	[1 Property from Database where Type = \"User\" and Name = \"Team\"]\n		[Set Current_Property to This /]\n		[Current_Type.Content_Module restrict_type Property_Module where Property_Item is Current_Property]\n			[Else]\n				[New Property_Module]\n					[Set New_Property_Module to This /]\n					[Set New_Property_Module.Property_Item to Current_Property /]\n					[Set New_Property_Module.Reverse to true /]\n					[Save New_Property_Module No_Refresh/]\n					[Add New_Property_Module to Current_Type.Content_Module /]\n				[/New]\n			[/Else]\n		[/Current_Type.Content_Module]\n	[/1 Property]\n[/If]\n\n<!-- Cycle over non-advanced properties -->\n[Current_Type.Item_Properties where not Advanced]\n	<!-- Disambiguate -->\n	[Set Current_Property to This /]\n	\n	<!-- Check Forward Direction -->\n	[If Current_Property.Values.Type is parent type of Current_Type]\n		<!-- Check if property module does not exist for property -->\n		[Current_Type.Content_Module restrict_type Property_Module where Property_Item is Current_Property]\n			[Else]\n				<!-- Create New Property Module for property -->\n				[New Property_Module]\n					<!-- Disambiguate -->\n					[Set New_Property_Module to this /]\n					\n					<!-- Set property -->\n					[Set New_Property_Module.Property_Item to Current_Property /]\n					\n					<!-- Set size for certain value types --> \n					[If Current_Property.Value_Type.Alias = \"Video\" or Current_Property.Value_Type.Alias = \"Picture\"]\n						[Set New_Property_Module.Size to \"Large\"/]\n					[/If]\n					[If Current_Property.Value_Type.Alias = \"Social_Media\"]\n						[Set New_Property_Module.Size to \"Small\"/]\n					[/If]\n					\n					<!-- Set display type for certain value types --> \n					[If Current_Property.Value_Type.Alias = \"Video\" or Current_Property.Value_Type.Alias = \"Social_Media\" or Current_Property.Value_Type.Alias = \"Picture\"]\n						[Set New_Property_Module.Display to \"Grid\"/]\n					[/If]\n	\n					<!-- Hide specific properties by default -->\n					[If Current_Type.Values.Alias is \"User\" and Current_Property.Values.Name is \"Name\"]\n						[Set New_Property_Module.Visible to false /]\n					[/If]\n																							\n					<!-- Save Property Module -->\n					[Save New_Property_Module No_Refresh/]\n				\n					<!-- Add new module to type -->\n					[Add New_Property_Module to Current_Type.Content_Module /]\n				[/New]\n			[/Else]\n		[/Current_Type.Content_Module]\n	[/If]\n	\n	<!-- Check Reverse Direction -->\n	[If Current_Property.Values.Value_Type is Parent Type of Current_Type]\n		<!-- Check if property module does not exist for reverse property -->\n		[Current_Type.Content_Module restrict_type Property_Module where Property_Item is Current_Property and Reverse]\n			[Else]\n				<!-- Create New Property Module for property -->\n				[New Property_Module]\n					<!-- Disambiguate -->\n					[Set New_Property_Module to this /]\n					\n					<!-- Set property -->\n					[Set New_Property_Module.Property_Item to Current_Property /]\n					\n					<!-- Set reverse -->\n					[Set New_Property_Module.Reverse to true /]\n					\n					<!-- Set display type for specific properties --> \n					[If Current_Property.Type.Alias = \"Video\" or Current_Property.Type.Alias = \"Social_Media\"]\n						[Set New_Property_Module.Display to \"Grid\"/]\n					[/If]\n					\n					<!-- Set size for specific properties --> \n					[If Current_Property.Value_Type.Alias = \"Video\" or Current_Property.Value_Type.Alias = \"Picture\"]\n						[Set New_Property_Module.Size to \"Large\"/]\n					[/If]\n					\n					<!-- Save Property Module -->\n					[Save New_Property_Module No_Refresh/]\n					\n					<!-- Add new module to type -->\n					[Add New_Property_Module to Current_Type.Content_Module /]\n				[/New]\n			[/Else]\n		[/Current_Type.Content_Module]\n	[/If]\n[/Current_Type.Item_Properties]\n\n\n<!-- Remove property modules of properties that have been deleted -->\n[Current_Action.Target.Content_Module as_attachment]\n	<!-- Disambiguate -->\n	[Set Current_Attachment to This /]\n	\n	<!-- Get the attached property module, if there is one -->\n	[Current_Attachment.Content_Module restrict_type Property_Module]\n		<!-- Disambiguate -->\n		[Set Current_Property_Module to This /]\n		\n		<!-- Check if property still exists -->\n		[Current_Property_Module.Property_Item]\n			<!-- TODO: Does Not Exist should check if the key resolves to an actual item -->\n			[Else]\n				<!-- Delete the Property Module -->\n				[Remove Current_Property_Module /]\n				\n				<!-- Detach the module -->\n				[Remove Current_Attachment /]\n			[/Else]\n		[/Current_Property_Module.Property_Item]\n	[/This.Content_Module]\n[/Current_Action.Target.Content_Module]',NULL,NULL,'Published','Type'),('Change_Password','2016-01-15 13:47:28',681,'Change Password',89,NULL,0,'2016-01-15 13:47:30','Accounts','Admin','<span class=\"Page_Item Clean_Window\" style=\"width:450px;\">\n	<!-- Close Button -->\n	<a class=\"Close_Button\" href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window();return false;\" style=\"float:right;width:30px;height:30px;\">\n		<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\">\n	</a>\n\n	<!-- Header -->\n	<span class=\"Page_Header\">\n		<span class=\"Page_Title\">\n			<span class=\"Left_And_Right\">\n				<span class=\"Page_Name\" style=\"float:none;\">\n					Change Password \n				</span>\n			</span>\n	</span>\n\n	<section style=\"margin-top:25px;\">\n		Click below to send a password reset link to your email address.\n	</section>\n\n	<section style=\"margin-top:25px;\"> \n		[Input \"Hidden\" with Name = \"Username\", Value = \"[Action.Target.Email_Address no_wrap/]\"/] \n		[Link	to Execute with Class = \"Button\"/] \n	</section>\n</span>','[Action \"Forgot Password\" as \"Execute\"/]\n[External_Script]\n	[Block \"Forgot Password Confirmation\"]\n		[Go to This into \"[Form.Calling_Namespace no_wrap/]\"/]\n	[/Block]\n[/External_Script]',NULL,NULL,'Published','User'),('Send_Invitation','2016-01-15 13:47:28',682,'Send Invitation',90,NULL,0,'2016-01-15 13:47:30','Accounts','Admin','<!-- Disambiguate -->\n[Set Current_User to Action.Target /]\n\n<!-- Admin only -->\n[Admin]\n	<span class=\"Send_Invitation_Window\">\n		<!-- Close Button -->\n		<a class=\"Close_Button\" href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window();return false;\">\n			<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\">\n		</a>\n		\n		<span class=\"Header\">\n			Send Invitation [If Current_User.Received_Invitation]Again[/If]\n		</span>\n		\n		[Message \"Invitation\" with Recipient = Current_User, Preview = True]\n			<span class=\"Section\">\n				<span class=\"Label\">\n					Subject\n				</span>\n				<span class=\"Input\">\n					[Input \"Text Field\" with Name = \"Subject\", Value = \"[Format as \"Jelly Attribute\"][Message.Subject no_wrap/][/Format]\"/]\n				</span>\n			</span>\n			\n			<span class=\"Section\">\n				<span class=\"Label\">\n					Body\n				</span>\n\n				<span class=\"Input\" style=\"clear:left; height:300px; overflow:scroll;border-top:solid 1px #e7e7e7; border-bottom:solid 1px #e7e7e7;\">\n					[Input \"Inline Text\" with Name = \"Body_Top\", Value = \"[Format as \"Jelly Attribute\"][Message.Body_Top no_wrap/][/Format]\", Style = \"padding:5px;border-left: solid 1px #e7e7e7;border-right: solid 1px #e7e7e7;\"/]\n					<div style=\"padding:5px;border-left: solid 1px #e7e7e7;border-right: solid 1px #e7e7e7;background-color:#ffffcc;\">\n						[Message.Body_Action no_wrap/]\n					</div>\n					[Input \"Inline Text\" with Name = \"Body_Bottom\", Value = \"[Format as \"Jelly Attribute\"][Message.Body_Bottom no_wrap/][/Format]\", Style = \"padding:5px;border-left: solid 1px #e7e7e7;border-right: solid 1px #e7e7e7;\"/]\n				</span>\n			</span>\n		[/Message]\n	</span>\n	\n	<span class=\"Execute\">\n		<!-- Execute link -->\n		[Link to Execute with Class=\"Button\"]\n			Send Invitation [If Current_User.Received_Invitation]Again[/If]\n		[/Link]\n	</span>\n[/Admin]','<!-- Admin only -->\n[Admin]\n	[Current_Action.Target]\n		<!-- Disambiguate -->\n		[Set Current_Item to this /]\n		[Set Current_User to this /]\n		\n		<!-- Check if the user has an email address -->\n		[If Current_User.Email_Address]\n			\n			<!-- Create and send invitation code -->\n			[New Invitation_Code]\n				<!-- Disambiguate -->\n				[Set New_Invitation_Code to this /]\n\n				<!-- Generate random invite code -->\n				[Set New_Invitation_Code.Name to \"[PHP]return substr(md5(microtime()),rand(0,26), 24);[/PHP]\" /]\n\n				<!-- Store invite code user -->\n				[Set New_Invitation_Code.User to Current_User /]\n\n				<!-- Mark that the user has received an invitation -->\n				[Set Current_User.Received_Invitation to true /]\n				[Set Current_User.Last_Received_Invitation_Date to Now /]\n				[Save Current_User /]\n\n				<!-- Save invite code -->\n				[Save New_Invitation_Code /]\n\n				<!-- Send invitation message --> \n				[Message \"Invitation\" no_wrap with \n						Recipient = Current_User,\n						Recipient_Invitation_Code = New_Invitation_Code.Name\n					]\n						[Set Invitation_Message to This/]\n						[New Message with\n								Subject = Form.Subject,\n								Body = \"[Format as \"Jelly Attribute\"][Form.Body_Top no_wrap/][Invitation_Message.Body_Action no_wrap/][Form.Body_Bottom no_wrap/][/Format]\",\n								Recipient = Current_User\n							]\n								[Set New_Message to This/]\n\n								<!-- Set sender to current user, or site owner as fallback --> \n								[If Current_Session.User]\n									[Then][Set New_Message.Variables.Sender = Current_Session.User/][/Then]\n									[Else][1 Site from Database][Set New_Message.Variables.Sender to Site.Owner/][/1][/Else]\n								[/If]\n\n								<!-- Send message -->  \n								[New_Message.Action \"Send\" as Execute/]\n						[/New Message] \n				[/Message]\n				\n				<!-- Track invite --> \n				[If Globals.Allow_Tracking]\n					[External_Script]\n						// Don\'t die on adblock\n						\n						if (mixpanel)\n						{\n							mixpanel.track(\n									\'Invite Sent\', \n									{	\n										\'Account\': \'[PHP]return $_SERVER\\[\'HTTP_HOST\'\\];[/PHP]\',\n										\'Email_Address\': \'[Current_User.Email_Address no_wrap/]\',\n										[If \"[PHP]return strtolower($_SERVER\\[\'HTTP_HOST\'\\]);[/PHP]\" = \"community.better.space\"]\n											[Set Current_Action.Markets_Traversed to false/]\n											\'Markets\': \n												\\[\n													[Current_User.What_kind_of_communities_do_you_organize_or_work no_wrap][If Markets_Traversed], \n														[/If]\'[Name no_wrap/]\'[Set Current_Action.Markets_Traversed to true/][/Current_User.What_kind_of_communities_do_you_organize_or_work]\n												\\]\n										[/If]\n									}\n								);\n						}\n					[/External_Script]\n				[/If]\n			\n				Invite sent.\n			[/New]\n		[/If]\n	[/Current_Action.Target]\n	[External_Script]\n		// TODO - do it the right way\n		Jelly.Interface.Close_Top_Window();\n	[/External_Script]	\n[/Admin]',NULL,NULL,'Published','User'),('Send_Text','2016-01-15 13:47:28',683,'Send Text',91,NULL,0,'2016-01-15 13:47:30','Accounts','Admin','[Set Current_Action.Variables.VIsible = False/]\n[If Current_Action.Target.Allow_Community_Members_To_Contact_Me]\n	[Then][Member][Set Current_Action.Variables.VIsible = True/][/Member][/Then]\n	[Else][Admin][Set Current_Action.Variables.VIsible = True/][/Admin][/Else]\n[/If]\n\n[If Current_Action.Variables.Visible]\n	<span class=\"Send_Text_Window\">	\n		<!-- Close Button -->\n		<a class=\"Close_Button\" href=\"#\" onclick=\"Jelly.Interface.Close_Top_Window();return false;\">\n			<img class=\"iconic\" data-src=\"/jelly/libraries/iconic/svg/smart/x-thin.svg\">\n		</a>\n\n		<span class=\"Header\">\n			Send Text\n		</span>	\n	\n		[Action as Result/]	\n		<span class=\"Recipients\">\n			<label>To:</label>\n\n			<div class=\"Input_Wrapper Big_List Grid Tiny\">\n				<span class=\"Big_List_Items\">\n					<span class=\"Big_List_Item Hover\">\n						[Current_Action.Target]\n							[Set Current_Member to this /]\n		\n							[Input \"Hidden\" with Name = \"Recipient\", Value = Current_Action.Target/]\n		\n							<!-- Details -->\n							<a class=\"Big_List_Item_Details Selected\" href=\"#\" onclick=\"return false;\" style=\"overflow:hidden;\">\n								<!-- Cover Image -->\n								<span class=\"Big_List_Item_Cover\">\n									[Current_Member as Cover /]\n								</span>\n			\n								<span class=\"Big_List_Item_Name\">\n									[Current_Member.Name /]\n								</span>\n		\n								<span class=\"Big_List_Item_Check\">√</span>\n							</a>\n						[/]	\n					</span>\n				</span>\n			</div>		\n		</span>\n\n		<span class=\"Author\">\n			<!-- Set sender to current user, or site owner as fallback --> \n			[If Current_Session.User]\n				[Then][Set Current_Action.Variables.Sender = Current_Session.User/][/Then]\n				[Else][1 Site from Database][Set Current_Action.Variables.Sender to Site.Owner/][/1][/Else]\n			[/If]		\n			[Input \"Hidden\" with Name = \"Sender\", Value = Current_Action.Variables.Sender/]\n		</span>\n\n		<span class=\"Message\">\n			<label>Message:</label>\n			[Input \"Text Area\" with Name = \"Body\"/]\n\n			[Set Current_Action.Variables.Signature to \"&#x1f4e8; Sent from [Site.Name/]! Respond to [Current_Action.Variables.Sender.First_Name/] @ [Current_Action.Variables.Sender.Phone_Number/]. (Don\'t reply)\"/] \n			[Input \"Hidden\" with Name = \"Signature\", Value = Current_Action.Variables.Signature/]\n			<div class=\"Signature\">[Current_Action.Variables.Signature/]</div>\n		</span>\n\n		<span class=\"Execute\">\n			<!-- Execute link -->\n			[Link to Execute]Send[/Link]\n		</span>\n	</span>\n[/If]','[If not Form.Body]\n	[Show Error for \"Body\"]Please enter a message[/Show]\n[/If]\n\n[If not Form.Sender.First_Name or Form.Sender.Phone_Number does not exist or Form.Sender.Phone_Number = \"\"]\n	[Show Error]Complete your profile with your name and phone number to send team texts.[/Show]\n[/If]\n\n[If not Form.Recipient]\n	[Show Error]Something went wrong. This message was not sent.[/Show]\n[/If]\n\n[If not Form.Error]\n	<!-- Send message --> \n	[PHP]	\n		// this line loads the library \n		require_once(\'jelly/php/libraries/twilio/twilio.php\'); \n	\n		// TODO - generalize configuration process for API keys etc\n		$Configuration = &Get_Environment_Variables();\n	\n		$Account_SID = $Configuration\\[\'API_Keys\'\\]\\[\'Twilio\'\\]\\[\'Account_SID\'\\];\n		$Auth_Token = $Configuration\\[\'API_Keys\'\\]\\[\'Twilio\'\\]\\[\'Auth_Token\'\\];\n		$From = $Configuration\\[\'API_Keys\'\\]\\[\'Twilio\'\\]\\[\'From\'\\];\n		$Client = new Services_Twilio($Account_SID, $Auth_Token); \n	\n		$Client->account->messages->create(array( \n			\'To\' => \'[Form.Recipient.Phone_Number no_wrap/]\', \n			\'From\' => $From,\n			\'Body\' => html_entity_decode(\"[Form.Body no_wrap/]\" . \" \" . \"[Form.Signature no_wrap/]\")\n		));\n	[/PHP]	\n\n	[External_Script]\n		[If Globals.Allow_Tracking]					\n			if (mixpanel)\n			{\n				mixpanel.track(\n						\"Message Sent\", \n						{	\n							\"Via\": \"Text\",									\n							\"Sender\": \"[Format as \"Javascript String\"][Form.Sender.Name no_wrap/][/Format]\", \n							\"Recipient\": \"[Format as \"Javascript String\"][Form.Recipient.Name no_wrap/][/Format]\", \n							\"Recipient Count\": 1,\n							\"Account\": \"[PHP]return $_SERVER\\[\"HTTP_HOST\"\\];[/PHP]\"\n						}\n					);\n			}\n		[/If]\n		[Block \"Text Sent Confirmation\"]\n			[Go to This into \"[Form.Calling_Namespace no_wrap/]\"/]\n		[/Block]\n	[/External_Script]\n[/If]',NULL,NULL,'Published','User'),('Refresh_Website','2016-01-15 13:47:28',684,'Refresh Website',92,NULL,0,'2016-01-15 13:47:30','Website','Admin','[If Current_Item.To_Refresh]\n	[Action as \"Loading\"/]\n	\n	<!-- Auto-execute the action -->\n	[External_Script]\n		Jelly.Handlers.Call_Handler_For_Target({\'Event\': \'Execute\', \'Target\': document.getElementById(\'[This.Namespace no_wrap /]\')});\n	[/External_Script]\n[/If]','<!-- Website Type_Action \"Refresh Website\" -->\n\n<!-- Dismabiguate -->\n[Set Current_Item to Current_Action.Target /]\n\n<!-- Check if website is marked to be refreshed -->\n[If Current_Item.To_Refresh]\n	[PHP]\n		// Load website info\n		$Website_ID = &New_String(\'[Format as \"PHP Single Quoted String\"][Current_Item.ID no_wrap /][/Format]\');\n		$URL = &New_String(\'[Format as \"PHP Single Quoted String\"][Current_Item.Path no_wrap /][/Format]\');\n	\n		// Get website item\n		$Website_Item_Command_String = &New_String(\'Current_Item as reference\');\n		$Website_Item_Result = Process_Command_String($Database, $Website_Item_Command_String, $Memory_Stack_Reference);\n		$Website_Item = $Website_Item_Result\\[\'Chunks\'\\]\\[0\\]\\[\'Item\'\\];\n		\n		// Request website HTML content\n		$Response = Get_Remote_Content($URL);\n		$Response_Body = $Response\\[\'Body\'\\];\n		$Response_Headers = $Response\\[\'Headers\'\\];\n		$Response_Code = $Response\\[\'Response_Code\'\\];\n		\n		// Get file type and name from headers\n		if ($Response_Headers\\[\'Content-Type\'\\])\n		{\n			// Get the content-type\n			$Response_Content_Type = $Response_Headers\\[\'Content-Type\'\\];\n			\n			// If more than one part is specified, use the first\n			list($Response_Content_Type) = explode(\';\', $Response_Content_Type);\n		}\n			\n		if ($Response_Headers\\[\'Content-Disposition\'\\])\n		{\n			// Contain to quoted string\n			// TODO if filename*= doesn\'t exist, use filename=\n			// TODO seems as though the UTF-8 version is unterminated\n			$Current_Response_Header = $Response_Headers\\[\'Content-Disposition\'\\];\n			$Response_File_Name = substr($Current_Response_Header, strpos($Current_Response_Header, \'filename*=UTF-8\\\'\\\'\') + strlen(\'filename*=UTF-8\\\'\\\'\'));\n			\n			// Decode the file name\n			// TODO doesn\'t seem as though it\'s actually UTF-8 encoded, just URL-encoded\n			$Response_File_Name = urldecode($Response_File_Name);\n		}\n		\n		// Check response code\n		switch ($Response_Code)\n		{\n			// Response Code 200 (OK)\n			case 200:\n			{\n				// Get content type parent and sub type\n				// TODO not used\n				list($Content_Type, $Content_Sub_Type) = explode(\'/\', $Response_Content_Type);\n\n				switch (strtolower($Response_Content_Type))\n				{\n					case \'image/gif\':\n					case \'image/jpeg\':\n					case \'image/png\':\n					{\n						// Check if a file name was specified in the headers\n						if ($Response_File_Name)\n						{\n							// Set the file name from the header\n							Set_Value($Website_Item, \'Name\', $Response_File_Name);\n						}\n						else\n						{\n							// Get file name from URL\n							$URL_File_Name = substr($URL, strrpos($URL, \'/\') + 1);\n							if (!$URL_File_Name)\n								$URL_File_Name = &New_String($URL);\n							Set_Value($Website_Item, \'Name\', $URL_File_Name);\n						}\n						Save_Item($Website_Item);\n		\n						// Create new picture\n						$Picture_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, \'Picture\'));\n						Set_Value($Picture_Item, \'Path\', $URL);\n						Save_Item($Picture_Item);\n		\n						// Add the picture to the website\n						Add_Value($Website_Item, \'Picture\', $Picture_Item);\n		\n						break;\n					}\n					case \'text/html\':\n					{\n						// Parse HTML document\n						$HTML_Document = new DOMDocument();\n						if ($Response_Body)\n						{\n							libxml_use_internal_errors(true);\n							$HTML_Document->loadHTML($Response_Body);\n							libxml_clear_errors();\n						}\n		\n						// Get open graph information\n						$Meta_Tags = $HTML_Document->getElementsByTagName(\'meta\');\n						$Found_Title = false;\n						$Found_Image = false;\n						foreach ($Meta_Tags as $Meta_Tag)\n						{\n							if ($Meta_Tag->getAttribute(\'property\') == \'og:title\')\n							{\n								$Found_Title = true;\n								$Title = $Meta_Tag->getAttribute(\'content\');\n							}\n							if ($Meta_Tag->getAttribute(\'property\') == \'og:image\')\n							{\n								$Found_Image = true;\n								$Image = $Meta_Tag->getAttribute(\'content\');\n							}\n						}\n		\n						echo \"maybe found\";\n			\n						if (!$Found_Title)\n						{\n							echo \"not found\";\n							$Title = \'\';\n							$Title_Tags = $HTML_Document->getElementsByTagName(\'title\');\n							foreach ($Title_Tags as $Title_Tag)\n							{\n								$Found_Title = true;\n								$Title = $Title_Tag->nodeValue;\n							}\n						}\n		\n						echo \"maybe found\";\n\n						// Set title to entire URL if not found otherwise\n						if (!$Found_Title)\n						{\n							echo \"not found again\";\n							$Title = $URL;\n						}\n\n						// Set website name to title										\n						Set_Value($Website_Item, \'Name\', $Title);\n			\n						// TODO - I wonder why save item is here. \n						Save_Item($Website_Item);\n		\n						if ($Found_Image)\n						{\n							// Create new picture\n							$Picture_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, \'Picture\'));\n							Set_Value($Picture_Item, \'Path\', $Image);\n							Save_Item($Picture_Item);\n		\n							// Add the picture to the website\n							Add_Value($Website_Item, \'Picture\', $Picture_Item);\n						}\n						else\n						{\n							// Get base link from document\n							$Base_HREF = $URL;\n							$Base_Tags = $HTML_Document->getElementsByTagName(\'base\');\n							foreach ($Base_Tags as $Base_Tag)\n								$Base_HREF = $Base_Tag->getAttribute(\'href\');\n							if (strpos($Base_HREF, \'/\', strlen(\'https://\')) !== false)\n								$Base_HREF_Root = substr($Base_HREF, 0, strpos($Base_HREF, \'/\', strlen(\'https://\')));\n							else\n								$Base_HREF_Root = $URL;\n							if (strrpos($Base_HREF, \'/\') + 1 > strlen(\'https://\'))\n								$Base_HREF_Directory = substr($Base_HREF, 0, strrpos($Base_HREF, \'/\') + 1);\n							else\n								$Base_HREF_Directory = $Base_HREF_Root . \'/\';\n\n		\n							// Get images from document\n							// TODO also get CSS images from document and related CSS files\n							$Image_Tags = $HTML_Document->getElementsByTagName(\'img\');\n							$Image_Tag_Index = 0;\n							foreach ($Image_Tags as $Image_Tag)\n							{\n								// Stop after 5 images\n								if ($Image_Tag_Index == 3)\n									break;\n			\n								// Check if image src was set\n								$Image_SRC = $Image_Tag->getAttribute(\'src\');\n								if ($Image_SRC)\n								{\n									// Generate absolute URL for image src\n									if (substr($Image_SRC, 0, strlen(\'http\')) == \'http\')\n										$Image_URL = &New_String($Image_SRC);\n									else if (substr($Image_SRC, 0, strlen(\'/\')) == \'/\')\n										$Image_URL = &New_String($Base_HREF_Root . $Image_SRC);\n									else\n										$Image_URL = &New_String($Base_HREF_Directory . $Image_SRC);\n		\n									// Create new picture\n									$Picture_Item = &Create_Memory_Item($Database, Get_Cached_Type($Database, \'Picture\'));\n									Set_Value($Picture_Item, \'Path\', $Image_URL);\n									Save_Item($Picture_Item);\n				\n									// Add the picture to the website\n									Add_Value($Website_Item, \'Picture\', $Picture_Item);\n								}\n			\n								$Image_Tag_Index++;\n							}\n						}\n		\n						// TODO: pick best picture?\n		\n						break;\n					}\n				}\n				break;\n			}\n			\n			// Other response codes\n			default:\n			{\n				// TODO Better name?\n				Set_Simple($Website_Item, \'Name\', \'Broken Link\' . \' \' . \'(\' . $Response_Code . \')\');\n				Save_Item($Website_Item);\n				break;\n			}\n		}\n	[/PHP]\n	\n	<!-- Render all pictures to get sizes -->\n	[Current_Item.Picture /]\n	\n	<!-- Delete all but largest image -->\n	<!-- TODO use \"by\" to start at second result? -->\n	[Set Current_Action.Variables.First_Image to true /]\n	[Current_Item.Picture by Width descending]\n		<!-- Delete all but the first result -->\n		[If not Current_Action.Variables.First_Image]\n			[This.Attachment]\n				[Remove this no_refresh /]\n			[/This.Attachment]\n			[Remove this no_refresh /]\n		[/If]\n		\n		<!-- Set th